<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>A Visual Proof of Viviani's Theorem</title>
    <!-- Include p5.js from CDN -->
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
    <style>
        body {
            font-family: Arial, sans-serif;
        }
        #demo-container {
            max-width: 800px;
            margin: 0 auto;
            padding: 20px;
        }
        #control-panel {
            margin-bottom: 20px;
        }
        #control-panel label {
            margin-right: 10px;
        }
        #canvas-container {
            text-align: center;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <!-- Section 1: Control Panel -->
        <div id="control-panel">
            <label>step</label>
            <input type="radio" id="step1" name="step-selector" value="1" checked>
            <label for="step1">1</label>
            <input type="radio" id="step2" name="step-selector" value="2">
            <label for="step2">2</label>
            <input type="radio" id="step3" name="step-selector" value="3">
            <label for="step3">3</label>
        </div>
        <!-- Section 2: Visualization Area -->
        <div id="canvas-container">
            <!-- p5.js canvas will be created here -->
        </div>
    </div>
    <script>
        let P;
        let currentStep = 1;
        let isDragging = false;
        let originalP;

        function setup() {
            createCanvas(600, 520);
            P = createVector(240, 312);

            // Add event listeners to radio buttons
            document.querySelectorAll('input[name="step-selector"]').forEach(radio => {
                radio.addEventListener('change', () => {
                    currentStep = radio.value;
                    if (currentStep == 3) {
                        originalP = P.copy();
                    }
                    redraw();
                });
            });
        }

        function draw() {
            background(255);

            // Define triangle vertices
            const A = createVector(100, 480);
            const B = createVector(500, 480);
            const C = createVector(300, 133.59);
            const midAB = createVector(300, 480);

            // Draw main triangle
            stroke(0);
            noFill();
            triangle(A.x, A.y, B.x, B.y, C.x, C.y);
            textSize(16);
            fill(0);
            text('A', A.x - 20, A.y + 20);
            text('B', B.x + 10, B.y + 20);
            text('C', C.x, C.y - 10);

            // Draw main altitude (dashed line from C to midAB)
            stroke(173, 216, 230);
            strokeWeight(1);
            strokeCap(ROUND);
            strokeDash([5, 5]);
            line(C.x, C.y, midAB.x, midAB.y);
            strokeDash([]);

            // Draw point P or its marker
            if (currentStep != 3) {
                fill(200);
                stroke(0);
                strokeWeight(1);
                ellipse(P.x, P.y, 10, 10);
                line(P.x - 5, P.y, P.x + 5, P.y);
                line(P.x, P.y - 5, P.x, P.y + 5);
                fill(255, 0, 0);
                textSize(12);
                text('P', P.x + 8, P.y + 5);
            } else if (originalP) {
                // Draw original P as gray marker
                fill(200, 200, 200, 128);
                stroke(150);
                ellipse(originalP.x, originalP.y, 10, 10);
                line(originalP.x - 5, originalP.y, originalP.x + 5, originalP.y);
                line(originalP.x, originalP.y - 5, originalP.x, originalP.y + 5);
            }

            // Draw step-specific content
            if (currentStep == 1) {
                drawStep1(A, B, C);
            } else if (currentStep == 2) {
                drawStep2(A, B, C);
            } else if (currentStep == 3) {
                drawStep3(A, B, C);
            }
        }

        function drawStep1(A, B, C) {
            // Calculate distances and feet
            const foot_AB = createVector(P.x, 480);
            const foot_AC = footAC(P);
            const foot_BC = footBC(P);

            // Draw perpendicular lines
            stroke(0);
            strokeWeight(1);
            line(P.x, P.y, foot_AB.x, foot_AB.y);
            line(P.x, P.y, foot_AC.x, foot_AC.y);
            line(P.x, P.y, foot_BC.x, foot_BC.y);

            // Draw feet as blue circles
            fill(0, 0, 255);
            noStroke();
            ellipse(foot_AB.x, foot_AB.y, 6, 6);
            ellipse(foot_AC.x, foot_AC.y, 6, 6);
            ellipse(foot_BC.x, foot_BC.y, 6, 6);
        }

        function drawStep2(A, B, C) {
            // Calculate distances
            const d_AB = 480 - P.y;
            const d_AC = distanceToAC(P);
            const d_BC = distanceToBC(P);

            // Calculate side lengths
            const side_red = 2 * d_AB / sqrt(3);
            const side_green = 2 * d_AC / sqrt(3);
            const side_blue = 2 * d_BC / sqrt(3);

            // Draw red triangle
            fill(255, 0, 0, 128);
            noStroke();
            const red_base_left = P.x - side_red / 2;
            const red_base_right = P.x + side_red / 2;
            triangle(red_base_left, 480, red_base_right, 480, P.x, P.y);

            // Draw green triangle
            const foot_AC = footAC(P);
            const direction_AC = createVector(0.5, -sqrt(3)/2);
            const half_green = side_green / 2;
            const green_v1 = p5.Vector.add(foot_AC, direction_AC.copy().mult(half_green));
            const green_v2 = p5.Vector.sub(foot_AC, direction_AC.copy().mult(half_green));
            fill(0, 255, 0, 128);
            triangle(P.x, P.y, green_v1.x, green_v1.y, green_v2.x, green_v2.y);
            // Label x at foot on AC
            fill(0);
            textSize(12);
            text('x', foot_AC.x, foot_AC.y - 5);

            // Draw blue triangle
            const foot_BC = footBC(P);
            const direction_BC = createVector(-0.5, -sqrt(3)/2);
            const half_blue = side_blue / 2;
            const blue_v1 = p5.Vector.add(foot_BC, direction_BC.copy().mult(half_blue));
            const blue_v2 = p5.Vector.sub(foot_BC, direction_BC.copy().mult(half_blue));
            fill(0, 0, 255, 128);
            triangle(P.x, P.y, blue_v1.x, blue_v1.y, blue_v2.x, blue_v2.y);
            // Label y at foot on BC
            text('y', foot_BC.x, foot_BC.y - 5);
        }

        function drawStep3(A, B, C) {
            // Calculate distances
            const d_AB = 480 - P.y;
            const d_AC = distanceToAC(P);
            const d_BC = distanceToBC(P);

            // Calculate side lengths
            const side_red = 2 * d_AB / sqrt(3);
            const side_blue = 2 * d_BC / sqrt(3);
            const side_green = 2 * d_AC / sqrt(3);

            // Calculate positions along main altitude
            const red_top_y = 480 - d_AB;
            const blue_top_y = red_top_y - d_BC;
            const green_top_y = blue_top_y - d_AC;

            // Draw red triangle
            fill(255, 0, 0, 128);
            noStroke();
            const red_base_left = 300 - side_red / 2;
            const red_base_right = 300 + side_red / 2;
            triangle(red_base_left, 480, red_base_right, 480, 300, red_top_y);

            // Draw blue triangle
            fill(0, 0, 255, 128);
            const blue_base_left = 300 - side_blue / 2;
            const blue_base_right = 300 + side_blue / 2;
            triangle(blue_base_left, red_top_y, blue_base_right, red_top_y, 300, blue_top_y);

            // Draw green triangle
            fill(0, 255, 0, 128);
            const green_base_left = 300 - side_green / 2;
            const green_base_right = 300 + side_green / 2;
            triangle(green_base_left, blue_top_y, green_base_right, blue_top_y, 300, green_top_y);
        }

        // Helper functions
        function distanceToAC(P) {
            const A = createVector(100, 480);
            const C = createVector(300, 133.59);
            const AC = C.copy().sub(A);
            const AP = P.copy().sub(A);
            const cross = AC.x * AP.y - AC.y * AP.x;
            return abs(cross) / AC.mag();
        }

        function footAC(P) {
            const A = createVector(100, 480);
            const C = createVector(300, 133.59);
            const AC = C.copy().sub(A);
            const AP = P.copy().sub(A);
            const t = AP.dot(AC) / AC.magSq();
            return A.copy().add(AC.copy().mult(t));
        }

        function distanceToBC(P) {
            const B = createVector(500, 480);
            const C = createVector(300, 133.59);
            const BC = C.copy().sub(B);
            const BP = P.copy().sub(B);
            const cross = BC.x * BP.y - BC.y * BP.x;
            return abs(cross) / BC.mag();
        }

        function footBC(P) {
            const B = createVector(500, 480);
            const C = createVector(300, 133.59);
            const BC = C.copy().sub(B);
            const BP = P.copy().sub(B);
            const t = BP.dot(BC) / BC.magSq();
            return B.copy().add(BC.copy().mult(t));
        }

        // Mouse interaction
        function mousePressed() {
            if (dist(mouseX, mouseY, P.x, P.y) < 10) {
                isDragging = true;
            }
        }

        function mouseDragged() {
            if (isDragging) {
                let newP = createVector(mouseX, mouseY);
                if (isInsideTriangle(newP)) {
                    P = newP;
                }
            }
        }

        function mouseReleased() {
            isDragging = false;
        }

        // Check if point is inside triangle ABC
        function isInsideTriangle(P) {
            const A = createVector(100, 480);
            const B = createVector(500, 480);
            const C = createVector(300, 133.59);

            const v0 = C.copy().sub(A);
            const v1 = B.copy().sub(A);
            const v2 = P.copy().sub(A);

            const dot00 = v0.dot(v0);
            const dot01 = v0.dot(v1);
            const dot02 = v0.dot(v2);
            const dot11 = v1.dot(v1);
            const dot12 = v1.dot(v2);

            const denom = dot00 * dot11 - dot01 * dot01;

            const u = (dot11 * dot02 - dot01 * dot12) / denom;
            const v = (dot00 * dot12 - dot01 * dot02) / denom;

            return (u >= 0) && (v >= 0) && (u + v <= 1);
        }
    </script>
</body>
</html>
