    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="dwarf-planet-controls">
                <label for="dwarf-planet-selector">dwarf planet</label>
                <div id="dwarf-planet-selector">
                    <button data-planet="Ceres">Ceres</button>
                    <button data-planet="Pluto" class="active">Pluto</button>
                    <button data-planet="Makemake">Makemake</button>
                    <button data-planet="Haumea">Haumea</button>
                    <button data-planet="Eris">Eris</button>
                </div>
            </div>
            <div id="time-controls">
                <label for="time-slider">time</label>
                <input type="range" id="time-slider" min="0" max="110000" step="1" value="2922">
                <button id="play-pause-button">▶</button>
            </div>
        </div>
        <div id="visualization-container">
            <div id="date-display">Tue 1 Jan 2008 00:00:00</div>
            <canvas id="3d-canvas"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/controls/OrbitControls.min.js"></script>
    <script>
        // Global variables
        let selectedDwarfPlanet = 'Pluto';
        let isAnimating = false;
        let animationFrameId = null;
        const j2000 = new Date('2000-01-01T12:00:00Z');
        const planets = {
            Mercury: { a: 0.3871, e: 0.2056, i: 7.005, Ω: 48.331, ω: 29.124, M: 174.796 },
            Venus: { a: 0.7233, e: 0.0068, i: 3.395, Ω: 76.680, ω: 54.884, M: 50.416 },
            Earth: { a: 1.0000, e: 0.0167, i: 0.000, Ω: -11.260, ω: 114.208, M: 358.617 },
            Mars: { a: 1.5237, e: 0.0934, i: 1.850, Ω: 49.562, ω: 286.502, M: 19.412 },
            Ceres: { a: 2.7691, e: 0.0760, i: 10.593, Ω: 80.393, ω: 73.598, M: 77.372 },
            Pluto: { a: 39.482, e: 0.2488, i: 17.160, Ω: 110.299, ω: 113.834, M: 14.882 },
            Haumea: { a: 43.132, e: 0.1912, i: 28.220, Ω: 122.155, ω: 239.544, M: 204.03 },
            Makemake: { a: 45.791, e: 0.1559, i: 29.006, Ω: 79.623, ω: 294.821, M: 95.895 },
            Eris: { a: 67.668, e: 0.4418, i: 44.187, Ω: 35.951, ω: 151.291, M: 206.51 }
        };
        const planetColors = {
            Mercury: 0x808080,
            Venus: 0xffd700,
            Earth: 0x0000ff,
            Mars: 0xff0000,
            Ceres: 0x800080,
            Pluto: 0x800080,
            Haumea: 0x800080,
            Makemake: 0x800080,
            Eris: 0x800080
        };

        // Scene setup
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xeeeeee);
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.set(0, 15, 30);
        const renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('3d-canvas') });
        renderer.setSize(window.innerWidth, window.innerHeight);
        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);
        const sunLight = new THREE.PointLight(0xffff00, 1, 100);
        sunLight.position.set(0, 0, 0);
        scene.add(sunLight);

        // Create Sun
        const sunGeometry = new THREE.SphereGeometry(0.5, 32, 32);
        const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
        const sun = new THREE.Mesh(sunGeometry, sunMaterial);
        scene.add(sun);

        // Create planets
        const planetMeshes = {};
        const orbitLines = {};
        Object.keys(planets).forEach(body => {
            const geometry = new THREE.SphereGeometry(0.1, 16, 16);
            const material = new THREE.MeshLambertMaterial({ color: planetColors[body] });
            const mesh = new THREE.Mesh(geometry, material);
            scene.add(mesh);
            planetMeshes[body] = mesh;

            // Create orbit
            const orbit = createOrbit(planets[body]);
            orbitLines[body] = orbit;
            scene.add(orbit);
        });

        // Create dwarf planet orbit (red)
        let dwarfPlanetOrbit = null;
        let dwarfPlanetMesh = null;

        function createOrbit(params) {
            const a = params.a;
            const e = params.e;
            const i = THREE.Math.degToRad(params.i);
            const Ω = THREE.Math.degToRad(params.Ω);
            const ω = THREE.Math.degToRad(params.ω);

            const curve = new THREE.EllipseCurve(
                0, 0,
                a, a * Math.sqrt(1 - e * e),
                0, 2 * Math.PI,
                false,
                0
            );

            const points = curve.getPoints(200);
            const geometry = new THREE.BufferGeometry().setFromPoints(points);

            const material = new THREE.LineBasicMaterial({
                color: planetColors[body] === 0x800080 ? 0xff0000 : 0x0000ff
            });

            const line = new THREE.Line(geometry, material);

            // Apply rotations
            line.rotation.z = ω;
            line.rotation.x = i;
            line.rotation.z += Ω;

            return line;
        }

        function calculatePosition(date, params) {
            const d = (date - j2000) / (1000 * 60 * 60 * 24);
            const P = 365.25 * Math.pow(params.a, 1.5);
            let M = (params.M + (360 / P) * d) % 360;
            M = THREE.Math.degToRad(M);

            // Solve Kepler's equation
            let E = M;
            for (let i = 0; i < 10; i++) {
                E = M + params.e * Math.sin(E);
            }

            const ν = 2 * Math.atan2(Math.sqrt(1 + params.e) * Math.sin(E/2), Math.sqrt(1 - params.e) * Math.cos(E/2));
            const r = params.a * (1 - params.e * Math.cos(E));

            const x_orb = r * Math.cos(ν);
            const y_orb = r * Math.sin(ν);

            // Transform to ecliptic coordinates
            const i_rad = THREE.Math.degToRad(params.i);
            const Ω_rad = THREE.Math.degToRad(params.Ω);
            const ω_rad = THREE.Math.degToRad(params.ω);

            const x = x_orb * (Math.cos(ω_rad) * Math.cos(Ω_rad) - Math.sin(ω_rad) * Math.sin(Ω_rad) * Math.cos(i_rad)) -
                      y_orb * (Math.sin(ω_rad) * Math.cos(Ω_rad) + Math.cos(ω_rad) * Math.sin(Ω_rad) * Math.cos(i_rad));

            const y = x_orb * (Math.cos(ω_rad) * Math.sin(Ω_rad) + Math.sin(ω_rad) * Math.cos(Ω_rad) * Math.cos(i_rad)) -
                      y_orb * (Math.sin(ω_rad) * Math.sin(Ω_rad) - Math.cos(ω_rad) * Math.cos(Ω_rad) * Math.cos(i_rad));

            const z = x_orb * (Math.sin(ω_rad) * Math.sin(i_rad)) +
                      y_orb * (Math.cos(ω_rad) * Math.sin(i_rad));

            return new THREE.Vector3(x, y, z);
        }

        function updateDateDisplay(value) {
            const date = new Date(j2000.getTime() + value * 24 * 60 * 60 * 1000);
            const options = { weekday: 'short', day: 'numeric', month: 'short', year: 'numeric', hour: '2-digit', minute: '2-digit', second: '2-digit' };
            document.getElementById('date-display').textContent = date.toLocaleString('en-US', options).replace(',', '');
        }

        function updatePositions(value) {
            const date = new Date(j2000.getTime() + value * 24 * 60 * 60 * 1000);

            Object.keys(planets).forEach(body => {
                const pos = calculatePosition(date, planets[body]);
                planetMeshes[body].position.copy(pos);
            });

            if (dwarfPlanetMesh) {
                const pos = calculatePosition(date, planets[selectedDwarfPlanet]);
                dwarfPlanetMesh.position.copy(pos);
            }
        }

        function updateDwarfPlanet() {
            // Remove existing dwarf planet and orbit
            if (dwarfPlanetOrbit) {
                scene.remove(dwarfPlanetOrbit);
                dwarfPlanetOrbit = null;
            }
            if (dwarfPlanetMesh) {
                scene.remove(dwarfPlanetMesh);
                dwarfPlanetMesh = null;
            }

            // Create new dwarf planet
            const params = planets[selectedDwarfPlanet];
            const geometry = new THREE.SphereGeometry(0.15, 16, 16);
            const material = new THREE.MeshLambertMaterial({ color: 0x800080 });
            dwarfPlanetMesh = new THREE.Mesh(geometry, material);
            scene.add(dwarfPlanetMesh);

            // Create new orbit
            dwarfPlanetOrbit = createOrbit(params);
            dwarfPlanetOrbit.material.color.set(0xff0000);
            scene.add(dwarfPlanetOrbit);
        }

        // Event Listeners
        document.querySelectorAll('#dwarf-planet-selector button').forEach(button => {
            button.addEventListener('click', () => {
                document.querySelector('#dwarf-planet-selector .active').classList.remove('active');
                button.classList.add('active');
                selectedDwarfPlanet = button.dataset.planet;
                updateDwarfPlanet();
                updatePositions(document.getElementById('time-slider').value);
            });
        });

        const timeSlider = document.getElementById('time-slider');
        timeSlider.addEventListener('input', () => {
            updateDateDisplay(timeSlider.value);
            updatePositions(timeSlider.value);
        });

        const playPauseButton = document.getElementById('play-pause-button');
        playPauseButton.addEventListener('click', () => {
            isAnimating = !isAnimating;
            playPauseButton.textContent = isAnimating ? '⏸' : '▶';
            if (isAnimating) {
                let lastTime = 0;
                function animate(timestamp) {
                    if (!lastTime) lastTime = timestamp;
                    const delta = timestamp - lastTime;
                    if (delta > 100) {
                        timeSlider.value = (parseInt(timeSlider.value) + 2) % timeSlider.max;
                        updateDateDisplay(timeSlider.value);
                        updatePositions(timeSlider.value);
                        lastTime = timestamp;
                    }
                    animationFrameId = requestAnimationFrame(animate);
                }
                animationFrameId = requestAnimationFrame(animate);
            } else {
                cancelAnimationFrame(animationFrameId);
            }
        });

        // Initial setup
        updateDwarfPlanet();
        updatePositions(timeSlider.value);

        // Resize handler
        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }
        animate();
    </script>
</body>
</html>
