                <div id="plot-cfc" class="plot"></div>
            </div>
            <div id="scene-container"></div>
        </div>
    </div>

    <script>
        const cfcData = {
            'CFC-12': { color: 'purple', data: [[1940, 5], [1950, 20], [1960, 50], [1970, 150], [1980, 320], [1990, 480], [2000, 540], [2010, 520], [2020, 500]] },
            'CFC-11': { color: 'orange', data: [[1940, 2], [1950, 10], [1960, 30], [1970, 90], [1980, 180], [1990, 260], [2000, 250], [2010, 230], [2020, 220]] },
            'CFC-113': { color: 'black', data: [[1940, 0], [1950, 1], [1960, 5], [1970, 15], [1980, 40], [1990, 80], [2000, 85], [2010, 75], [2020, 70]] },
            'CCl4': { color: 'green', data: [[1940, 50], [1950, 60], [1960, 75], [1970, 90], [1980, 100], [1990, 105], [2000, 95], [2010, 85], [2020, 80]] }
        };

        // Initialize dropdown
        const selectCfc = document.getElementById('select-cfc');
        Object.keys(cfcData).forEach(cfc => {
            const option = document.createElement('option');
            option.value = cfc;
            option.textContent = cfc;
            selectCfc.appendChild(option);
        });
        selectCfc.value = 'CFC-12';

        // Initialize sliders
        const sliderYear = document.getElementById('slider-year');
        const sliderTime = document.getElementById('slider-time');
        const labelYear = document.getElementById('label-year');
        const labelTime = document.getElementById('label-time');

        sliderYear.addEventListener('input', () => {
            labelYear.textContent = sliderYear.value;
            update();
        });

        sliderTime.addEventListener('input', () => {
            labelTime.textContent = sliderTime.value;
            update();
        });

        // 3D Scene Setup
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer();
        renderer.setSize(window.innerWidth * 0.75, window.innerHeight * 0.5);
        document.getElementById('scene-container').appendChild(renderer.domElement);

        // Earth
        const earthGeometry = new THREE.SphereGeometry(2, 32, 32);
        const earthMaterial = new THREE.MeshBasicMaterial({ color: 0x0000ff });
        const earth = new THREE.Mesh(earthGeometry, earthMaterial);
        scene.add(earth);

        // Ozone Layer
        const ozoneGeometry = new THREE.SphereGeometry(2.1, 32, 32);
        const ozoneMaterial = new THREE.MeshBasicMaterial({ color: 0x00ff00, transparent: true, opacity: 0.5 });
        const ozoneLayer = new THREE.Mesh(ozoneGeometry, ozoneMaterial);
        scene.add(ozoneLayer);

        // Atmosphere
        const atmosphereGeometry = new THREE.SphereGeometry(2.2, 32, 32);
        const atmosphereMaterial = new THREE.MeshBasicMaterial({ color: 0xffaa88, transparent: true, opacity: 0.1 });
        const atmosphere = new THREE.Mesh(atmosphereGeometry, atmosphereMaterial);
        scene.add(atmosphere);

        // Sun
        const sunGeometry = new THREE.SphereGeometry(0.5, 32, 32);
        const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
        const sun = new THREE.Mesh(sunGeometry, sunMaterial);
        sun.position.set(-5, 0, 0);
        scene.add(sun);

        // Radiation Arrows
        const arrowColor = 0xffa500;
        for (let i = 0; i < 5; i++) {
            const arrow = new THREE.ArrowHelper(
                new THREE.Vector3(1, (Math.random() - 0.5) * 2, (Math.random() - 0.5) * 2).normalize(),
                new THREE.Vector3(-4, 0, 0),
                4,
                arrowColor
            );
            scene.add(arrow);
        }

        camera.position.set(0, 2, 5);
        camera.lookAt(0, 0, 0);

        function animate() {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        }
        animate();

        // Plot Initialization
        let ozonePlot = document.getElementById('plot-ozone');
        let cfcPlot = document.getElementById('plot-cfc');

        function interpolateCFC(cfc, year) {
            const data = cfcData[cfc].data;
            for (let i = 0; i < data.length - 1; i++) {
                if (year >= data[i][0] && year <= data[i + 1][0]) {
                    const t = (year - data[i][0]) / (data[i + 1][0] - data[i][0]);
                    return data[i][1] + t * (data[i + 1][1] - data[i][1]);
                }
            }
            return year < data[0][0] ? data[0][1] : data[data.length - 1][1];
        }

        function update() {
            const selectedCfc = selectCfc.value;
            const showAll = document.getElementById('checkbox-show-all').checked;
            const year = parseInt(sliderYear.value);
            const time = parseInt(sliderTime.value);

            // Update CFC Plot
            const traces = [];
            Object.entries(cfcData).forEach(([cfc, { color, data }]) => {
                if (showAll || cfc === selectedCfc) {
                    traces.push({
                        x: data.map(d => d[0]),
                        y: data.map(d => d[1]),
                        type: 'scatter',
                        mode: 'lines',
                        name: cfc,
                        line: { color: color, width: cfc === selectedCfc && !showAll ? 3 : 1 }
                    });
                }
            });
            const redMarker = {
                x: [year],
                y: [interpolateCFC(selectedCfc, year)],
                mode: 'markers',
                marker: { color: 'red', size: 10 },
                type: 'scatter'
            };
            traces.push(redMarker);

            Plotly.newPlot(cfcPlot, traces, {
                title: 'CFC concentration',
                xaxis: { range: [1920, 2020], title: 'year' },
                yaxis: { range: [0, 600] },
                showlegend: showAll
            });

            // Update Ozone Plot
            const cfcConcentration = interpolateCFC(selectedCfc, year);
            const k = 0.1; // Rate constant
            const ozone0 = 100; // Initial ozone concentration
            const tValues = Array.from({ length: time + 1 }, (_, i) => i);
            const yValues = tValues.map(t => 1 / ozone0 + k * cfcConcentration * t);

            Plotly.newPlot(ozonePlot, [{
                x: tValues,
                y: yValues,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'blue' }
            }], {
                title: 'concentration of ozone',
                xaxis: { range: [0, 30], title: 'time (s)' },
                yaxis: { range: [0, 110], title: { text: '1/[O<sub>3</sub>]', font: { size: 14 } } }
            });

            // Update Ozone Layer Opacity
            ozoneMaterial.opacity = Math.max(0.1, 1 - cfcConcentration / 600);
        }

        // Initial render
        update();

        // Event Listeners
        selectCfc.addEventListener('change', update);
        document.getElementById('checkbox-show-all').addEventListener('change', update);
    </script>
</body>
</html>
