  <script>
    function setup() {
      const canvas = createCanvas(500, 500);
      canvas.parent('viz-container');
      noLoop();

      // Add event listeners to sliders
      document.getElementById('slider-moon-pos').addEventListener('input', redraw);
      document.getElementById('slider-moon-pull').addEventListener('input', redraw);
      document.getElementById('slider-sun-pos').addEventListener('input', redraw);
      document.getElementById('slider-sun-pull').addEventListener('input', redraw);

      // Add reset button listener
      document.getElementById('btn-reset').addEventListener('click', () => {
        document.getElementById('slider-moon-pos').value = 144;
        document.getElementById('slider-moon-pull').value = 40;
        document.getElementById('slider-sun-pos').value = 144;
        document.getElementById('slider-sun-pull').value = 20;
        redraw();
      });

      // Initial draw
      redraw();
    }

    function draw() {
      // Read slider values
      const moonAngle = parseFloat(document.getElementById('slider-moon-pos').value);
      const moonPull = parseFloat(document.getElementById('slider-moon-pull').value);
      const sunAngle = parseFloat(document.getElementById('slider-sun-pos').value);
      const sunPull = parseFloat(document.getElementById('slider-sun-pull').value);

      // Convert angles to radians
      const moonAngleRad = radians(moonAngle);
      const sunAngleRad = radians(sunAngle);

      // Clear canvas
      background(255);

      // Draw water layer
      fill('#aed6f1');
      stroke(0);
      strokeWeight(1);
      beginShape();
      for (let theta = 0; theta <= 360; theta += 1) {
        const thetaRad = radians(theta);
        const moonEffect = moonPull * pow(cos(thetaRad - moonAngleRad), 2);
        const sunEffect = sunPull * pow(cos(thetaRad - sunAngleRad), 2);
        const totalRadius = 200 + 0.2 * (moonEffect + sunEffect);
        const x = 250 + totalRadius * cos(thetaRad);
        const y = 250 + totalRadius * sin(thetaRad);
        vertex(x, y);
      }
      endShape(CLOSE);

      // Draw Earth
      fill('#966F33');
      noStroke();
      ellipse(250, 250, 360, 360);
    }
  </script>
</body>
</html>
