            comet: { a: 14.4, e: 0.432, color: 'yellow' }
        };

        // Initialize scenes
        const orbitScene = new THREE.Scene();
        const telescopicScene = new THREE.Scene();
        telescopicScene.background = new THREE.Color(0x000000);

        // Orbit camera
        const orbitCamera = new THREE.PerspectiveCamera(45, window.innerWidth/2 / (window.innerHeight/2 - 40), 0.1, 1000);
        orbitCamera.position.set(0, 20, 20);
        orbitCamera.lookAt(0, 0, 0);

        // Telescopic camera
        const telescopicCamera = new THREE.PerspectiveCamera(15, window.innerWidth/2 / (window.innerHeight/2 - 40), 0.1, 1000);

