        const sun = new THREE.Mesh(sunGeometry, sunMaterial);
        orbitScene.add(sun);
        telescopicScene.add(sun.clone());

        // Create orbit lines
        function createOrbit(a, e, color) {
            const f = a * e;
            const b = Math.sqrt(a*a*(1-e*e));
            const curve = new THREE.EllipseCurve(0, 0, a, b, 0, 2 * Math.PI, false, 0);
            const points = curve.getPoints(100);
            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color: color });
            return new THREE.Line(geometry, material);
        }

        for (const planet in orbits) {
            const orbit = createOrbit(orbits[planet].a, orbits[planet].e, orbits[planet].color);
            orbitScene.add(orbit);
        }

        // Comet group
        const cometGroup = new THREE.Group();

        // Nucleus
        const nucleusGeometry = new THREE.SphereGeometry(0.2, 32, 32);
        const nucleusMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff });
        const nucleus = new THREE.Mesh(nucleusGeometry, nucleusMaterial);
        cometGroup.add(nucleus);

        // Coma
        const comaGeometry = new THREE.SphereGeometry(1, 32, 32);
        const comaMaterial = new THREE.MeshBasicMaterial({ color: 0x90ee90, opacity: 0.5, transparent: true });
        const coma = new THREE.Mesh(comaGeometry, comaMaterial);
        cometGroup.add(coma);

        // Tail
        const tailGeometry = new THREE.ConeGeometry(1, 5, 32);
        const tailMaterial = new THREE.MeshBasicMaterial({ color: 0x90ee90, opacity: 0.4, transparent: true });
        const tail = new THREE.Mesh(tailGeometry, tailMaterial);
        tail.rotation.x = Math.PI / 2;
        cometGroup.add(tail);

        orbitScene.add(cometGroup);
        telescopicScene.add(cometGroup);

        // Earth
        const earthGeometry = new THREE.SphereGeometry(0.3, 32, 32);
        const earthMaterial = new THREE.MeshBasicMaterial({ color: 0x0000ff });
        const earth = new THREE.Mesh(earthGeometry, earthMaterial);
        orbitScene.add(earth);

        // Renderers
        const orbitRenderer = new THREE.WebGLRenderer({ canvas: document.getElementById('orbit-canvas'), antialias: true });
        const telescopicRenderer = new THREE.WebGLRenderer({ canvas: document.getElementById('telescopic-canvas'), antialias: true });

        // Resize handlers
        function resizeRenderer(renderer, camera) {
            const width = renderer.domElement.parentElement.clientWidth;
            const height = renderer.domElement.parentElement.clientHeight;
            renderer.setSize(width, height);
            camera.aspect = width / height;
            camera.updateProjectionMatrix();
        }

        window.addEventListener('resize', () => {
            resizeRenderer(orbitRenderer, orbitCamera);
            resizeRenderer(telescopicRenderer, telescopicCamera);
        });

        // Initial resize
        resizeRenderer(orbitRenderer, orbitCamera);
        resizeRenderer(telescopicRenderer, telescopicCamera);

        // Slider elements
        const sliders = {
            earthPosition: document.getElementById('slider-earth-position'),
            cometPosition: document.getElementById('slider-comet-position'),
            coma: document.getElementById('slider-coma'),
            tail: document.getElementById('slider-tail'),
            tailSpread: document.getElementById('slider-tail-spread')
        };

        // Update function
        function update() {
            // Get values
            const earthAngle = THREE.MathUtils.degToRad(Number(sliders.earthPosition.value));
            const cometAngle = THREE.MathUtils.degToRad(Number(sliders.cometPosition.value));
            const comaScale = Number(sliders.coma.value) / 20 + 0.1;
            const tailLength = Number(sliders.tail.value) / 20 + 0.1;
            const tailWidth = Number(sliders.tailSpread.value) / 50 + 0.1;

            // Update Earth position
            const earthOrbit = orbits.earth;
            earth.position.x = earthOrbit.a * Math.cos(earthAngle);
            earth.position.z = earthOrbit.a * Math.sin(earthAngle);

            // Update comet position
            const cometOrbit = orbits.comet;
            const cometX = cometOrbit.a * Math.cos(cometAngle);
            const cometZ = cometOrbit.a * Math.sin(cometAngle);
            cometGroup.position.set(cometX, 0, cometZ);

            // Update camera positions
            telescopicCamera.position.copy(earth.position);
            telescopicCamera.lookAt(cometGroup.position);

            // Update coma scale
            coma.scale.set(comaScale, comaScale, comaScale);

            // Update tail
            tail.scale.set(tailWidth, tailLength, tailWidth);
            const tailDirection = new THREE.Vector3().copy(cometGroup.position).normalize();
            tail.quaternion.setFromUnitVectors(new THREE.Vector3(0, 1, 0), tailDirection);

            // Update orbit camera lookat
            orbitCamera.lookAt(0, 0, 0);
        }

        // Event listeners
        for (const slider of Object.values(sliders)) {
            slider.addEventListener('input', update);
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            orbitRenderer.render(orbitScene, orbitCamera);
            telescopicRenderer.render(telescopicScene, telescopicCamera);
        }

        // Initial update
        update();
        animate();
    </script>
</body>
</html>
