            width: 100%;
            height: 250px;
            margin-bottom: 10px;
        }
        button {
            padding: 8px 16px;
            margin: 0 5px;
            font-size: 16px;
            cursor: pointer;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="controls-container">
            <h2>Initial Conditions</h2>
            <div class="control-row">
                <label for="slider-displacement">Displacement</label>
                <input type="range" id="slider-displacement" min="-3" max="3" step="0.1" value="0.1">
                <span id="value-displacement">0.1</span>
            </div>
            <div class="control-row">
                <label for="slider-velocity">Velocity</label>
                <input type="range" id="slider-velocity" min="-5" max="5" step="0.1" value="1">
                <span id="value-velocity">1.0</span>
            </div>
            <h2>System Parameters</h2>
            <div class="control-row">
                <label for="slider-stiffness">Spring Stiffness</label>
                <input type="range" id="slider-stiffness" min="0.1" max="5" step="0.01" value="1">
                <span id="value-stiffness">1.00</span>
            </div>
            <div class="control-row">
                <label for="slider-mass">Mass</label>
                <input type="range" id="slider-mass" min="0.1" max="5" step="0.01" value="1">
                <span id="value-mass">1.00</span>
            </div>
            <div class="control-row">
                <label for="slider-damping">Damping</label>
                <input type="range" id="slider-damping" min="0" max="5" step="0.1" value="0.1">
                <span id="value-damping">0.1</span>
            </div>
            <h2>Controller Gains</h2>
            <div class="control-row">
                <label for="slider-proportional-gain">Proportional Gain</label>
                <input type="range" id="slider-proportional-gain" min="0" max="5" step="0.1" value="0.1">
                <span id="value-proportional-gain">0.1</span>
            </div>
            <div class="control-row">
                <label for="slider-derivative-gain">Derivative Gain</label>
                <input type="range" id="slider-derivative-gain" min="0" max="5" step="0.1" value="0.1">
                <span id="value-derivative-gain">0.1</span>
            </div>
            <h2>Animation Controls</h2>
            <div class="control-row">
                <button id="btn-play">▶</button>
                <button id="btn-pause">||</button>
                <button id="btn-reset">|◀</button>
            </div>
        </div>
        <div id="visuals-container">
            <div id="scene-container"></div>
            <div id="plot-displacement"></div>
            <div id="plot-velocity"></div>
        </div>
    </div>

    <script>
        // State variables
        let time = 0;
        let animationFrameId = null;
        let isRunning = false;
        let displacementData = [];
        let velocityData = [];

        // 3D Scene setup
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(50, window.innerWidth / window.innerHeight, 0.1, 100);
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth * 0.7, 400);
        document.getElementById('scene-container').appendChild(renderer.domElement);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);
        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(5, 5, 5);
        scene.add(directionalLight);

        // Base
        const baseGeometry = new THREE.BoxGeometry(10, 0.1, 5);
        const baseMaterial = new THREE.MeshStandardMaterial({ color: 0x00ff00 });
        const base = new THREE.Mesh(baseGeometry, baseMaterial);
        scene.add(base);

        // Mass
        const massGeometry = new THREE.BoxGeometry(1, 1, 1);
        const massMaterial = new THREE.MeshStandardMaterial({ color: 0xffff00 });
        const mass = new THREE.Mesh(massGeometry, massMaterial);
        scene.add(mass);

        // Spring
        const springPath = new THREE.CatmullRomCurve3([
            new THREE.Vector3(-2, 0, 0),
            new THREE.Vector3(0, 0, 0),
            new THREE.Vector3(0, 0, 0),
            new THREE.Vector3(2, 0, 0)
        ]);
        const springGeometry = new THREE.TubeGeometry(springPath, 20, 0.1, 8, false);
        const springMaterial = new THREE.MeshStandardMaterial({ color: 0xff0000 });
        const spring = new THREE.Mesh(springGeometry, springMaterial);
        scene.add(spring);

        // Damper
        const damperHousingGeometry = new THREE.CylinderGeometry(0.2, 0.2, 2, 32);
        const damperHousingMaterial = new THREE.MeshStandardMaterial({ color: 0x0000ff });
        const damperHousing = new THREE.Mesh(damperHousingGeometry, damperHousingMaterial);
        damperHousing.rotation.x = Math.PI / 2;
        scene.add(damperHousing);

        const damperPistonGeometry = new THREE.CylinderGeometry(0.15, 0.15, 2, 32);
        const damperPistonMaterial = new THREE.MeshStandardMaterial({ color: 0x6666ff });
        const damperPiston = new THREE.Mesh(damperPistonGeometry, damperPistonMaterial);
        damperPiston.rotation.x = Math.PI / 2;
        scene.add(damperPiston);

        // Enclosure
        const enclosureGeometry = new THREE.BoxGeometry(12, 12, 12);
        const enclosureMaterial = new THREE.MeshBasicMaterial({ wireframe: true, transparent: true });
        const enclosure = new THREE.Mesh(enclosureGeometry, enclosureMaterial);
        scene.add(enclosure);

        camera.position.set(10, 10, 10);
        camera.lookAt(0, 0, 0);

        // Plotly setup
        const displacementPlot = document.getElementById('plot-displacement');
        const velocityPlot = document.getElementById('plot-velocity');

        // Initialize plots
        function initPlots() {
            Plotly.newPlot(displacementPlot, [
                { x: [], y: [], mode: 'lines', name: 'Displacement' },
                { x: [0], y: [0], mode: 'markers', marker: { color: 'blue' }, name: 'Current' }
            ], {
                xaxis: { range: [0, 10], title: 'Time (s)' },
                yaxis: { range: [-3, 3], title: 'Displacement (m)' },
                margin: { l: 50, r: 20, b: 50, t: 20 },
                showlegend: false
            });

            Plotly.newPlot(velocityPlot, [
                { x: [], y: [], mode: 'lines', name: 'Velocity' },
                { x: [0], y: [0], mode: 'markers', marker: { color: 'green' }, name: 'Current' }
            ], {
                xaxis: { range: [0, 10], title: 'Time (s)' },
                yaxis: { range: [-3, 3], title: 'Velocity (m/s)' },
                margin: { l: 50, r: 20, b: 50, t: 20 },
                showlegend: false
            });
        }

        // Update plots with new data
        function updatePlots() {
            const t = Array.from({ length: 1001 }, (_, i) => i / 100);
            const x = t.map(ti => calculateDisplacement(ti));
            const v = t.map(ti => calculateVelocity(ti));

            displacementData = x;
            velocityData = v;

            Plotly.react(displacementPlot, [
                { x: t, y: x },
                { x: [0], y: [x[0]] }
            ]);

            Plotly.react(velocityPlot, [
                { x: t, y: v },
                { x: [0], y: [v[0]] }
            ]);
        }

        // Analytical solution for displacement
        function calculateDisplacement(t) {
            const m = parseFloat(document.getElementById('slider-mass').value);
            const c = parseFloat(document.getElementById('slider-damping').value);
            const k = parseFloat(document.getElementById('slider-stiffness').value);
            const Kp = parseFloat(document.getElementById('slider-proportional-gain').value);
            const Kd = parseFloat(document.getElementById('slider-derivative-gain').value);
            const x0 = parseFloat(document.getElementById('slider-displacement').value);
            const v0 = parseFloat(document.getElementById('slider-velocity').value);

            const k_eff = k + Kp;
            const c_eff = c + Kd;
            const omega_n = Math.sqrt(k_eff / m);
            const zeta = c_eff / (2 * Math.sqrt(m * k_eff));

            if (zeta < 1) { // Underdamped
                const omega_d = omega_n * Math.sqrt(1 - zeta * zeta);
                const A = x0;
                const B = (v0 + zeta * omega_n * x0) / omega_d;
                return Math.exp(-zeta * omega_n * t) * (A * Math.cos(omega_d * t) + B * Math.sin(omega_d * t));
            } else if (zeta === 1) { // Critically damped
                const A = x0;
                const B = v0 + omega_n * x0;
                return (A + B * t) * Math.exp(-omega_n * t);
            } else { // Overdamped
                const r1 = -omega_n * (zeta - Math.sqrt(zeta * zeta - 1));
                const r2 = -omega_n * (zeta + Math.sqrt(zeta * zeta - 1));
                const A = (v0 - r2 * x0) / (r1 - r2);
                const B = x0 - A;
                return A * Math.exp(r1 * t) + B * Math.exp(r2 * t);
            }
        }

        // Analytical solution for velocity
        function calculateVelocity(t) {
            const m = parseFloat(document.getElementById('slider-mass').value);
            const c = parseFloat(document.getElementById('slider-damping').value);
            const k = parseFloat(document.getElementById('slider-stiffness').value);
            const Kp = parseFloat(document.getElementById('slider-proportional-gain').value);
            const Kd = parseFloat(document.getElementById('slider-derivative-gain').value);
            const x0 = parseFloat(document.getElementById('slider-displacement').value);
            const v0 = parseFloat(document.getElementById('slider-velocity').value);

            const k_eff = k + Kp;
            const c_eff = c + Kd;
            const omega_n = Math.sqrt(k_eff / m);
            const zeta = c_eff / (2 * Math.sqrt(m * k_eff));

            if (zeta < 1) { // Underdamped
                const omega_d = omega_n * Math.sqrt(1 - zeta * zeta);
                const A = x0;
                const B = (v0 + zeta * omega_n * x0) / omega_d;
                return Math.exp(-zeta * omega_n * t) * [
                    (-zeta * omega_n * (A * Math.cos(omega_d * t) + B * Math.sin(omega_d * t))) +
                    (-omega_d * A * Math.sin(omega_d * t) + omega_d * B * Math.cos(omega_d * t))
                ];
            } else if (zeta === 1) { // Critically damped
                const A = x0;
                const B = v0 + omega_n * x0;
                return -omega_n * (A + B * t) * Math.exp(-omega_n * t) + B * Math.exp(-omega_n * t);
            } else { // Overdamped
                const r1 = -omega_n * (zeta - Math.sqrt(zeta * zeta - 1));
                const r2 = -omega_n * (zeta + Math.sqrt(zeta * zeta - 1));
                const A = (v0 - r2 * x0) / (r1 - r2);
                const B = x0 - A;
                return A * r1 * Math.exp(r1 * t) + B * r2 * Math.exp(r2 * t);
            }
        }

        // Update 3D scene
        function update3DScene(x) {
            mass.position.x = x;
            spring.scale.x = Math.abs(x) + 0.1;
            spring.position.x = x / 2;
            damperPiston.position.x = x;
        }

        // Animation loop
        function animate() {
            if (!isRunning) return;

            time += 0.02;
            if (time > 10) {
                isRunning = false;
                time = 10;
            }

            const x = calculateDisplacement(time);
            const v = calculateVelocity(time);

            // Update 3D scene
            update3DScene(x);

            // Update plots
            Plotly.restyle(displacementPlot, {
                x: [[time]],
                y: [[x]]
            }, [1]);

            Plotly.restyle(velocityPlot, {
                x: [[time]],
                y: [[v]]
            }, [1]);

            renderer.render(scene, camera);
            animationFrameId = requestAnimationFrame(animate);
        }

        // Event listeners
        document.getElementById('btn-play').addEventListener('click', () => {
            if (!isRunning) {
                isRunning = true;
                animate();
            }
        });

        document.getElementById('btn-pause').addEventListener('click', () => {
            isRunning = false;
            cancelAnimationFrame(animationFrameId);
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            isRunning = false;
            cancelAnimationFrame(animationFrameId);
            time = 0;
            const x0 = parseFloat(document.getElementById('slider-displacement').value);
            update3DScene(x0);
            updatePlots();
        });

        // Slider event listeners
        const sliders = document.querySelectorAll('input[type="range"]');
        sliders.forEach(slider => {
            slider.addEventListener('input', () => {
                const valueSpan = document.querySelector(`#value-${slider.id}`);
                valueSpan.textContent = parseFloat(slider.value).toFixed(slider.step.includes('.') ? 2 : 0);
                isRunning = false;
                cancelAnimationFrame(animationFrameId);
                time = 0;
                updatePlots();
                const x0 = parseFloat(document.getElementById('slider-displacement').value);
                update3DScene(x0);
            });
        });

        // Initial setup
        initPlots();
        updatePlots();
        update3DScene(parseFloat(document.getElementById('slider-displacement').value));
        renderer.render(scene, camera);
    </script>
</body>
</html>
