<!DOCTYPE html>
<html>
<head>
    <title>String Mass Simulation</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/numeric/1.2.6/numeric.min.js"></script>
    <style>
        #main-container {
            display: flex;
            height: 100vh;
        }
        #control-panel {
            width: 25%;
            padding: 10px;
            border-right: 1px solid #ccc;
        }
        #visualization-area {
            width: 75%;
            padding: 10px;
        }
        .plot-container {
            height: 300px;
            margin-top: 10px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <h4>tension T</h4>
            <input type="range" id="slider-tension" min="1" max="100" step="1" value="50">
            <h4>displacement</h4>
            <label for="slider-x1">x₁</label>
            <input type="range" id="slider-x1" min="-0.5" max="0.5" step="0.01" value="0.1">
            <label for="slider-x2">x₂</label>
            <input type="range" id="slider-x2" min="-0.5" max="0.5" step="0.01" value="0.1">
            <label for="slider-x3">x₃</label>
            <input type="range" id="slider-x3" min="-0.5" max="0.5" step="0.01" value="0.0">
            <h4>mass</h4>
            <label for="slider-m1">m₁</label>
            <input type="range" id="slider-m1" min="0.1" max="2.0" step="0.05" value="1.0">
            <label for="slider-m2">m₂</label>
            <input type="range" id="slider-m2" min="0.1" max="2.0" step="0.05" value="1.0">
            <label for="slider-m3">m₃</label>
            <input type="range" id="slider-m3" min="0.1" max="2.0" step="0.05" value="1.0">
            <h4>release system</h4>
            <div id="animation-controls">
                <button id="btn-play-pause">▶</button>
                <button id="btn-reset">⏮</button>
            </div>
        </div>
        <div id="visualization-area">
            <div id="main-animation-container"></div>
            <div id="displacement-plot-container" class="plot-container"></div>
            <div id="velocity-plot-container" class="plot-container"></div>
        </div>
    </div>

    <script>
        let isAnimating = false;
        let t = 0;
        let omega = [];
        let A_matrix = [];
        let c = [];
        let timeArray = [];
        let displacementData = [[], [], []];
        let velocityData = [[], [], []];
        let masses = [1.0, 1.0, 1.0];
        let displacements = [0.1, 0.1, 0.0];
        let tension = 50;
        let myp5;

        document.addEventListener('DOMContentLoaded', function() {
            const sliders = [
                'slider-tension', 'slider-x1', 'slider-x2', 'slider-x3',
                'slider-m1', 'slider-m2', 'slider-m3'
            ];
            sliders.forEach(id => {
                document.getElementById(id).addEventListener('input', resetSimulation);
            });

            document.getElementById('btn-play-pause').addEventListener('click', togglePlayPause);
            document.getElementById('btn-reset').addEventListener('click', resetSimulation);

            let sketch = function(p) {
                p.setup = function() {
                    p.createCanvas(400, 300);
                    p.frameRate(30);
                };

                p.draw = function() {
                    if (!isAnimating) return;
                    t += 0.01;
                    if (t > 5) t = 0;
                    drawScene(p);
                    updatePlotsVerticalLine();
                };
            };
            myp5 = new p5(sketch, 'main-animation-container');

            resetSimulation();
        });

        function togglePlayPause() {
            isAnimating = !isAnimating;
            if (isAnimating) {
                document.getElementById('btn-play-pause').textContent = '❚❚';
                myp5.loop();
            } else {
                document.getElementById('btn-play-pause').textContent = '▶';
                myp5.noLoop();
            }
        }

        function resetSimulation() {
            isAnimating = false;
            document.getElementById('btn-play-pause').textContent = '▶';
            myp5.noLoop();

            tension = parseFloat(document.getElementById('slider-tension').value);
            displacements[0] = parseFloat(document.getElementById('slider-x1').value);
            displacements[1] = parseFloat(document.getElementById('slider-x2').value);
            displacements[2] = parseFloat(document.getElementById('slider-x3').value);
            masses[0] = parseFloat(document.getElementById('slider-m1').value);
            masses[1] = parseFloat(document.getElementById('slider-m2').value);
            masses[2] = parseFloat(document.getElementById('slider-m3').value);

            t = 0;

            let M = numeric.diag(masses);
            let K = [
                [2*tension, -tension, 0],
                [-tension, 2*tension, -tension],
                [0, -tension, 2*tension]
            ];
            let M_inv_sqrt = numeric.diag(masses.map(m => 1/Math.sqrt(m)));
            let C = numeric.dot(numeric.dot(M_inv_sqrt, K), M_inv_sqrt);

            let eig = numeric.eig(C);
            let eigenvalues = eig.lambda.x;
            omega = eigenvalues.map(lambda => Math.sqrt(lambda));

            A_matrix = numeric.dot(M_inv_sqrt, eig.eigenvectors);

            let y0 = displacements.slice();
            c = numeric.solve(A_matrix, y0);

            const timeSteps = 500;
            const dt = 5 / timeSteps;
            timeArray = [];
            displacementData = [[], [], []];
            velocityData = [[], [], []];
            for (let step = 0; step <= timeSteps; step++) {
                let tVal = step * dt;
                timeArray.push(tVal);
                let y = calculateDisplacement(tVal);
                let v = calculateVelocity(tVal);
                for (let j = 0; j < 3; j++) {
                    displacementData[j].push(y[j]);
                    velocityData[j].push(v[j]);
                }
            }

            updatePlots();
            myp5.redraw();
            updatePlotsVerticalLine();
        }

        function calculateDisplacement(tVal) {
            let y = [0, 0, 0];
            for (let i = 0; i < 3; i++) {
                let cosTerm = Math.cos(omega[i] * tVal);
                for (let j = 0; j < 3; j++) {
                    y[j] += c[i] * A_matrix[j][i] * cosTerm;
                }
            }
            return y;
        }

        function calculateVelocity(tVal) {
            let v = [0, 0, 0];
            for (let i = 0; i < 3; i++) {
                let sinTerm = Math.sin(omega[i] * tVal);
                for (let j = 0; j < 3; j++) {
                    v[j] -= c[i] * A_matrix[j][i] * omega[i] * sinTerm;
                }
            }
            return v;
        }

        function updatePlots() {
            let displacementTraces = [
                {x: timeArray, y: displacementData[0], type: 'line', name: 'm1', line: {color: 'blue'}},
                {x: timeArray, y: displacementData[1], type: 'line', name: 'm2', line: {color: 'orange'}},
                {x: timeArray, y: displacementData[2], type: 'line', name: 'm3', line: {color: 'green'}}
            ];
            let displacementLayout = {
                title: 'displacement',
                xaxis: {title: 'time t', range: [0,5]},
                yaxis: {title: 'y(t)'},
                shapes: [{
                    type: 'line',
                    x0: 0, x1: 0,
                    y0: 0, y1: 1,
                    yref: 'paper',
                    line: {color: 'red', width: 2}
                }]
            };
            Plotly.newPlot('displacement-plot-container', displacementTraces, displacementLayout);

            let velocityTraces = [
                {x: timeArray, y: velocityData[0], type: 'line', name: 'm1', line: {color: 'blue'}},
                {x: timeArray, y: velocityData[1], type: 'line', name: 'm2', line: {color: 'orange'}},
                {x: timeArray, y: velocityData[2], type: 'line', name: 'm3', line: {color: 'green'}}
            ];
            let velocityLayout = {
                title: 'velocity',
                xaxis: {title: 'time t', range: [0,5]},
                yaxis: {title: 'v(t)'},
                shapes: [{
                    type: 'line',
                    x0: 0, x1: 0,
                    y0: 0, y1: 1,
                    yref: 'paper',
                    line: {color: 'red', width: 2}
                }]
            };
            Plotly.newPlot('velocity-plot-container', velocityTraces, velocityLayout);
        }

        function updatePlotsVerticalLine() {
            Plotly.relayout('displacement-plot-container', {
                'shapes[0].x0': t,
                'shapes[0].x1': t
            });
            Plotly.relayout('velocity-plot-container', {
                'shapes[0].x0': t,
                'shapes[0].x1': t
            });
        }

        function drawScene(p) {
            p.background(255);
            p.stroke(200);
            for (let x = 0; x <=4; x++) {
                p.line(p.map(x, 0,4,0,p.width), 0, p.map(x,0,4,0,p.width), p.height);
            }
            for (let y = -1; y <=1; y += 0.5) {
                p.line(0, p.map(y, -1,1,p.height,0), p.width, p.map(y, -1,1,p.height,0));
            }
            p.stroke(0);
            p.line(0, p.map(0,-1,1,p.height,0), p.width, p.map(0,-1,1,p.height,0));
            p.line(p.map(0,0,4,0,p.width), 0, p.map(0,0,4,0,p.width), p.height);
            let y = calculateDisplacement(t);
            let points = [
                {x:0, y:0},
                {x:1, y:y[0]},
                {x:2, y:y[1]},
                {x:3, y:y[2]},
                {x:4, y:0}
            ];
            p.stroke(0);
            p.noFill();
            p.beginShape();
            for (let pt of points) {
                p.vertex(p.map(pt.x, 0,4,0,p.width), p.map(pt.y, -1,1,p.height,0));
            }
            p.endShape();
            p.fill(255,0,0);
            p.noStroke();
            let r1 = Math.sqrt(masses[0]) * 10;
            let r2 = Math.sqrt(masses[1]) * 10;
            let r3 = Math.sqrt(masses[2]) * 10;
            p.ellipse(p.map(1,0,4,0,p.width), p.map(y[0], -1,1,p.height,0), r1, r1);
            p.ellipse(p.map(2,0,4,0,p.width), p.map(y[1], -1,1,p.height,0), r2, r2);
            p.ellipse(p.map(3,0,4,0,p.width), p.map(y[2], -1,1,p.height,0), r3, r3);
        }
    </script>
</body>
</html>
