```html
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Jarvis March Convex Hull Visualization</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
        }
        #app-container {
            display: flex;
            flex-direction: column;
            align-items: center;
            padding: 10px;
        }
        #header-controls, #step-controls {
            margin: 10px 0;
        }
        button {
            background-color: #f0f0f0;
            border: 1px solid #ccc;
            padding: 8px 12px;
            margin: 2px;
            cursor: pointer;
        }
        button:disabled {
            background-color: #ddd;
            cursor: not-allowed;
        }
        #canvas-container {
            position: relative;
            width: 700px;
            height: 500px;
            border: 1px solid #ccc;
            margin: 10px 0;
        }
        #explanation-panel {
            width: 700px;
            padding: 10px;
            background-color: #f9f9f9;
            border: 1px solid #ccc;
            text-align: left;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="header-controls">
            <button id="btn-move">move the points</button>
            <button id="btn-start-algo">start the algorithm</button>
        </div>
        <div id="step-controls" style="display: none;">
            <div style="display: inline-block; margin-right: 20px;">
                <button disabled>step 1</button>
                <button id="btn-step1-explain" disabled>explain</button>
                <button id="btn-step1-do" disabled>do it</button>
            </div>
            <div style="display: inline-block; margin-right: 20px;">
                <button disabled>step 2</button>
                <button id="btn-step2-explain" disabled>explain</button>
                <button id="btn-step2-do" disabled>do it</button>
            </div>
            <div id="step3-controls-container" style="display: inline-block;">
                <button disabled>step 3</button>
                <button id="btn-step3-explain" disabled>explain</button>
                <div id="step3-actions" style="display: inline-block;"></div>
            </div>
        </div>
        <div id="canvas-container"></div>
        <div id="explanation-panel">
            <p id="explanation-text"></p>
        </div>
    </div>

    <script>
        let mode = 'move';
        let points = [];
        let draggedPoint = null;
        let algorithmState = {
            step: 0,
            hullPointsIndices: [],
            fullHull: [],
            leftmostPointIndex: null,
            currentPointIndex: null,
            candidateLines: [],
            explanationSteps: []
        };

        function setup() {
            const canvas = createCanvas(700, 500);
            canvas.parent('canvas-container');
            resetPoints();
            noLoop();
        }

        function resetPoints() {
            points = [
                createVector(150, 350),
                createVector(250, 150),
                createVector(350, 250),
                createVector(550, 180),
                createVector(480, 320),
                createVector(420, 400)
            ];
        }

        function draw() {
            background(255);
            stroke(220);
            noFill();
            rect(0, 0, width, height);

            // Draw points and hull
            for (let i = 0; i < points.length; i++) {
                const p = points[i];
                if (algorithmState.hullPointsIndices.includes(i)) {
                    fill(45, 60, 110);
                } else {
                    fill(200, 220, 100);
                }
                stroke(0);
                ellipse(p.x, p.y, 12, 12);
                line(p.x - 6, p.y, p.x + 6, p.y);
                line(p.x, p.y - 6, p.x, p.y + 6);
            }

            // Draw candidate lines
            if (algorithmState.candidateLines.length > 0) {
                stroke(60, 150, 140);
                strokeWeight(2);
                for (const [from, to] of algorithmState.candidateLines) {
                    const p1 = points[from];
                    const p2 = points[to];
                    line(p1.x, p1.y, p2.x, p2.y);
                }
            }

            // Draw hull edges
            if (algorithmState.hullPointsIndices.length > 1) {
                stroke(45, 60, 110);
                strokeWeight(3);
                noFill();
                beginShape();
                for (const i of algorithmState.hullPointsIndices) {
                    const p = points[i];
                    vertex(p.x, p.y);
                }
                if (algorithmState.step === 3 && algorithmState.hullPointsIndices.length === algorithmState.fullHull.length) {
                    const first = points[algorithmState.hullPointsIndices[0]];
                    vertex(first.x, first.y);
                }
                endShape();
            }
        }

        function mousePressed() {
            if (mode === 'move') {
                for (let i = 0; i < points.length; i++) {
                    const d = dist(mouseX, mouseY, points[i].x, points[i].y);
                    if (d < 6) {
                        draggedPoint = i;
                        break;
                    }
                }
            }
        }

        function mouseDragged() {
            if (draggedPoint !== null) {
                points[draggedPoint].x = mouseX;
                points[draggedPoint].y = mouseY;
                redraw();
            }
        }

        function mouseReleased() {
            draggedPoint = null;
        }

        function resetAlgorithm() {
            algorithmState = {
                step: 0,
                hullPointsIndices: [],
                fullHull: [],
                leftmostPointIndex: null,
                currentPointIndex: null,
                candidateLines: [],
                explanationSteps: []
            };
            document.getElementById('explanation-text').textContent = '';
        }

        function precomputeHull() {
            const hull = [];
            let leftmost = 0;
            for (let i = 1; i < points.length; i++) {
                if (points[i].x < points[leftmost].x) {
                    leftmost = i;
                }
            }
            let p = leftmost;
            do {
                hull.push(p);
                let q = (p + 1) % points.length;
                for (let i = 0; i < points.length; i++) {
                    if (i === p) continue;
                    if (isClockwise(points[p], points[q], points[i])) {
                        q = i;
                    }
                }
                p = q;
            } while (p !== leftmost);
            return hull;
        }

        function isClockwise(p1, p2, p3) {
            return (p2.x - p1.x) * (p3.y - p1.y) - (p2.y - p1.y) * (p3.x - p1.x) < 0;
        }

        document.getElementById('btn-move').addEventListener('click', () => {
            mode = 'move';
            resetAlgorithm();
            document.getElementById('step-controls').style.display = 'none';
            document.getElementById('btn-step1-explain').disabled = true;
            document.getElementById('btn-step1-do').disabled = true;
            document.getElementById('btn-step2-explain').disabled = true;
            document.getElementById('btn-step2-do').disabled = true;
            document.getElementById('btn-step3-explain').disabled = true;
            document.getElementById('step3-actions').innerHTML = '';
            redraw();
        });

        document.getElementById('btn-start-algo').addEventListener('click', () => {
            mode = 'algorithm';
            resetAlgorithm();
            algorithmState.fullHull = precomputeHull();
            document.getElementById('step-controls').style.display = 'block';
            document.getElementById('btn-step1-explain').disabled = false;
            document.getElementById('btn-step1-do').disabled = false;
            redraw();
        });

        document.getElementById('btn-step1-explain').addEventListener('click', () => {
            algorithmState.explanationSteps.push("Step 1. Find the leftmost point.");
            document.getElementById('explanation-text').textContent = algorithmState.explanationSteps.join('\n');
        });

        document.getElementById('btn-step1-do').addEventListener('click', () => {
            let leftmost = 0;
            for (let i = 1; i < points.length; i++) {
                if (points[i].x < points[leftmost].x) {
                    leftmost = i;
                }
            }
            algorithmState.leftmostPointIndex = leftmost;
            algorithmState.currentPointIndex = leftmost;
            algorithmState.hullPointsIndices.push(leftmost);
            algorithmState.step = 1;
            document.getElementById('btn-step1-explain').disabled = true;
            document.getElementById('btn-step1-do').disabled = true;
            document.getElementById('btn-step2-explain').disabled = false;
            document.getElementById('btn-step2-do').disabled = false;
            redraw();
        });

        document.getElementById('btn-step2-explain').addEventListener('click', () => {
            algorithmState.explanationSteps.push("Step 2. Find the point clockwise furthest to the right relative to the leftmost point.");
            document.getElementById('explanation-text').textContent = algorithmState.explanationSteps.join('\n');
        });

        document.getElementById('btn-step2-do').addEventListener('click', () => {
            const current = algorithmState.currentPointIndex;
            let next = algorithmState.fullHull[1];
            algorithmState.candidateLines = [];
            for (let i = 0; i < points.length; i++) {
                if (i !== current && !algorithmState.hullPointsIndices.includes(i)) {
                    algorithmState.candidateLines.push([current, i]);
                }
            }
            redraw();
            setTimeout(() => {
                algorithmState.candidateLines = [];
                algorithmState.hullPointsIndices.push(next);
                algorithmState.currentPointIndex = next;
                algorithmState.step = 2;
                document.getElementById('btn-step2-explain').disabled = true;
                document.getElementById('btn-step2-do').disabled = true;
                document.getElementById('btn-step3-explain').disabled = false;
                const actionsContainer = document.getElementById('step3-actions');
                actionsContainer.innerHTML = '<button id="btn-step3-do">do it</button>';
                for (let i = 2; i < algorithmState.fullHull.length; i++) {
                    const btn = document.createElement('button');
                    btn.textContent = 'repeat';
                    btn.id = `btn-step3-repeat-${i-2}`;
                    btn.addEventListener('click', () => handleStep3Repeat(i));
                    actionsContainer.appendChild(btn);
                }
                document.getElementById('btn-step3-do').addEventListener('click', () => handleStep3Repeat(2));
                redraw();
            }, 1000);
        });

        function handleStep3Repeat(stepIndex) {
            const current = algorithmState.currentPointIndex;
            const next = algorithmState.fullHull[stepIndex];
            algorithmState.candidateLines = [];
            for (let i = 0; i < points.length; i++) {
                if (i !== current && !algorithmState.hullPointsIndices.includes(i)) {
                    algorithmState.candidateLines.push([current, i]);
                }
            }
            redraw();
            setTimeout(() => {
                algorithmState.candidateLines = [];
                algorithmState.hullPointsIndices.push(next);
                algorithmState.currentPointIndex = next;
                redraw();
                const btn = document.getElementById(`btn-step3-repeat-${stepIndex-2}`);
                if (btn) btn.disabled = true;
                if (stepIndex === algorithmState.fullHull.length - 1) {
                    document.querySelectorAll('#step3-actions button').forEach(b => b.disabled = true);
                } else {
                    const nextBtn = document.getElementById(`btn-step3-repeat-${stepIndex-1}`);
                    if (nextBtn) nextBtn.disabled = false;
                }
            }, 1000);
        }

        document.getElementById('btn-step3-explain').addEventListener('click', () => {
            algorithmState.explanationSteps.push("Step 3. Repeat finding the point clockwise furthest to the right relative to the previously found point, until the leftmost point is reached again.");
            document.getElementById('explanation-text').textContent = algorithmState.explanationSteps.join('\n');
        });
    </script>
</body>
</html>
