            <div class="slider-container">
                <label for="slider-alpha-max">maximum fraction of respiring biomass <span class="slider-value" id="value-alpha-max">0.30</span></label>
                <input type="range" id="slider-alpha-max" min="0.1" max="1.0" step="0.01" value="0.3">
            </div>
            <div class="slider-container">
                <label for="slider-t-min">run time minimum (h) <span class="slider-value" id="value-t-min">0</span></label>
                <input type="range" id="slider-t-min" min="0" max="0" step="1" value="0" disabled>
            </div>
            <div class="slider-container">
                <label for="slider-t-max">run time maximum (h) <span class="slider-value" id="value-t-max">60</span></label>
                <input type="range" id="slider-t-max" min="10" max="100" step="1" value="60">
            </div>
            <div class="slider-container">
                <label for="slider-enzyme-control">intermediate enzyme control [mol/m³] <span class="slider-value" id="value-enzyme-control">1.00</span></label>
                <input type="range" id="slider-enzyme-control" min="0.1" max="5.0" step="0.01" value="1">
            </div>
            <div class="slider-container">
                <label for="slider-feed-rate">feed flow rate (m³/h) <span class="slider-value" id="value-feed-rate">0.20</span></label>
                <input type="range" id="slider-feed-rate" min="0.0" max="2.0" step="0.01" value="0.2">
            </div>
            <div class="slider-container">
                <label for="slider-substrate-feed-conc">substrate feed concentration [mol/m³] <span class="slider-value" id="value-substrate-feed-conc">100</span></label>
                <input type="range" id="slider-substrate-feed-conc" min="10" max="200" step="1" value="100">
            </div>
            <div class="slider-container">
                <label for="slider-feed-time">feed time (h) <span class="slider-value" id="value-feed-time">35</span></label>
                <input type="range" id="slider-feed-time" min="0" max="60" step="1" value="35">
            </div>
            <div class="slider-container">
                <label for="select-plot">plot</label>
                <select id="select-plot">
                    <option value="fed batch">fed batch</option>
                    <option value="α">α</option>
                </select>
            </div>
        </div>
        <div id="visualization">
            <div id="plot-top"></div>
            <div id="plot-bottom"></div>
        </div>
    </div>

    <script>
        // Initial conditions
        const initialConditions = {
            X: 0.1,    // Biomass
            Glu: 60,   // Glucose
            Eta: 0,    // Ethanol
            E0: 1.0,   // Enzyme E0
            E1: 0.0,   // Enzyme E1
            E2: 0.0,   // Enzyme E2
            V: 1.0     // Volume
        };

        // Hill equation parameters
        const n = 4;  // Hill coefficient

        // Simulation data storage
        let simulationData = null;

        // RK4 ODE solver
        function rk4(y, t, h, dydtFunc, params) {
            const k1 = dydtFunc(y, t, params);
            const k2 = dydtFunc(y.map((val, i) => val + 0.5 * h * k1[i]), t + 0.5 * h, params);
            const k3 = dydtFunc(y.map((val, i) => val + 0.5 * h * k2[i]), t + 0.5 * h, params);
            const k4 = dydtFunc(y.map((val, i) => val + h * k3[i]), t + h, params);
            return y.map((val, i) => val + (h / 6) * (k1[i] + 2 * k2[i] + 2 * k3[i] + k4[i]));
        }

        // ODE system
        function dydt(y, t, params) {
            const { X, Glu, Eta, E0, E1, E2, V } = y;
            const { alphaMax, KI, F, G_in, tFeed } = params;

            const alpha = alphaMax * Math.pow(KI, n) / (Math.pow(KI, n) + Math.pow(Glu, n));
            const mu = 0.5 * (1 + Math.tanh((Glu - 10) / 1));  // Simplified growth rate

            const dXdt = mu * X * (1 - X / 10) - (F / V) * X;
            const dGlu_dt = -mu * X / 0.5 - (F / V) * (Glu - G_in);
            const dEta_dt = 0.3 * mu * X - (F / V) * Eta;

            // Enzyme dynamics
            const dE0dt = -0.1 * E0 + 0.01 * (1 - alpha);
            const dE1dt = 0.1 * alpha * Glu - 0.05 * E1;
            const dE2dt = 0.05 * (1 - alpha) * Glu - 0.01 * E2;

            const dVdt = (t <= tFeed) ? F : 0;

            return [dXdt, dGlu_dt, dEta_dt, dE0dt, dE1dt, dE2dt, dVdt];
        }

        // Run simulation
        function runSimulation() {
            const params = {
                alphaMax: parseFloat(document.getElementById('slider-alpha-max').value),
                KI: parseFloat(document.getElementById('slider-enzyme-control').value),
                F: parseFloat(document.getElementById('slider-feed-rate').value),
                G_in: parseFloat(document.getElementById('slider-substrate-feed-conc').value),
                tFeed: parseFloat(document.getElementById('slider-feed-time').value),
                tMax: parseFloat(document.getElementById('slider-t-max').value)
            };

            const dt = 0.1;
            const steps = Math.ceil(params.tMax / dt);
            const tValues = Array.from({ length: steps + 1 }, (_, i) => i * dt);
            const yValues = [Object.values(initialConditions)];

            for (let i = 0; i < steps; i++) {
                const yNext = rk4(yValues[i], tValues[i], dt, dydt, params);
                yValues.push(yNext);
            }

            simulationData = {
                t: tValues,
                X: yValues.map(row => row[0]),
                Glu: yValues.map(row => row[1]),
                Eta: yValues.map(row => row[2]),
                E0: yValues.map(row => row[3]),
                E1: yValues.map(row => row[4]),
                E2: yValues.map(row => row[5]),
                V: yValues.map(row => row[6])
            };
        }

        // Update slider displays
        function setupSliders() {
            const sliders = [
                { id: 'slider-alpha-max', labelId: 'value-alpha-max', format: v => v.toFixed(2) },
                { id: 'slider-t-min', labelId: 'value-t-min', format: v => Math.round(v) },
                { id: 'slider-t-max', labelId: 'value-t-max', format: v => Math.round(v) },
                { id: 'slider-enzyme-control', labelId: 'value-enzyme-control', format: v => v.toFixed(2) },
                { id: 'slider-feed-rate', labelId: 'value-feed-rate', format: v => v.toFixed(2) },
                { id: 'slider-substrate-feed-conc', labelId: 'value-substrate-feed-conc', format: v => Math.round(v) },
                { id: 'slider-feed-time', labelId: 'value-feed-time', format: v => Math.round(v) }
            ];

            sliders.forEach(slider => {
                const input = document.getElementById(slider.id);
                const label = document.getElementById(slider.labelId);
                label.textContent = slider.format(input.value);

                input.addEventListener('input', () => {
                    label.textContent = slider.format(input.value);
                    if (slider.id === 'slider-t-max') {
                        const feedTimeSlider = document.getElementById('slider-feed-time');
                        feedTimeSlider.max = input.value;
                        if (feedTimeSlider.value > input.value) {
                            feedTimeSlider.value = input.value;
                            document.getElementById('value-feed-time').textContent = input.value;
                        }
                    }
                });
            });
        }

        // Draw plots
        function drawPlots() {
            const plotType = document.getElementById('select-plot').value;
            const t = simulationData.t;

            if (plotType === 'fed batch') {
                // Top plot: diauxic behavior
                const topData = [
                    { x: t, y: simulationData.X, name: 'X(t)', line: { color: 'blue' } },
                    { x: t, y: simulationData.Glu, name: 'Glu(t)', line: { color: 'red' } },
                    { x: t, y: simulationData.Eta, name: 'Eta(t)', line: { color: 'green' } }
                ];

                // Bottom plot: enzyme concentrations
                const bottomData = [
                    { x: t, y: simulationData.E2, name: 'E₂(t)', line: { color: 'black' } },
                    { x: t, y: simulationData.E1, name: 'E₁(t)', line: { color: 'magenta' } },
                    { x: t, y: simulationData.E0, name: 'E₀(t)', line: { color: 'orange' } }
                ];

                Plotly.react('plot-top', topData, {
                    title: 'diauxic behavior',
                    yaxis: { title: 'concentration [mol/m³]' },
                    xaxis: { title: 'time (h)' }
                });

                Plotly.react('plot-bottom', bottomData, {
                    title: 'enzyme concentration',
                    yaxis: { title: 'enzyme concentration [mol/m³]' },
                    xaxis: { title: 'time (h)' }
                });
            } else if (plotType === 'α') {
                // Calculate α values
                const alphaMax = parseFloat(document.getElementById('slider-alpha-max').value);
                const KI = parseFloat(document.getElementById('slider-enzyme-control').value);
                const alpha = simulationData.Glu.map(glu => alphaMax * Math.pow(KI, n) / (Math.pow(KI, n) + Math.pow(glu, n)));

                // Top plot: glucose concentration
                const topData = [
                    { x: t, y: simulationData.Glu, name: 'Glu(t)', line: { color: 'red' } }
                ];

                // Bottom plot: fraction of respiring biomass
                const bottomData = [
                    { x: t, y: alpha, name: 'α', line: { color: 'blue' } }
                ];

                Plotly.react('plot-top', topData, {
                    title: 'glucose concentration',
                    yaxis: { title: 'glucose concentration [mol/m³]' },
                    xaxis: { title: 'time (h)' }
                });

                Plotly.react('plot-bottom', bottomData, {
                    title: 'fraction of respiring biomass',
                    yaxis: { title: 'α' },
                    xaxis: { title: 'time (h)' }
                });
            }
        }

        // Event listeners
        function setupEventListeners() {
            const sliders = document.querySelectorAll('input[type="range"]');
            sliders.forEach(slider => {
                slider.addEventListener('input', () => {
                    runSimulation();
                    drawPlots();
                });
            });

            document.getElementById('select-plot').addEventListener('change', () => {
                drawPlots();
            });
        }

        // Initialize
        function init() {
            setupSliders();
            setupEventListeners();
            runSimulation();
            drawPlots();
        }

        // Start the application
        init();
    </script>
</body>
</html>
