<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Astronomical Visualization</title>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
            display: flex;
            height: 100vh;
        }
        #main-container {
            display: flex;
            width: 100%;
        }
        #controls-container {
            width: 300px;
            padding: 10px;
            background: #f0f0f0;
            overflow-y: auto;
        }
        #visualization-container {
            flex-grow: 1;
            position: relative;
        }
        .control-group {
            margin-bottom: 15px;
        }
        label {
            display: block;
            margin-bottom: 5px;
        }
        input[type="range"] {
            width: 100%;
        }
        .readout {
            margin: 5px 0;
        }
        #options-ground, #options-space {
            margin-top: 10px;
        }
        .hidden {
            display: none;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="controls-container">
            <div class="control-group">
                <label for="slider-date">date:</label>
                <input type="range" id="slider-date" min="1" max="365" step="1" value="79">
                <span id="label-date">March 20</span>
            </div>
            <div class="control-group">
                <label for="slider-latitude">latitude:</label>
                <input type="range" id="slider-latitude" min="-90" max="90" step="1" value="45">
                <span id="label-latitude">45°</span>
            </div>
            <div class="control-group">
                <label for="slider-time">solar time:</label>
                <input type="range" id="slider-time" min="0" max="24" step="0.1" value="12">
                <span id="label-time">12:00</span>
            </div>
            <div class="control-group">
                <label for="select-view">view from:</label>
                <select id="select-view">
                    <option value="ground">ground</option>
                    <option value="space">space</option>
                </select>
            </div>
            <div class="control-group">
                <div class="readout">day length: <span id="readout-day-length"></span></div>
                <div class="readout">night length: <span id="readout-night-length"></span></div>
                <div class="readout">sunrise time: <span id="readout-sunrise-time"></span></div>
                <div class="readout">sunrise direction: <span id="readout-sunrise-direction"></span></div>
                <div class="readout">sunset time: <span id="readout-sunset-time"></span></div>
                <div class="readout">sunset direction: <span id="readout-sunset-direction"></span></div>
            </div>
            <div id="options-ground">
                <input type="checkbox" id="check-sun-path" checked>
                <label for="check-sun-path">Sun path</label><br>
                <input type="checkbox" id="check-constellations-day">
                <label for="check-constellations-day">constellations (day)</label>
            </div>
            <div id="options-space" class="hidden">
                <input type="checkbox" id="check-stationary-earth">
                <label for="check-stationary-earth">stationary Earth</label><br>
                <input type="checkbox" id="check-constellations" checked>
                <label for="check-constellations">constellations</label><br>
                <input type="checkbox" id="check-day-night-division" checked>
                <label for="check-day-night-division">day/night division</label><br>
                <input type="checkbox" id="check-day-night-parallel" checked>
                <label for="check-day-night-parallel">day/night on parallel</label>
            </div>
        </div>
        <div id="visualization-container"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/controls/OrbitControls.min.js"></script>
    <script>
        // Scene setup
        let scene, camera, renderer, controls;
        let sun, earth, sunPathLine, constellations, dayNightPlane, latitudeLine, dayNightParallel;
        let groundPlane, celestialDome;
        let currentView = 'ground';
        let needsUpdate = true;

        // Initialize scene
        function init() {
            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(window.innerWidth - 300, window.innerHeight);
            document.getElementById('visualization-container').appendChild(renderer.domElement);

            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0x000000);

            // Camera
            camera = new THREE.PerspectiveCamera(45, (window.innerWidth - 300)/window.innerHeight, 0.1, 1000);
            camera.position.set(0, 1, 5);

            // Orbit controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);

            // Create objects
            createSun();
            createEarth();
            createConstellations();
            createGround();
            createCelestialDome();
            createSunPath();
            createDayNightDivision();
            createLatitudeParallel();

            // Initial setup
            updateView();
            updateReadouts();
            updateScene();

            // Animation loop
            animate();
        }

        // Create Sun
        function createSun() {
            const geometry = new THREE.SphereGeometry(0.5, 32, 32);
            const material = new THREE.MeshBasicMaterial({ color: 0xffff00 });
            sun = new THREE.Mesh(geometry, material);
            sun.position.set(0, 0, 20);
            scene.add(sun);
        }

        // Create Earth
        function createEarth() {
            const geometry = new THREE.SphereGeometry(1, 32, 32);
            const material = new THREE.MeshPhongMaterial({ color: 0x0000ff });
            earth = new THREE.Mesh(geometry, material);
            earth.rotation.z = THREE.Math.degToRad(23.45);
            scene.add(earth);
        }

        // Create constellations
        function createConstellations() {
            const points = [];
            // Simplified constellation data
            const constellationData = [
                [[0, 0, 1], [0, 0, -1]],
                [[0, 1, 0], [0, -1, 0]],
                [[1, 0, 0], [-1, 0, 0]]
            ];
            constellationData.forEach(line => {
                line.forEach(point => {
                    const v = new THREE.Vector3(...point).multiplyScalar(15);
                    points.push(v);
                });
            });
            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color: 0xffffff, opacity: 0.3, transparent: true });
            const lines = new THREE.LineSegments(geometry, material);
            lines.visible = document.getElementById('check-constellations').checked;
            scene.add(lines);
            constellations = lines;
        }

        // Create ground
        function createGround() {
            const geometry = new THREE.CylinderGeometry(10, 10, 1, 32);
            const material = new THREE.MeshPhongMaterial({ color: 0x00ff00 });
            groundPlane = new THREE.Mesh(geometry, material);
            groundPlane.rotation.x = -Math.PI / 2;
            scene.add(groundPlane);
        }

        // Create celestial dome
        function createCelestialDome() {
            const geometry = new THREE.SphereGeometry(15, 32, 32);
            const material = new THREE.MeshPhongMaterial({ color: 0x0000ff, opacity: 0.2, transparent: true, side: THREE.BackSide });
            celestialDome = new THREE.Mesh(geometry, material);
            scene.add(celestialDome);
        }

        // Create Sun path
        function createSunPath() {
            const geometry = new THREE.BufferGeometry();
            const material = new THREE.LineBasicMaterial({ color: 0xffff00 });
            sunPathLine = new THREE.Line(geometry, material);
            scene.add(sunPathLine);
        }

        // Create day/night division
        function createDayNightDivision() {
            const geometry = new THREE.PlaneGeometry(30, 30);
            const material = new THREE.MeshBasicMaterial({ color: 0x000000, opacity: 0.3, transparent: true });
            dayNightPlane = new THREE.Mesh(geometry, material);
            dayNightPlane.rotation.y = Math.PI / 2;
            scene.add(dayNightPlane);
        }

        // Create latitude parallel
        function createLatitudeParallel() {
            const geometry = new THREE.RingGeometry(1, 1.01, 32);
            const material = new THREE.MeshBasicMaterial({ color: 0xffff00 });
            latitudeLine = new THREE.Mesh(geometry, material);
            latitudeLine.rotation.x = Math.PI / 2;
            scene.add(latitudeLine);
        }

        // Update scene based on current state
        function updateScene() {
            // Update camera position based on view
            if (currentView === 'ground') {
                camera.position.set(0, 1, 5);
                camera.lookAt(0, 0, 0);
            } else {
                camera.position.set(0, 0, 30);
                camera.lookAt(0, 0, 0);
            }

            // Update Sun position based on time
            const time = parseFloat(document.getElementById('slider-time').value);
            const angle = THREE.Math.degToRad((time / 24) * 360);
            sun.position.x = 20 * Math.sin(angle);
            sun.position.z = 20 * Math.cos(angle);

            // Update Earth rotation
            if (!document.getElementById('check-stationary-earth').checked) {
                earth.rotation.y = angle;
            }

            // Update Sun path visibility
            sunPathLine.visible = document.getElementById('check-sun-path').checked;

            // Update constellations visibility
            constellations.visible = document.getElementById('check-constellations').checked;

            // Update day/night division visibility
            dayNightPlane.visible = document.getElementById('check-day-night-division').checked;

            // Update latitude parallel visibility
            latitudeLine.visible = document.getElementById('check-day-night-parallel').checked;

            needsUpdate = true;
        }

        // Update readouts
        function updateReadouts() {
            const date = parseInt(document.getElementById('slider-date').value);
            const latitude = parseInt(document.getElementById('slider-latitude').value);
            const declination = calculateDeclination(date);
            const dayLength = calculateDayLength(latitude, declination);

            document.getElementById('readout-day-length').textContent = `${dayLength.toFixed(2)} hours`;
            document.getElementById('readout-night-length').textContent = `${(24 - dayLength).toFixed(2)} hours`;
            document.getElementById('readout-sunrise-time').textContent = calculateSunriseTime(dayLength);
            document.getElementById('readout-sunset-time').textContent = calculateSunsetTime(dayLength);
            document.getElementById('readout-sunrise-direction').textContent = calculateSunDirection(declination, latitude, true);
            document.getElementById('readout-sunset-direction').textContent = calculateSunDirection(declination, latitude, false);
        }

        // Astronomical calculations
        function calculateDeclination(dayOfYear) {
            // Simplified approximation
            return 23.45 * Math.sin(2 * Math.PI * (dayOfYear - 80) / 365);
        }

        function calculateDayLength(latitude, declination) {
            const latRad = THREE.Math.degToRad(latitude);
            const decRad = THREE.Math.degToRad(declination);
            const cosH = -Math.tan(latRad) * Math.tan(decRad);
            const H = Math.acos(cosH) * 24 / Math.PI;
            return H;
        }

        function calculateSunriseTime(dayLength) {
            return `${(12 - dayLength/2).toFixed(2)}:00`;
        }

        function calculateSunsetTime(dayLength) {
            return `${(12 + dayLength/2).toFixed(2)}:00`;
        }

        function calculateSunDirection(declination, latitude, isSunrise) {
            // Simplified approximation
            const direction = isSunrise ? 90 - Math.abs(declination - latitude) : 90 + Math.abs(declination - latitude);
            return `${direction.toFixed(1)}°`;
        }

        // Update date label
        function updateDateLabel(value) {
            const date = new Date(2020, 0, parseInt(value));
            const options = { month: 'long', day: 'numeric' };
            return date.toLocaleDateString('en-US', options);
        }

        // Event listeners
        document.getElementById('slider-date').addEventListener('input', (e) => {
            document.getElementById('label-date').textContent = updateDateLabel(e.target.value);
            updateReadouts();
            updateScene();
        });

        document.getElementById('slider-latitude').addEventListener('input', (e) => {
            document.getElementById('label-latitude').textContent = `${e.target.value}°`;
            updateReadouts();
            updateScene();
        });

        document.getElementById('slider-time').addEventListener('input', (e) => {
            const time = parseFloat(e.target.value);
            document.getElementById('label-time').textContent = `${Math.floor(time)}:${(time % 1 * 60).toFixed(0).padStart(2, '0')}`;
            updateScene();
        });

        document.getElementById('select-view').addEventListener('change', (e) => {
            currentView = e.target.value;
            document.getElementById('options-ground').classList.toggle('hidden', currentView !== 'ground');
            document.getElementById('options-space').classList.toggle('hidden', currentView !== 'space');
            updateScene();
        });

        document.querySelectorAll('input[type="checkbox"]').forEach(checkbox => {
            checkbox.addEventListener('change', () => {
                updateScene();
            });
        });

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            if (needsUpdate) {
                renderer.render(scene, camera);
                needsUpdate = false;
            }
        }

        // Initialize on load
        window.onload = init;
    </script>
</body>
</html>
