            let cValues;

            if (t === 0) {
                cValues = xValues.map(x => (1.0 + 250 * x) * 1e-3);
            } else {
                cValues = xValues.map(x => {
                    let sum = 0;
                    for (let n = 0; n < N_TERMS; n++) {
                        const k_n = ((n + 0.5) * Math.PI) / L;
