    <script>
        let state = {
            funcKey: 'x',
            type: 'trapezoidal',
            a: 5,
            n: 3
        };

        // Function definitions and antiderivatives
        const functions = {
            x: {
                fn: x => x,
                antiderivative: a => a*a/2
            },
            x2: {
                fn: x => Math.pow(x-3, 2),
                antiderivative: a => (Math.pow(a-3, 3))/3 + 9*a - 27
            },
            x3: {
                fn: x => Math.pow(x-3, 3) + 27,
                antiderivative: a => (Math.pow(a-3, 4))/4 + 27*a
            },
            sqrt: {
                fn: x => Math.sqrt(x),
                antiderivative: a => (2/3)*Math.pow(a, 3/2)
            },
            sin: {
                fn: x => Math.sin(x) + 1,
                antiderivative: a => -Math.cos(a) + a + 1
            },
            exp: {
                fn: x => Math.exp(x)/3,
                antiderivative: a => Math.exp(a)/3
            }
        };

        // Initialize p5 canvas
        let canvas;
        function setup() {
            canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');
            noLoop();
            draw();
        }

        // Update state and redraw on button clicks
        document.querySelectorAll('.function-btn').forEach(btn => {
            btn.addEventListener('click', () => {
                document.querySelectorAll('.function-btn').forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
                state.funcKey = btn.dataset.function;
                redraw();
            });
        });

        document.querySelectorAll('.type-btn').forEach(btn => {
            btn.addEventListener('click', () => {
                document.querySelectorAll('.type-btn').forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
                state.type = btn.dataset.type;
                redraw();
            });
        });

        // Update state and redraw on slider changes
        document.getElementById('slider-a').addEventListener('input', (e) => {
            state.a = parseFloat(e.target.value);
            document.getElementById('value-a').textContent = state.a.toFixed(2);
            redraw();
        });

        document.getElementById('slider-n').addEventListener('input', (e) => {
            state.n = parseInt(e.target.value);
            document.getElementById('value-n').textContent = state.n;
            redraw();
        });

        function redraw() {
            draw();
        }

        function draw() {
            background(248, 248, 248);

            const func = functions[state.funcKey];
            const a = state.a;
            const n = state.n;
            const type = state.type;

            // Calculate y range
            let minY = 0, maxY = 0;
            for (let x = 0; x <= a; x += 0.01) {
                const y = func.fn(x);
                if (x === 0 || y < minY) minY = y;
                if (x === 0 || y > maxY) maxY = y;
            }
            minY = Math.max(0, minY - 0.5);
            maxY += 0.5;

            // Map logical coordinates to canvas pixels
            const xScale = width / (a + 1);
            const yScale = height / (maxY - minY);

            // Draw axes
            stroke('#cccccc');
            line(0, height - minY*yScale, width, height - minY*yScale); // x-axis
            line(0, 0, 0, height); // y-axis

            // Draw function curve
            noFill();
            stroke(0);
            beginShape();
            for (let x = 0; x <= a; x += 0.01) {
                const y = func.fn(x);
                vertex(x*xScale, height - y*yScale);
            }
            endShape();

            // Draw estimation shapes
            const deltaX = a / n;
            let estimatedArea = 0;

            for (let i = 0; i < n; i++) {
                const xLeft = i * deltaX;
                const xRight = (i + 1) * deltaX;
                const xMid = (xLeft + xRight) / 2;

                let height = 0;
                let xSample = xLeft;

                switch(type) {
                    case 'left': height = func.fn(xLeft); break;
                    case 'right': height = func.fn(xRight); break;
                    case 'midpoint': height = func.fn(xMid); break;
                    case 'trapezoidal': 
                        height = (func.fn(xLeft) + func.fn(xRight)) / 2;
                        break;
                    case 'overestimate':
                        height = Math.max(func.fn(xLeft), func.fn(xRight));
                        break;
                    case 'underestimate':
                        height = Math.min(func.fn(xLeft), func.fn(xRight));
                        break;
                }

                // Calculate true integral for this interval
                const trueArea = func.antiderivative(xRight) - func.antiderivative(xLeft);

                // Draw shape
                fill(trueArea > 0 && height*deltaX > trueArea ? 
                    'rgba(173, 216, 230, 0.7)' : 'rgba(144, 238, 144, 0.7)');
                stroke(50);

                if (type === 'trapezoidal') {
                    const yLeft = func.fn(xLeft);
                    const yRight = func.fn(xRight);
                    quad(
                        xLeft*xScale, height - yLeft*yScale,
                        xRight*xScale, height - yRight*yScale,
                        xRight*xScale, height,
                        xLeft*xScale, height
                    );
                } else {
                    rect(xLeft*xScale, height - height*yScale, deltaX*xScale, height*yScale);
                }

                estimatedArea += height * deltaX;
            }

            // Draw upper limit marker
            stroke(255, 0, 0);
            line(a*xScale, 0, a*xScale, height);

            // Calculate true integral
            const trueIntegral = func.antiderivative(a) - func.antiderivative(0);

            // Draw text overlay
            fill(0);
            noStroke();
            textSize(14);
            textAlign(CENTER);
            text(`estimated area: ${estimatedArea.toFixed(5)}`, width/2, 30);
            text(`integral: ${trueIntegral.toFixed(5)}`, width/2, 50);
        }
    </script>
</body>
</html>
