<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-group">
                <label for="slider-current">current (A)</label>
                <input type="range" id="slider-current" min="0" max="1" step="0.001" value="0.431">
                <span id="value-current">0.431</span>
            </div>
            <div class="slider-group">
                <label for="slider-earth-field">horizontal Earth field (T)</label>
                <input type="range" id="slider-earth-field" min="0" max="0.0001" step="0.0000001" value="0.0000194">
                <span id="value-earth-field">1.94e-5</span>
            </div>
            <div class="slider-group">
                <label for="slider-radius">radius (m)</label>
                <input type="range" id="slider-radius" min="0.1" max="1.0" step="0.01" value="0.5">
                <span id="value-radius">0.50</span>
            </div>
            <div class="slider-group">
                <label for="slider-turns">number of turns</label>
                <input type="range" id="slider-turns" min="1" max="100" step="1" value="25">
                <span id="value-turns">25</span>
            </div>
            <button id="btn-reset" title="Reset to initial values">↻</button>
        </div>
        <div id="viz-container">
            <canvas id="p5-canvas"></canvas>
        </div>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        // Elliptic integral functions using AGM method
        function ellipticK(m) {
            let a = 1.0;
            let b = Math.sqrt(1.0 - m);
            for (let i = 0; i < 10; i++) {
                [a, b] = [(a + b) / 2, Math.sqrt(a * b)];
            }
            return Math.PI / (2 * a);
        }

        function ellipticE(m) {
            let a = 1.0;
            let b = Math.sqrt(1.0 - m);
            let s = a * a;
            for (let i = 0; i < 10; i++) {
                [a, b] = [(a + b) / 2, Math.sqrt(a * b)];
                s += (a - b) * (a - b);
            }
            return (Math.PI / (4 * a)) * (1 + s);
        }

        // Initialize sliders and event listeners
        const sliders = {
            current: document.getElementById('slider-current'),
            earthField: document.getElementById('slider-earth-field'),
            radius: document.getElementById('slider-radius'),
            turns: document.getElementById('slider-turns')
        };

        const valueSpans = {
            current: document.getElementById('value-current'),
            earthField: document.getElementById('value-earth-field'),
            radius: document.getElementById('value-radius'),
            turns: document.getElementById('value-turns')
        };

        function updateValueDisplays() {
            valueSpans.current.textContent = parseFloat(sliders.current.value).toFixed(3);
            valueSpans.earthField.textContent = parseFloat(sliders.earthField.value).toExponential(3);
            valueSpans.radius.textContent = parseFloat(sliders.radius.value).toFixed(2);
            valueSpans.turns.textContent = parseInt(sliders.turns.value);
        }

        function resetSliders() {
            sliders.current.value = 0.431;
            sliders.earthField.value = 0.0000194;
            sliders.radius.value = 0.5;
            sliders.turns.value = 25;
            updateValueDisplays();
            redrawPlot();
        }

        function setupEventListeners() {
            sliders.current.addEventListener('input', () => {
                updateValueDisplays();
                redrawPlot();
            });
            sliders.earthField.addEventListener('input', () => {
                updateValueDisplays();
                redrawPlot();
            });
            sliders.radius.addEventListener('input', () => {
                updateValueDisplays();
                redrawPlot();
            });
            sliders.turns.addEventListener('input', () => {
                updateValueDisplays();
                redrawPlot();
            });
            document.getElementById('btn-reset').addEventListener('click', resetSliders);
        }

        // p5.js sketch
        function redrawPlot() {
            new p5(function(p) {
                p.setup = function() {
                    p.createCanvas(600, 600);
                    p.background(255);
                    p.colorMode(p.HSB, 360, 100, 100);
                    drawField(p);
                };

                function drawField(p) {
                    const I = parseFloat(sliders.current.value);
                    const BzEarth = parseFloat(sliders.earthField.value);
                    const R = parseFloat(sliders.radius.value);
                    const N = parseInt(sliders.turns.value);
                    const gridSize = 25;
                    const stepSize = 0.01;
                    const points = [];
                    let minMag = Infinity, maxMag = -Infinity;

                    // Generate grid and calculate magnitudes
                    for (let i = 0; i < gridSize; i++) {
                        for (let j = 0; j < gridSize; j++) {
                            const y = p.map(i, 0, gridSize-1, -0.5, 0.5);
                            const z = p.map(j, 0, gridSize-1, -0.5, 0.5);
                            const { By, Bz } = calculateBTotal(y, z, R, I, N, BzEarth);
                            const mag = Math.sqrt(By*By + Bz*Bz);
                            points.push({ y, z, By, Bz, mag });
                            minMag = Math.min(minMag, mag);
                            maxMag = Math.max(maxMag, mag);
                        }
                    }

                    // Draw streamlines
                    p.strokeCap(p.ROUND);
                    for (let point of points) {
                        drawStreamline(p, point.y, point.z, R, I, N, BzEarth, stepSize, minMag, maxMag, true);
                        drawStreamline(p, point.y, point.z, R, I, N, BzEarth, stepSize, minMag, maxMag, false);
                    }

                    // Draw axes
                    drawAxes(p);
                }

                function calculateBTotal(y, z, R, I, N, BzEarth) {
                    const coil1 = calculateB(y, z, R, I, N, -R/2);
                    const coil2 = calculateB(y, z, R, I, N, R/2);
                    return {
                        By: coil1.By + coil2.By,
                        Bz: coil1.Bz + coil2.Bz + BzEarth
                    };
                }

                function calculateB(y, z, R, I, N, z_offset) {
                    const mu0 = 4e-7 * Math.PI;
                    const zp = z - z_offset;
                    const m = (4 * R * y) / ((R + y)**2 + zp**2);
                    const common1 = (mu0 * I * N) / (2 * Math.PI);
                    const common2 = 1 / Math.sqrt((R + y)**2 + zp**2);
                    let By = 0, Bz = 0;

                    if (Math.abs(y) > 1e-6) {
                        const K = ellipticK(m);
                        const E = ellipticE(m);
                        const numerator = (R*R + y*y + zp*zp);
                        const denominator = (R - y)**2 + zp*zp;
                        const term1 = (numerator / denominator) * E - K;
                        By = common1 * (zp / y) * common2 * term1;
                    }

                    const term2 = K - ((R*R - y*y - zp*zp) / ((R - y)**2 + zp*zp)) * E;
                    Bz = common1 * common2 * term2;
                    return { By, Bz };
                }

                function drawStreamline(p, y0, z0, R, I, N, BzEarth, stepSize, minMag, maxMag, forward) {
                    let y = y0, z = z0;
                    const direction = forward ? 1 : -1;

                    for (let step = 0; step < 100; step++) {
                        const { By, Bz } = calculateBTotal(y, z, R, I, N, BzEarth);
                        const mag = Math.sqrt(By*By + Bz*Bz);
                        if (mag === 0) break;

                        const dy = By / mag;
                        const dz = Bz / mag;
                        const x1 = p.map(y, -0.5, 0.5, 0, p.width);
                        const y1 = p.map(z, -0.5, 0.5, p.height, 0);

                        y += direction * stepSize * dy;
                        z += direction * stepSize * dz;
                        const x2 = p.map(y, -0.5, 0.5, 0, p.width);
                        const y2 = p.map(z, -0.5, 0.5, p.height, 0);

                        const hue = p.map(mag, minMag, maxMag, 270, 60);
                        p.stroke(hue, 100, 100, 50);
                        p.strokeWeight(1);
                        p.line(x1, y1, x2, y2);

                        if (step % 10 === 0) {
                            const angle = Math.atan2(dz, dy);
                            p.stroke(0);
                            p.strokeWeight(1);
                            p.line(x2, y2, x2 + 5 * Math.cos(angle + Math.PI/6), y2 + 5 * Math.sin(angle + Math.PI/6));
                            p.line(x2, y2, x2 + 5 * Math.cos(angle - Math.PI/6), y2 + 5 * Math.sin(angle - Math.PI/6));
                        }

                        if (y < -0.5 || y > 0.5 || z < -0.5 || z > 0.5) break;
                    }
                }

                function drawAxes(p) {
                    p.stroke(255, 0, 0);
                    p.strokeWeight(2);
                    const yZero = p.map(0, -0.5, 0.5, 0, p.width);
                    const zZero = p.map(0, -0.5, 0.5, p.height, 0);

                    // Y axis
                    p.line(0, p.map(-0.5, -0.5, 0.5, p.height, 0), p.width, p.map(0.5, -0.5, 0.5, p.height, 0));
                    // Z axis
                    p.line(p.map(-0.5, -0.5, 0.5, 0, p.width), p.height, p.map(0.5, -0.5, 0.5, 0, p.width), 0);

                    // Ticks and labels
                    p.strokeWeight(1);
                    for (let val = -0.5; val <= 0.5; val += 0.2) {
                        // Y ticks
                        const x = p.map(val, -0.5, 0.5, 0, p.width);
                        p.line(x, zZero - 5, x, zZero + 5);
                        p.fill(255, 0, 0);
                        p.noStroke();
                        p.textSize(12);
                        p.textAlign(p.CENTER, p.TOP);
                        p.text(val.toFixed(1), x, zZero + 10);

                        // Z ticks
                        const y = p.map(val, -0.5, 0.5, p.height, 0);
                        p.line(yZero - 5, y, yZero + 5, y);
                        p.textAlign(p.RIGHT, p.CENTER);
                        p.text(val.toFixed(1), yZero - 10, y);
                    }

                    // Labels
                    p.textAlign(p.CENTER, p.CENTER);
                    p.text('Y', p.width/2, zZero - 20);
                    p.text('Z', yZero + 20, p.height/2);
                }
            });
        }

        // Initialize
        setupEventListeners();
        updateValueDisplays();
        redrawPlot();
    </script>
</body>
</html>
