      display: flex;
      flex-direction: column;
      align-items: center;
    }
    .canvas-container p {
      margin: 0 0 5px 0;
      font-weight: bold;
    }
  </style>
</head>
<body>
  <header>
    <h1>Applying Graphics Filters Using Convolution</h1>
    <p>This demo shows how convolution filters can be applied to pixelated images of letters to create effects like shadows and blurs.</p>
  </header>
  <div id="control-panel">
    <div class="control-group">
      <label for="slider-convolver">convolver</label>
      <button type="button" id="convolver-minus-btn">-</button>
      <input type="range" id="slider-convolver" min="1" max="5" value="2" step="1">
      <button type="button" id="convolver-plus-btn">+</button>
    </div>
    <div class="control-group">
      <label for="slider-image-size">image size</label>
      <button type="button" id="image-size-minus-btn">-</button>
      <input type="range" id="slider-image-size" min="10" max="40" value="24" step="1">
      <button type="button" id="image-size-plus-btn">+</button>
    </div>
    <div class="control-group">
      <label for="input-letter">pick a letter:</label>
      <input type="text" id="input-letter" maxlength="1" value="a">
    </div>
    <div class="control-group radio-group">
      <label>filter</label>
      <input type="radio" name="filter" id="radio-shadow" value="shadow" checked>
      <label for="radio-shadow">shadow</label>
      <input type="radio" name="filter" id="radio-blur" value="blur">
      <label for="radio-blur">blur</label>
    </div>
  </div>
  <div id="visualization-area">
    <div class="canvas-container">
      <p>before</p>
      <div id="canvas-before-container"></div>
    </div>
    <div class="canvas-container">
      <p>after</p>
      <div id="canvas-after-container"></div>
    </div>
  </div>

  <script>
    // Global variables for image data
    let beforeGrid = [];
    let afterGrid = [];
    let currentLetter = 'a';
    let convolverValue = 2;
    let imageSize = 24;
    let selectedFilter = 'shadow';

    // Before canvas sketch
    let beforeSketch = new p5(function(p) {
      p.setup = function() {
        p.createCanvas(200, 200);
        p.noLoop();
      };
      p.draw = function() {
        p.background(255);
        drawGrid(beforeGrid, p, false);
      };
    }, 'canvas-before-container');

    // After canvas sketch
    let afterSketch = new p5(function(p) {
      p.setup = function() {
        p.createCanvas(200, 200);
        p.noLoop();
      };
      p.draw = function() {
        p.background(255);
        drawGrid(afterGrid, p, true);
      };
    }, 'canvas-after-container');

    function updateVisualization() {
      // Read control values
      convolverValue = parseInt(document.getElementById('slider-convolver').value);
      imageSize = parseInt(document.getElementById('slider-image-size').value);
      currentLetter = document.getElementById('input-letter').value.toLowerCase() || 'a';
      selectedFilter = document.querySelector('input[name="filter"]:checked').value;

      // Generate before grid
      beforeGrid = generateBeforeGrid(imageSize, currentLetter);

      // Generate kernel
      const kernel = generateKernel(convolverValue, selectedFilter);

      // Apply convolution
      afterGrid = applyConvolution(beforeGrid, kernel);

      // Redraw canvases
      beforeSketch.redraw();
      afterSketch.redraw();
    }

    function generateBeforeGrid(N, letter) {
      const grid = new Array(N);
      for (let i = 0; i < N; i++) {
        grid[i] = new Array(N).fill(0);
      }

      const tempCanvas = document.createElement('canvas');
      tempCanvas.width = N;
      tempCanvas.height = N;
      const ctx = tempCanvas.getContext('2d');
      ctx.fillStyle = 'black';
      ctx.font = `${N * 0.8}px sans-serif`;
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';
      ctx.fillText(letter, N / 2, N / 2);

      const imageData = ctx.getImageData(0, 0, N, N).data;
      for (let i = 0; i < N; i++) {
        for (let j = 0; j < N; j++) {
          const index = (i * N + j) * 4;
          const alpha = imageData[index + 3];
          grid[i][j] = alpha > 128 ? 1 : 0;
        }
      }

      return grid;
    }

    function generateKernel(k, filterType) {
      const D = 2 * k + 1;
      const kernel = new Array(D).fill(0).map(() => new Array(D).fill(0));

      if (filterType === 'blur') {
        for (let i = 0; i < D; i++) {
          for (let j = 0; j < D; j++) {
            kernel[i][j] = 1.0 / (D * D);
          }
        }
      } else if (filterType === 'shadow') {
        const offset = 1;
        for (let i = offset; i < offset + k; i++) {
          for (let j = offset; j < offset + k; j++) {
            kernel[i][j] = 1.0 / (k * k);
          }
        }
      }

      return kernel;
    }

    function applyConvolution(grid, kernel) {
      const N = grid.length;
      const D = kernel.length;
      const halfD = Math.floor(D / 2);
      const output = new Array(N).fill(0).map(() => new Array(N).fill(0));

      for (let i = 0; i < N; i++) {
        for (let j = 0; j < N; j++) {
          let sum = 0;
          for (let ki = 0; ki < D; ki++) {
            for (let kj = 0; kj < D; kj++) {
              const x = i + ki - halfD;
              const y = j + kj - halfD;
              const kernelValue = kernel[ki][kj];
              const pixelValue = (x >= 0 && x < N && y >= 0 && y < N) ? grid[x][y] : 0;
              sum += kernelValue * pixelValue;
            }
          }
          output[i][j] = sum;
        }
      }

      return output;
    }

    function drawGrid(grid, p, isAfter) {
      if (!grid || grid.length === 0) return;
      const N = grid.length;
      const cellSize = 200 / N;

      p.noStroke();
      for (let i = 0; i < N; i++) {
        for (let j = 0; j < N; j++) {
          let value;
          if (isAfter) {
            value = grid[i][j];
            const gray = 255 * (1 - value);
            p.fill(gray);
          } else {
            value = grid[i][j];
            p.fill(value === 1 ? 0 : 255);
          }
          p.rect(j * cellSize, i * cellSize, cellSize, cellSize);
        }
      }
    }

    // Event Listeners
    document.getElementById('slider-convolver').addEventListener('input', updateVisualization);
    document.getElementById('slider-image-size').addEventListener('input', updateVisualization);
    document.getElementById('input-letter').addEventListener('input', updateVisualization);
    document.querySelectorAll('input[name="filter"]').forEach(radio => {
      radio.addEventListener('change', updateVisualization);
    });

    // Plus/minus buttons for sliders
    document.getElementById('convolver-plus-btn').addEventListener('click', () => {
      const slider = document.getElementById('slider-convolver');
      slider.value = Math.min(parseInt(slider.value) + 1, slider.max);
      slider.dispatchEvent(new Event('input'));
    });

    document.getElementById('convolver-minus-btn').addEventListener('click', () => {
      const slider = document.getElementById('slider-convolver');
      slider.value = Math.max(parseInt(slider.value) - 1, slider.min);
      slider.dispatchEvent(new Event('input'));
    });

    document.getElementById('image-size-plus-btn').addEventListener('click', () => {
      const slider = document.getElementById('slider-image-size');
      slider.value = Math.min(parseInt(slider.value) + 1, slider.max);
      slider.dispatchEvent(new Event('input'));
    });

    document.getElementById('image-size-minus-btn').addEventListener('click', () => {
      const slider = document.getElementById('slider-image-size');
      slider.value = Math.max(parseInt(slider.value) - 1, slider.min);
      slider.dispatchEvent(new Event('input'));
    });

    // Arrow key navigation for letter input
    document.getElementById('input-letter').addEventListener('keydown', function(e) {
      if (e.key === 'ArrowUp' || e.key === 'ArrowDown') {
        e.preventDefault();
        let current = this.value.charCodeAt(0);
        if (e.key === 'ArrowUp') {
          current = current === 122 ? 97 : current + 1;
        } else {
          current = current === 97 ? 122 : current - 1;
        }
        this.value = String.fromCharCode(current);
        this.dispatchEvent(new Event('input'));
      }
    });

    // Initial render
    updateVisualization();
  </script>
</body>
</html>
