                </label>
                <label>jump to atom y
                    <input type="range" id="slider-residue-y" min="1" max="400" step="1" value="52">
                    <span id="residue-y-value">52</span>
                </label>
                <label>RMSD
                    <input type="range" id="slider-rmsd" min="0" max="15" step="0.1" value="4.9">
                    <span id="rmsd-value">4.9</span>
                </label>
                <label>plot size
                    <input type="range" id="slider-plot-size" min="200" max="800" step="1" value="400">
                    <span id="plot-size-value">400</span>
                </label>
            </div>
        </div>

        <div id="visualization-area">
            <button id="btn-reset">+</button>
            <p id="info-display"></p>
            <div id="canvas-container"></div>
        </div>
    </div>

    <!-- External Libraries -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/controls/OrbitControls.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

    <script>
        // Global Variables
        let proteinData;
        let currentView = 'crystallography';
        let residueA = 1;
        let residueX = 50;
        let residueY = 52;
        let rmsdThreshold = 4.9;
        let plotSize = 400;
        let grayLevel = 0.5;
        let showLateralChains = true;
        let aminoAcids = ['A', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'K', 'L', 'M', 'N', 'P', 'Q', 'R', 'S', 'T', 'V', 'W', 'Y'];
        let threeScene, threeCamera, threeRenderer, threeControls, threeMeshes = [];
        let p5Instance;

        // Initialize
        function setup() {
            // Generate protein data
            proteinData = generateProteinData(400);

            // Populate residue selector
            const select = document.getElementById('select-residue');
            aminoAcids.forEach(aa => {
                const option = document.createElement('option');
                option.value = aa;
                option.textContent = aa;
                select.appendChild(option);
            });

            // Initialize event listeners
            document.getElementById('btn-crystallography').addEventListener('click', () => switchView('crystallography'));
            document.getElementById('btn-contact-map').addEventListener('click', () => switchView('contact-map'));
            document.getElementById('checkbox-lateral-chains').addEventListener('change', (e) => {
                showLateralChains = e.target.checked;
                update3DView();
            });
            document.getElementById('slider-gray-level').addEventListener('input', (e) => {
                grayLevel = parseFloat(e.target.value);
                document.getElementById('gray-level-value').textContent = grayLevel.toFixed(2);
                update3DView();
            });
            document.getElementById('slider-browse-sequence').addEventListener('input', (e) => {
                residueA = parseInt(e.target.value);
                document.getElementById('browse-sequence-value').textContent = residueA;
                updateInfoDisplay();
                update3DView();
            });
            document.getElementById('select-residue').addEventListener('change', () => {
                const selectedAA = document.getElementById('select-residue').value;
                const currentIndex = residueA;
                let newIndex = -1;

                // Find next occurrence of selected AA
                for (let i = currentIndex + 1; i <= 400; i++) {
                    if (proteinData[i-1].resName === selectedAA) {
                        newIndex = i;
                        break;
                    }
                }

                // If not found, search from start
                if (newIndex === -1) {
                    for (let i = 1; i <= currentIndex; i++) {
                        if (proteinData[i-1].resName === selectedAA) {
                            newIndex = i;
                            break;
                        }
                    }
                }

                if (newIndex !== -1) {
                    residueA = newIndex;
                    document.getElementById('slider-browse-sequence').value = residueA;
                    document.getElementById('browse-sequence-value').textContent = residueA;
                    updateInfoDisplay();
                    update3DView();
                }
            });
            document.getElementById('slider-residue-x').addEventListener('input', (e) => {
                residueX = parseInt(e.target.value);
                document.getElementById('residue-x-value').textContent = residueX;
                updateInfoDisplay();
                updateView();
            });
            document.getElementById('slider-residue-y').addEventListener('input', (e) => {
                residueY = parseInt(e.target.value);
                document.getElementById('residue-y-value').textContent = residueY;
                updateInfoDisplay();
                updateView();
            });
            document.getElementById('slider-rmsd').addEventListener('input', (e) => {
                rmsdThreshold = parseFloat(e.target.value);
                document.getElementById('rmsd-value').textContent = rmsdThreshold.toFixed(1);
                if (currentView === 'contact-map') update2DView();
            });
            document.getElementById('slider-plot-size').addEventListener('input', (e) => {
                plotSize = parseInt(e.target.value);
                document.getElementById('plot-size-value').textContent = plotSize;
                resizeCanvas();
            });
            document.getElementById('btn-reset').addEventListener('click', resetCamera);

            // Initialize views
            init3DView();
            init2DView();
            switchView('crystallography');
            updateInfoDisplay();
        }

        // Data Generation
        function generateProteinData(numResidues) {
            const residues = [];
            const aminoAcids = ['ALA', 'CYS', 'ASP', 'GLU', 'PHE', 'GLY', 'HIS', 'ILE', 'LYS', 'LEU', 
                              'MET', 'ASN', 'PRO', 'GLN', 'ARG', 'SER', 'THR', 'VAL', 'TRP', 'TYR'];

            let pos = {x: 0, y: 0, z: 0};
            const atoms = ['CA', 'N', 'C', 'O', 'CB'];

            for (let i = 0; i < numResidues; i++) {
                // Random walk for CA positions
                const step = 2;
                pos = {
                    x: pos.x + (Math.random() - 0.5) * step,
                    y: pos.y + (Math.random() - 0.5) * step,
                    z: pos.z + (Math.random() - 0.5) * step
                };

                const resName = aminoAcids[Math.floor(Math.random() * aminoAcids.length)];
                const residue = {
                    resName: resName,
                    resSeq: i+1,
                    atoms: []
                };

                // Create atoms relative to CA
                atoms.forEach(atom => {
                    let offset = {x:0, y:0, z:0};
                    if (atom === 'N') offset = {x:-1, y:1, z:0};
                    if (atom === 'C') offset = {x:1, y:1, z:0};
                    if (atom === 'O') offset = {x:1, y:2, z:0};
                    if (atom === 'CB') offset = {x:0, y:0, z:1};

                    residue.atoms.push({
                        atomName: atom,
                        x: pos.x + offset.x,
                        y: pos.y + offset.y,
                        z: pos.z + offset.z
                    });
                });

                residues.push(residue);
            }
            return residues;
        }

        // 3D View Functions
        function init3DView() {
            // Setup scene
            threeScene = new THREE.Scene();
            threeScene.background = new THREE.Color(0xffffff);

            // Camera
            threeCamera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            threeCamera.position.set(0, 0, 20);

            // Renderer
            threeRenderer = new THREE.WebGLRenderer({ canvas: document.createElement('canvas') });
            threeRenderer.setSize(plotSize, plotSize);
            document.getElementById('canvas-container').appendChild(threeRenderer.domElement);

            // Controls
            threeControls = new THREE.OrbitControls(threeCamera, threeRenderer.domElement);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x404040);
            threeScene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
            directionalLight.position.set(5, 5, 5).normalize();
            threeScene.add(directionalLight);

            // Bounding box
            const box = new THREE.BoxHelper(threeScene, 0x888888);
            threeScene.add(box);

            // Initial render
            update3DView();

            // Animation loop
            function animate() {
                requestAnimationFrame(animate);
                threeControls.update();
                threeRenderer.render(threeScene, threeCamera);
            }
            animate();
        }

        function update3DView() {
            if (!threeScene) return;

            // Clear existing meshes
            threeMeshes.forEach(mesh => threeScene.remove(mesh));
            threeMeshes = [];

            // Get color for main chain
            const mainColor = new THREE.Color(0x0000ff).lerp(new THREE.Color(0x00ffff), grayLevel);

            // Create atoms
            proteinData.forEach((res, index) => {
                const isResA = index + 1 === residueA;
                const isResX = index + 1 === residueX;
                const isResY = index + 1 === residueY;

                res.atoms.forEach(atom => {
                    if (!showLateralChains && atom.atomName !== 'CA') return;

                    const color = isResA ? 0xffff00 : 
                                 (isResX || isResY) ? 0xff0000 : 
                                 mainColor.getHex();

                    const geometry = new THREE.SphereGeometry(0.3, 16, 16);
                    const material = new THREE.MeshLambertMaterial({ color: color });
                    const sphere = new THREE.Mesh(geometry, material);

                    sphere.position.set(atom.x, atom.y, atom.z);
                    threeScene.add(sphere);
                    threeMeshes.push(sphere);
                });
            });
        }

        // 2D View Functions
        function init2DView() {
            p5Instance = new p5((p) => {
                p.setup = () => {
                    const canvas = p.createCanvas(plotSize, plotSize);
                    canvas.parent('canvas-container');
                    p.background(255);
                };

                p.draw = () => {
                    if (currentView !== 'contact-map') return;
                    update2DView();
                };
            });
        }

        function update2DView() {
            if (!p5Instance) return;
            p5Instance.draw = () => {
                p5Instance.background(255);
                const scale = p5Instance.width / 400;

                // Draw axes
                p5Instance.stroke(150);
                p5Instance.strokeWeight(1);

                // Vertical lines
                for (let i = 0; i <= 400; i += 100) {
                    const x = i * scale;
                    p5Instance.line(x, 0, x, p5Instance.height);
                    p5Instance.text(i, x + 5, 15);
                }

                // Horizontal lines
                for (let i = 0; i <= 400; i += 100) {
                    const y = i * scale;
                    p5Instance.line(0, y, p5Instance.width, y);
                    p5Instance.text(i, 10, y - 5);
                }

                // Draw contact points
                p5Instance.stroke(0);
                p5Instance.strokeWeight(1);

                for (let i = 0; i < 400; i++) {
                    for (let j = i+1; j < 400; j++) {
                        const ca1 = proteinData[i].atoms.find(a => a.atomName === 'CA');
                        const ca2 = proteinData[j].atoms.find(a => a.atomName === 'CA');
                        const dx = ca1.x - ca2.x;
                        const dy = ca1.y - ca2.y;
                        const dz = ca1.z - ca2.z;
                        const dist = Math.sqrt(dx*dx + dy*dy + dz*dz);

                        if (dist <= rmsdThreshold) {
                            const x = (i + 1) * scale;
                            const y = (j + 1) * scale;
                            p5Instance.point(x, y);
                        }
                    }
                }

                // Draw residue lines
                p5Instance.stroke(255, 0, 0);
                p5Instance.strokeWeight(2);

                // Vertical line for residue X
                const xLine = residueX * scale;
                p5Instance.line(xLine, 0, xLine, p5Instance.height);

                // Horizontal line for residue Y
                const yLine = residueY * scale;
                p5Instance.line(0, yLine, p5Instance.width, yLine);
            };
            p5Instance.draw();
        }

        // View Switching
        function switchView(view) {
            currentView = view;
            document.getElementById('btn-crystallography').classList.toggle('active', view === 'crystallography');
            document.getElementById('btn-crystallography').classList.toggle('inactive', view !== 'crystallography');
            document.getElementById('btn-contact-map').classList.toggle('active', view === 'contact-map');
            document.getElementById('btn-contact-map').classList.toggle('inactive', view !== 'contact-map');

            // Show/hide relevant controls
            document.getElementById('crystallography-controls').style.display = 
                view === 'crystallography' ? 'block' : 'none';

            // Update view
            updateView();
        }

        function updateView() {
            if (currentView === 'crystallography') {
                update3DView();
                document.getElementById('canvas-container').style.display = 'block';
                document.getElementById('canvas-container').nextElementSibling.style.display = 'none';
            } else {
                update2DView();
                document.getElementById('canvas-container').style.display = 'none';
                document.getElementById('canvas-container').nextElementSibling.style.display = 'block';
            }
        }

        function updateInfoDisplay() {
            const resA = proteinData[residueA - 1];
            const resX = proteinData[residueX - 1];
            const resY = proteinData[residueY - 1];

            const info = `Res A: ${resA.resName}${residueA} | Res X: ${resX.resName}${residueX} | Res Y: ${resY.resName}${residueY}`;
            document.getElementById('info-display').textContent = info;
        }

        function resizeCanvas() {
            if (currentView === 'crystallography') {
                threeRenderer.setSize(plotSize, plotSize);
                threeCamera.aspect = plotSize / plotSize;
                threeCamera.updateProjectionMatrix();
            } else if (p5Instance) {
                p5Instance.resizeCanvas(plotSize, plotSize);
                update2DView();
            }
        }

        function resetCamera() {
            if (threeCamera && threeControls) {
                threeCamera.position.set(0, 0, 20);
                threeCamera.lookAt(0, 0, 0);
                threeControls.update();
            }
        }

        // Initialize on load
        window.onload = setup;
    </script>
</body>
</html>
