      {name: 'Huron', lon: -83.0, lat: 44.0, found: false},
      {name: 'Issyk-Kul', lon: 76.0, lat: 42.0, found: false},
      {name: 'Michigan', lon: -87.0, lat: 44.0, found: false},
      {name: 'Ladoga', lon: 30.0, lat: 61.0, found: false},
