    function initTable() {
      const table = document.getElementById('table-quiz');
      table.innerHTML = '';
      const thead = document.createElement('thead');
      const tbody = document.createElement('tbody');
      const headerRow = document.createElement('tr');
      currentQuiz.forEach(item => {
        const th = document.createElement('th');
        th.textContent = item.name;
        headerRow.appendChild(th);
      });
      thead.appendChild(headerRow);
      table.appendChild(thead);
      const statusRow = document.createElement('tr');
      currentQuiz.forEach(item => {
        const td = document.createElement('td');
        td.textContent = 'False';
        td.dataset.name = item.name;
        statusRow.appendChild(td);
      });
      tbody.appendChild(statusRow);
      table.appendChild(tbody);
    }

    function updateTableCell(name, found) {
      const td = document.querySelector(`#table-quiz td[data-name="${name}"]`);
      if (td) {
        td.textContent = found ? 'True' : 'False';
        td.classList.toggle('found', found);
      }
    }

    function resetQuiz() {
      currentQuiz.forEach(item => {
        item.found = false;
        updateTableCell(item.name, false);
      });
    }

    myp5 = new p5(function(sketch) {
      let mapImage;

      sketch.preload = function() {
        mapImage = sketch.loadImage('https://upload.wikimedia.org/wikipedia/commons/c/c5/Blue_Marble_Next_Generation_%2B_topography_%2B_bathymetry.jpg');
      };

      sketch.setup = function() {
        const canvasDiv = document.getElementById('canvas-container');
        canvasWidth = canvasDiv.clientWidth;
        canvasHeight = canvasDiv.clientHeight;
        const canvas = sketch.createCanvas(canvasWidth, canvasHeight);
        canvas.parent('canvas-container');
        sketch.frameRate(30);
      };

      sketch.draw = function() {
        if (!mapImage) return;

        sketch.image(mapImage, 0, 0, sketch.width, sketch.height);

        sketch.fill(173, 216, 230, 150);
        sketch.noStroke();
        sketch.circle(sketch.mouseX, sketch.mouseY, 30);

        if (showLocations) {
          currentQuiz.forEach(item => {
            if (!item.found) {
              const pos = mapToPixels(item.lon, item.lat);
              sketch.fill('red');
              sketch.noStroke();
              sketch.circle(pos.x, pos.y, 10);
            }
          });
        }

        if (showHints) {
          const unfound = currentQuiz.filter(item => !item.found);
          const hints = sketch.shuffle(unfound).slice(0, 3);
          hints.forEach(item => {
            const pos = mapToPixels(item.lon, item.lat);
            sketch.fill('white');
            sketch.textSize(12);
            sketch.text(item.name, pos.x + 10, pos.y + 10);
          });
        }
      };

      sketch.mouseMoved = function() {
        const geo = mapToGeo(sketch.mouseX, sketch.mouseY);
        mouseGeo = geo;
        document.getElementById('p-position').textContent = `Current position {${Math.round(geo.lon)}, ${Math.round(geo.lat)}}`;
      };

      sketch.mouseClicked = function() {
        const clickPos = { x: sketch.mouseX, y: sketch.mouseY };
        currentQuiz.forEach(item => {
          if (!item.found) {
            const pos = mapToPixels(item.lon, item.lat);
            const d = sketch.dist(clickPos.x, clickPos.y, pos.x, pos.y);
            if (d < 15) {
              item.found = true;
              updateTableCell(item.name, true);
              return false;
            }
          }
        });
      };
    });

    function mapToPixels(lon, lat) {
      const x = myp5.map(lon, -180, 180, 0, canvasWidth);
      const y = myp5.map(lat, 90, -90, 0, canvasHeight);
      return { x, y };
    }

    function mapToGeo(x, y) {
      const lon = myp5.map(x, 0, canvasWidth, -180, 180);
      const lat = myp5.map(y, 0, canvasHeight, 90, -90);
      return { lon: Math.round(lon), lat: Math.round(lat) };
    }

    document.getElementById('radio-rivers').addEventListener('change', function() {
      if (this.checked) {
        currentQuiz = riversData;
        initTable();
        resetQuiz();
        myp5.redraw();
      }
    });

    document.getElementById('radio-lakes').addEventListener('change', function() {
      if (this.checked) {
        currentQuiz = lakesData;
        initTable();
        resetQuiz();
        myp5.redraw();
      }
    });

    document.getElementById('btn-reset').addEventListener('click', function() {
      resetQuiz();
      myp5.redraw();
    });

    document.getElementById('check-show-locations').addEventListener('change', function() {
      showLocations = this.checked;
      myp5.redraw();
    });

    document.getElementById('check-show-some').addEventListener('change', function() {
      showHints = this.checked;
      myp5.redraw();
    });

    window.addEventListener('load', function() {
      initTable();
      resetQuiz();
    });
  </script>
</body>
</html>
