            border: none;
            border-radius: 3px;
        }
        #viz-container {
            flex: 1;
            display: flex;
            flex-direction: column;
            padding: 20px;
            border: 1px solid #ccc;
        }
        #header-section {
            margin-bottom: 10px;
        }
        #main-content {
            display: flex;
            flex: 1;
        }
        #info-panel {
            width: 300px;
            margin-right: 20px;
        }
        #canvas-area {
            flex: 1;
            position: relative;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <label>tiers</label>
            <div id="tiers-container">
                <button id="btn-tier-1">1</button>
                <button id="btn-tier-2">2</button>
                <button id="btn-tier-3">3</button>
                <button id="btn-tier-4">4</button>
                <button id="btn-tier-5" class="active">5</button>
            </div>
            <label>show binary</label>
            <input type="checkbox" id="checkbox-binary">
            <label>characters</label>
            <input type="radio" id="radio-chars-yes" name="chars-toggle" value="yes" checked>
            <label for="radio-chars-yes">yes</label>
            <input type="radio" id="radio-chars-no" name="chars-toggle" value="no">
            <label for="radio-chars-no">no</label>
        </div>
        <div id="viz-container">
            <div id="header-section">
                <p id="original-text">MISSISSIPPI_RIVER</p>
                <p id="encoded-text">1100000101000101001001000011111010011011110101</p>
            </div>
            <div id="main-content">
                <div id="info-panel">
                    <p id="explanation-text">Once at the top, the total frequency becomes 17, which is the total number of characters.</p>
                    <div id="encoding-table">
                        <p>I = 00</p>
                        <p>S = 01</p>
                        <p>P = 100</p>
                        <p>R = 101</p>
                        <p>M = 1100</p>
                        <p>V = 1101</p>
                        <p>E = 1110</p>
                        <p>_ = 1111</p>
                    </div>
                </div>
                <div id="canvas-area"></div>
            </div>
        </div>
    </div>

    <script>
        const nodeData = [
            { id: 'I', label: 'I', freq: 5, tier: 1, pos: {x: 100, y: 450}, children: [] },
            { id: 'S', label: 'S', freq: 4, tier: 1, pos: {x: 180, y: 450}, children: [] },
            { id: 'P', label: 'P', freq: 2, tier: 1, pos: {x: 260, y: 450}, children: [] },
            { id: 'R', label: 'R', freq: 2, tier: 1, pos: {x: 340, y: 450}, children: [] },
            { id: 'M', label: 'M', freq: 1, tier: 1, pos: {x: 420, y: 450}, children: [] },
            { id: 'V', label: 'V', freq: 1, tier: 1, pos: {x: 500, y: 450}, children: [] },
            { id: 'E', label: 'E', freq: 1, tier: 1, pos: {x: 580, y: 450}, children: [] },
            { id: '_', label: '_', freq: 1, tier: 1, pos: {x: 660, y: 450}, children: [] },
            { id: 'IS', label: 'IS', freq: 9, tier: 2, pos: {x: 140, y: 350}, children: ['I', 'S'] },
            { id: 'PR', label: 'PR', freq: 4, tier: 2, pos: {x: 300, y: 350}, children: ['P', 'R'] },
            { id: 'MV', label: 'MV', freq: 2, tier: 2, pos: {x: 460, y: 350}, children: ['M', 'V'] },
            { id: 'E_', label: 'E_', freq: 2, tier: 2, pos: {x: 620, y: 350}, children: ['E', '_'] },
            { id: 'MVE_', label: 'MVE_', freq: 4, tier: 3, pos: {x: 540, y: 250}, children: ['MV', 'E_'] },
            { id: 'PRMVE_', label: 'PRMVE_', freq: 8, tier: 4, pos: {x: 420, y: 150}, children: ['PR', 'MVE_'] },
            { id: 'ROOT', label: 'ISPRMVE_', freq: 17, tier: 5, pos: {x: 280, y: 50}, children: ['IS', 'PRMVE_'] }
        ];

        let currentTier = 5;
        let showBinary = false;
        let showNodeChars = true;

        function setup() {
            const canvas = createCanvas(800, 500);
            canvas.parent('canvas-area');
            noLoop();
        }

        function draw() {
            background(255);
            stroke('#C70000');
            strokeWeight(2);
            textSize(12);
            textAlign(CENTER, CENTER);

            // Draw edges
            nodeData.forEach(node => {
                if (node.children.length > 0 && node.tier <= currentTier) {
                    node.children.forEach((childId, index) => {
                        const child = nodeData.find(n => n.id === childId);
                        if (child && child.tier <= currentTier) {
                            line(node.pos.x, node.pos.y, child.pos.x, child.pos.y);
                            if (showBinary) {
                                const midX = (node.pos.x + child.pos.x) / 2;
                                const midY = (node.pos.y + child.pos.y) / 2;
                                text(index === 0 ? '0' : '1', midX, midY - 10);
                            }
                        }
                    });
                }
            });

            // Draw nodes
            nodeData.forEach(node => {
                if (node.tier <= currentTier) {
                    fill('#FEFDD5');
                    stroke('#C70000');
                    strokeWeight(2);
                    rectMode(CENTER);
                    rect(node.pos.x, node.pos.y, 60, 30, 5);

                    let label = `${node.label}(${node.freq})`;
                    if (!showNodeChars && node.children.length > 0) {
                        label = `(${node.freq})`;
                    }
                    fill(0);
                    noStroke();
                    text(label, node.pos.x, node.pos.y);
                }
            });
        }

        function updateExplanation() {
            const explanationText = document.getElementById('explanation-text');
            if (currentTier === 1) {
                explanationText.textContent = "After determining the frequency of each letter, we draw the tree bottom up, one tier at a time.";
            } else if (currentTier >= 2 && currentTier <= 4) {
                explanationText.textContent = "We continue grouping until we get to the highest tier.";
            } else {
                explanationText.textContent = "Once at the top, the total frequency becomes 17, which is the total number of characters.";
            }
        }

        function updateVisibility() {
            const encodingTable = document.getElementById('encoding-table');
            const encodedText = document.getElementById('encoded-text');
            encodingTable.style.display = currentTier >= 2 ? 'block' : 'none';
            encodedText.style.display = currentTier === 5 ? 'block' : 'none';
        }

        document.querySelectorAll('#tiers-container button').forEach(button => {
            button.addEventListener('click', () => {
                document.querySelectorAll('#tiers-container button').forEach(b => b.classList.remove('active'));
                button.classList.add('active');
                currentTier = parseInt(button.textContent);
                updateExplanation();
                updateVisibility();
                redraw();
            });
        });

        document.getElementById('checkbox-binary').addEventListener('change', (e) => {
            showBinary = e.target.checked;
            redraw();
        });

        document.querySelectorAll('input[name="chars-toggle"]').forEach(radio => {
            radio.addEventListener('change', (e) => {
                showNodeChars = e.target.value === 'yes';
                redraw();
            });
        });

        // Initial setup
        updateVisibility();
    </script>
</body>
</html>
