<!DOCTYPE html>
<html>
<head>
  <title>Harmonic Series Visualization</title>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
  <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
  <style>
    body {
      margin: 0;
      font-family: Arial, sans-serif;
      display: flex;
      flex-direction: column;
      align-items: center;
    }
    #control-panel {
      display: flex;
      align-items: center;
      padding: 10px;
      background: #f0f0f0;
      width: 100%;
      justify-content: center;
    }
    #control-panel label {
      margin-right: 10px;
    }
    #visualization-area {
      display: flex;
      width: 100%;
      height: calc(100vh - 80px);
    }
    #function-plot-container {
      flex: 2;
      position: relative;
    }
    #sum-plot-container {
      flex: 1;
      position: relative;
    }
    #formula-label {
      position: absolute;
      top: 10px;
      left: 10px;
      background: rgba(255, 255, 255, 0.7);
      padding: 5px 10px;
      border-radius: 5px;
      font-size: 16px;
    }
  </style>
</head>
<body>
  <div id="control-panel">
    <label for="slider-n">n</label>
    <input type="range" id="slider-n" min="1" max="20" value="1" step="1">
    <input type="number" id="input-n" min="1" max="20" value="1" step="1">
  </div>
  <div id="visualization-area">
    <div id="function-plot-container">
      <div id="formula-label">$$y = \frac{2}{x}$$</div>
    </div>
    <div id="sum-plot-container"></div>
  </div>

  <script>
    document.addEventListener('DOMContentLoaded', function() {
      const slider = document.getElementById('slider-n');
      const input = document.getElementById('input-n');
      let n = 1;

      function updateN(newValue) {
        n = Math.min(20, Math.max(1, newValue));
        slider.value = n;
        input.value = n;
        if (functionSketch && sumSketch) {
          functionSketch.redraw();
          sumSketch.redraw();
        }
      }

      slider.addEventListener('input', function() {
        updateN(parseInt(slider.value));
      });

      input.addEventListener('input', function() {
        updateN(parseInt(input.value));
      });

      updateN(1);

      let functionSketch;
      let sumSketch;

      functionSketch = new p5(function(sketch) {
        sketch.setup = function() {
          const container = document.getElementById('function-plot-container');
          const width = container.offsetWidth;
          const height = container.offsetHeight;
          sketch.createCanvas(width, height);
          sketch.noLoop();
        };

        sketch.draw = function() {
          drawFunctionPlot(sketch, n);
        };
      });

      sumSketch = new p5(function(sketch) {
        sketch.setup = function() {
          const container = document.getElementById('sum-plot-container');
          const width = container.offsetWidth;
          const height = container.offsetHeight;
          sketch.createCanvas(width, height);
          sketch.noLoop();
        };

        sketch.draw = function() {
          drawSumPlot(sketch, n);
        };
      });

      function drawFunctionPlot(sketch, n) {
        const width = sketch.width;
        const height = sketch.height;
        const colors = [
          '#ffff80', '#98fb98', '#7fffd4', '#00ffff', '#87cefa',
          '#6495ed', '#9370db', '#8a2be2', '#ff69b4', '#ff00ff',
          '#ff1493', '#dc143c', '#ff4500', '#ffa500', '#ffd700',
          '#adff2f', '#00ff00', '#00ff7f', '#00fa9a', '#00ced1'
        ];

        sketch.background(255);
        sketch.stroke(0);
        sketch.strokeWeight(1);

        sketch.line(0, height, width, height);
        sketch.line(0, 0, 0, height);

        for (let i = 1; i <= 10; i++) {
          const px = sketch.map(i, 0, 10.5, 0, width);
          sketch.line(px, height, px, height - 5);
          sketch.textAlign(sketch.RIGHT, sketch.TOP);
          sketch.text(i, px - 2, height + 5);
        }

        for (let i = 0; i <= 2; i++) {
          const py = sketch.map(i, 0, 2.2, height, 0);
          sketch.line(0, py, 5, py);
          sketch.textAlign(sketch.RIGHT, sketch.CENTER);
          sketch.text(i, -5, py + 2);
        }

        sketch.stroke(0, 0, 255);
        sketch.noFill();
        sketch.beginShape();
        for (let x = 0.5; x <= 10; x += 0.1) {
          const y = 2 / x;
          const px = sketch.map(x, 0, 10.5, 0, width);
          const py = sketch.map(y, 0, 2.2, height, 0);
          sketch.vertex(px, py);
        }
        sketch.endShape();

        for (let k = 1; k <= n; k++) {
          const color = colors[(k - 1) % colors.length];
          sketch.fill(color);
          sketch.stroke(0);
          sketch.strokeWeight(1);

          const x1 = sketch.map(k, 0, 10.5, 0, width);
          const yBottom = sketch.map(2 / (k + 1), 0, 2.2, height, 0);
          const x2 = sketch.map(k + 1, 0, 10.5, 0, width);
          const yTop = sketch.map(2 / k, 0, 2.2, height, 0);

          sketch.rect(x1, yTop, x2 - x1, yBottom - yTop);

          if (k <= 3) {
            sketch.stroke(128);
            sketch.strokeWeight(1);
            sketch.drawingContext.setLineDash([5, 5]);
            sketch.line(x1, height, x1, yTop);
            sketch.drawingContext.setLineDash([]);
          }
        }
      }

      function drawSumPlot(sketch, n) {
        const width = sketch.width;
        const height = sketch.height;
        const colors = [
          '#ffff80', '#98fb98', '#7fffd4', '#00ffff', '#87cefa',
          '#6495ed', '#9370db', '#8a2be2', '#ff69b4', '#ff00ff',
          '#ff1493', '#dc143c', '#ff4500', '#ffa500', '#ffd700',
          '#adff2f', '#00ff00', '#00ff7f', '#00fa9a', '#00ced1'
        ];

        sketch.background(255);
        sketch.stroke(0);
        sketch.strokeWeight(1);

        sketch.line(0, height, width, height);
        sketch.line(0, 0, 0, height);

        for (let i = 0; i <= 1; i++) {
          const px = sketch.map(i, 0, 1.5, 0, width);
          sketch.line(px, height, px, height + 5);
          sketch.textAlign(sketch.CENTER, sketch.TOP);
          sketch.text(i, px, height + 5);
        }

        for (let i = 0; i <= 2; i++) {
          const py = sketch.map(i, 0, 2.2, height, 0);
          sketch.line(0, py, -5, py);
          sketch.textAlign(sketch.RIGHT, sketch.CENTER);
          sketch.text(i, -10, py);
        }

        let yStack = 0;
        for (let k = n; k >= 1; k--) {
          const h = 2 / k - 2 / (k + 1);
          const color = colors[(k - 1) % colors.length];
          sketch.fill(color);
          sketch.stroke(0);
          sketch.strokeWeight(1);

          const x1 = sketch.map(0, 0, 1.5, 0, width);
          const y1 = sketch.map(yStack, 0, 2.2, height, 0);
          const x2 = sketch.map(1, 0, 1.5, 0, width);
          const y2 = sketch.map(yStack + h, 0, 2.2, height, 0);

          sketch.rect(x1, y2, x2 - x1, y1 - y2);

          yStack += h;
        }
      }
    });
  </script>
</body>
</html>
