    <script>
        let currentModel = 'monod';
        let currentView = 'bifurcation';
        const parameters = {
            x2f: 4.0,
            umax: 0.6,
            km: 0.12,
            k1: 0.4545,
            ds: 0.6
        };

        // Initialize sliders and event listeners
        document.addEventListener('DOMContentLoaded', () => {
            const sliders = {
                'slider-x2f': { valueId: 'value-x2f', decimals: 1 },
                'slider-umax': { valueId: 'value-umax', decimals: 2 },
                'slider-km': { valueId: 'value-km', decimals: 2 },
                'slider-k1': { valueId: 'value-k1', decimals: 4 },
                'slider-ds': { valueId: 'value-ds', decimals: 2 }
            };

            Object.keys(sliders).forEach(id => {
                const slider = document.getElementById(id);
                const valueSpan = document.getElementById(sliders[id].valueId);
                slider.addEventListener('input', () => {
                    const value = parseFloat(slider.value);
                    parameters[id.replace('slider-', '')] = value;
                    valueSpan.textContent = value.toFixed(sliders[id].decimals);
                    updatePlot();
                });
            });

            document.getElementById('btn-monod').addEventListener('click', () => {
                currentModel = 'monod';
                document.getElementById('slider-k1-container').style.display = 'none';
                updateActiveButton('btn-monod', 'btn-si');
                updatePlot();
            });

            document.getElementById('btn-si').addEventListener('click', () => {
                currentModel = 'si';
                document.getElementById('slider-k1-container').style.display = 'block';
                updateActiveButton('btn-si', 'btn-monod');
                updatePlot();
            });

            document.getElementById('btn-bifurcation').addEventListener('click', () => {
                currentView = 'bifurcation';
                updateActiveButton('btn-bifurcation', 'btn-nontrivial');
                updatePlot();
            });

            document.getElementById('btn-nontrivial').addEventListener('click', () => {
                currentView = 'nontrivial';
                updateActiveButton('btn-nontrivial', 'btn-bifurcation');
                updatePlot();
            });

            // Initialize plot
            updatePlot();
        });

        function updateActiveButton(activeId, inactiveId) {
            document.getElementById(activeId).classList.add('active');
            document.getElementById(inactiveId).classList.remove('active');
        }

        function updatePlot() {
            const plotContainer = document.getElementById('plot-container');
            if (currentView === 'bifurcation') {
                drawBifurcationDiagram(plotContainer);
            } else {
                drawNontrivialStatePlot(plotContainer);
            }
        }

        function drawBifurcationDiagram(container) {
            const { x2f, umax, km, k1, ds } = parameters;
            const x2Values = Array.from({ length: 100 }, (_, i) => (x2f / 99) * i);

            let DValues, criticalX2;
            if (currentModel === 'monod') {
                DValues = x2Values.map(x2 => (umax * x2) / (km + x2));
            } else {
                DValues = x2Values.map(x2 => (umax * x2) / (km + x2 + k1 * x2 * x2));
                criticalX2 = Math.sqrt(km / k1);
            }

            // Calculate bifurcation point
            const D_bif = currentModel === 'monod' 
                ? (umax * x2f) / (km + x2f)
                : (umax * x2f) / (km + x2f + k1 * x2f * x2f);

            // Find intersection points
            const nontrivialIntersections = [];
            for (let i = 0; i < x2Values.length - 1; i++) {
                if ((DValues[i] - ds) * (DValues[i + 1] - ds) < 0) {
                    const x2 = x2Values[i] + (ds - DValues[i]) * (x2Values[i + 1] - x2Values[i]) / (DValues[i + 1] - DValues[i]);
                    nontrivialIntersections.push(x2);
                }
            }

            // Calculate stability for intersections
            const intersectionPoints = nontrivialIntersections.map(x2 => {
                const dDdx = currentModel === 'monod'
                    ? (umax * km) / Math.pow(km + x2, 2)
                    : (umax * (km - k1 * x2 * x2)) / Math.pow(km + x2 + k1 * x2 * x2, 2);
                return { x2, stable: dDdx > 0 };
            });

            // Create traces
            const traces = [];

            // Nontrivial steady state curve
            if (currentModel === 'monod') {
                traces.push({
                    x: DValues,
                    y: x2Values,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Nontrivial Steady State',
                    line: { color: 'blue' }
                });
            } else {
                const stableX2 = x2Values.filter(x2 => x2 <= criticalX2);
                const unstableX2 = x2Values.filter(x2 => x2 > criticalX2);
                traces.push({
                    x: stableX2.map(x2 => (umax * x2) / (km + x2 + k1 * x2 * x2)),
                    y: stableX2,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Stable',
                    line: { color: 'blue' }
                });
                traces.push({
                    x: unstableX2.map(x2 => (umax * x2) / (km + x2 + k1 * x2 * x2)),
                    y: unstableX2,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Unstable',
                    line: { color: 'red', dash: 'dash' }
                });
            }

            // Trivial steady state line
            traces.push({
                x: [0, D_bif],
                y: [x2f, x2f],
                type: 'scatter',
                mode: 'lines',
                name: 'Unstable Trivial',
                line: { color: 'red', dash: 'dash' }
            });
            traces.push({
                x: [D_bif, 1.0],
                y: [x2f, x2f],
                type: 'scatter',
                mode: 'lines',
                name: 'Stable Trivial',
                line: { color: 'blue' }
            });

            // Vertical line at Ds
            const layout = {
                xaxis: { title: 'dilution rate', range: [0, 1.0] },
                yaxis: { title: 'X₂', range: [0, 6.0] },
                shapes: [{
                    type: 'line',
                    x0: ds, x1: ds,
                    y0: 0, y1: 6.0,
                    line: { color: 'gray', width: 1 }
                }],
                margin: { l: 50, r: 50, b: 50, t: 50 }
            };

            // Intersection points
            if (intersectionPoints.length > 0) {
                traces.push({
                    x: intersectionPoints.map(p => ds),
                    y: intersectionPoints.map(p => p.x2),
                    mode: 'markers',
                    name: 'Nontrivial Intersections',
                    marker: {
                        color: intersectionPoints.map(p => p.stable ? 'magenta' : 'cyan'),
                        size: 10
                    }
                });
            }

            // Trivial intersection point
            traces.push({
                x: [ds],
                y: [x2f],
                mode: 'markers',
                name: 'Trivial Intersection',
                marker: { color: 'green', size: 10 }
            });

            Plotly.newPlot(container, traces, layout);
        }

        function drawNontrivialStatePlot(container) {
            const { x2f, umax, km, k1, ds } = parameters;
            const x2Values = Array.from({ length: 100 }, (_, i) => (7 * i) / 99);

            // μ(x2) curve
            const muValues = x2Values.map(x2 => (umax * x2) / (km + x2 + k1 * x2 * x2));

            // Calculate D_upper and D_lower
            const D_upper = umax / (1 + 2 * Math.sqrt(k1 * km));
            const D_lower = (umax * x2f) / (km + x2f + k1 * x2f * x2f);

            // Find intersection points
            const intersections = [];
            for (let i = 0; i < x2Values.length - 1; i++) {
                if ((muValues[i] - ds) * (muValues[i + 1] - ds) < 0) {
                    const x2 = x2Values[i] + (ds - muValues[i]) * (x2Values[i + 1] - x2Values[i]) / (muValues[i + 1] - muValues[i]);
                    intersections.push(x2);
                }
            }

            // Determine stability of intersections
            const intersectionPoints = intersections.map(x2 => {
                const dMudx = (umax * (km - k1 * x2 * x2)) / Math.pow(km + x2 + k1 * x2 * x2, 2);
                return { x2, stable: dMudx > 0 };
            });

            // Create traces
            const traces = [{
                x: x2Values,
                y: muValues,
                type: 'scatter',
                mode: 'lines',
                name: 'μ(x₂)',
                line: { color: 'blue' }
            }];

            // Horizontal lines
            traces.push({
                x: [0, 7],
                y: [ds, ds],
                type: 'scatter',
                mode: 'lines',
                name: 'Dₛ',
                line: { color: 'gray' }
            });
            traces.push({
                x: [0, 7],
                y: [D_upper, D_upper],
                type: 'scatter',
                mode: 'lines',
                name: 'D_upper',
                line: { color: 'red', dash: 'dash' }
            });
            traces.push({
                x: [0, 7],
                y: [D_lower, D_lower],
                type: 'scatter',
                mode: 'lines',
                name: 'D_lower',
                line: { color: 'red', dash: 'dash' }
            });

            // Bistability region
            traces.push({
                x: [0, 7, 7, 0],
                y: [D_upper, D_upper, D_lower, D_lower],
                fill: 'toself',
                fillcolor: 'rgba(255, 165, 0, 0.3)',
                line: { color: 'transparent' },
                name: 'Bistability Region'
            });

            // Intersection points
            if (intersectionPoints.length > 0) {
                traces.push({
                    x: intersectionPoints.map(p => p.x2),
                    y: intersectionPoints.map(p => ds),
                    mode: 'markers',
                    name: 'Intersections',
                    marker: {
                        color: intersectionPoints.map(p => p.stable ? 'magenta' : 'cyan'),
                        size: 10
                    }
                });
            }

            const layout = {
                xaxis: { title: 'substrate concentration', range: [0, 7] },
                yaxis: { title: 'specific growth rate coefficient', range: [0, 1] },
                margin: { l: 50, r: 50, b: 50, t: 50 }
            };

            Plotly.newPlot(container, traces, layout);
        }
    </script>
</body>
</html>
