            </div>
        </div>
    </div>

    <script>
        // THREE.js Setup
        const globeContainer = document.getElementById('globe-container');
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(45, globeContainer.clientWidth / globeContainer.clientHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(globeContainer.clientWidth, globeContainer.clientHeight);
        globeContainer.appendChild(renderer.domElement);

        // Procedural Earth Texture
        const earthTexture = (() => {
            const canvas = document.createElement('canvas');
            const ctx = canvas.getContext('2d');
            canvas.width = 256;
            canvas.height = 256;
            ctx.fillStyle = '#0000ff';
            ctx.fillRect(0, 0, canvas.width, canvas.height);
            ctx.fillStyle = '#00ff00';
            // Simple continent shapes
            ctx.fillRect(64, 64, 32, 64); // Africa
            ctx.fillRect(128, 64, 32, 64); // Asia
            ctx.fillRect(32, 128, 32, 64); // South America
            return new THREE.CanvasTexture(canvas);
        })();

        // Earth Mesh
        const earthGeometry = new THREE.SphereGeometry(1, 64, 64);
        const earthMaterial = new THREE.MeshPhongMaterial({ map: earthTexture });
        const earthMesh = new THREE.Mesh(earthGeometry, earthMaterial);
        scene.add(earthMesh);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);
        const sunLight = new THREE.DirectionalLight(0xffffff, 1);
        sunLight.position.set(0, 1, 1).normalize();
        scene.add(sunLight);

        // Grid
        const gridGeometry = new THREE.WireframeGeometry(earthGeometry);
        const gridMaterial = new THREE.LineBasicMaterial({ color: 0xffffff, opacity: 0.1, transparent: true });
        const grid = new THREE.LineSegments(gridGeometry, gridMaterial);
        earthMesh.add(grid);

        // Vectors
        const axisHelper = new THREE.AxesHelper(1.2);
        scene.add(axisHelper);
        const surfaceNormal = new THREE.ArrowHelper(new THREE.Vector3(0, 0, 1), new THREE.Vector3(0, 0, 1), 0.5, 0x000000);
        earthMesh.add(surfaceNormal);
        const sunVector = new THREE.ArrowHelper(new THREE.Vector3(0, 1, -1), new THREE.Vector3(0, 0, 1), 0.5, 0xff0000);
        earthMesh.add(sunVector);

        // Orbit Controls
        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        camera.position.set(0, 0, 3);
        controls.update();

        // Plotly Setup
        const instantPlot = document.getElementById('plot-instant');
        const cumulativePlot = document.getElementById('plot-cumulative');

        const initPlot = (id, title, yTitle, yRange) => {
            Plotly.newPlot(id, [], {
                title, yaxis: { title: yTitle, range: yRange },
                xaxis: { title: 'time (hr)', range: [0, 24] },
                margin: { l: 50, r: 20, t: 40, b: 40 }
            });
        };

        initPlot('plot-instant', 'Instantaneous Energy', 'watts/m²', [0, 1400]);
        initPlot('plot-cumulative', 'Cumulative Energy', 'watts/m²', [0, 14000]);

        // Slider References
        const seasonSlider = document.getElementById('slider-season');
        const latitudeSlider = document.getElementById('slider-latitude');
        const timeSlider = document.getElementById('slider-time');
        const valueSeason = document.getElementById('value-season');
        const valueLatitude = document.getElementById('value-latitude');
        const valueTime = document.getElementById('value-time');

        // Update Function
        function update() {
            // Update Values
            const season = parseFloat(seasonSlider.value);
            const latitude = parseFloat(latitudeSlider.value);
            const time = parseFloat(timeSlider.value);
            valueSeason.textContent = season.toFixed(3);
            valueLatitude.textContent = latitude.toFixed(2);
            valueTime.textContent = time.toFixed(2);

            // Physics Calculations
            const declination = 23.5 * Math.cos(2 * Math.PI * season);
            const δ = THREE.MathUtils.degToRad(declination);
            const λ = THREE.MathUtils.degToRad(latitude);

            // Globe Update
            earthMesh.rotation.y = (time / 24) * 2 * Math.PI;
            sunLight.position.set(0, Math.sin(δ), -Math.cos(δ)).normalize();
            sunVector.setDirection(sunLight.position);

            // Surface Normal at 0° longitude
            const normalDir = new THREE.Vector3(
                Math.cos(λ) * Math.cos(0),
                Math.cos(λ) * Math.sin(0),
                Math.sin(λ)
            );
            surfaceNormal.setDirection(normalDir);

            // Latitude Line
            scene.children = scene.children.filter(c => !(c instanceof THREE.LineLoop));
            const latCircle = new THREE.CircleGeometry(1.01, 64, 0, Math.PI/2 - λ);
            const latLine = new THREE.LineLoop(latCircle, new THREE.LineDashedMaterial({ color: 0x0000ff, dashSize: 0.05, gapSize: 0.05 }));
            latLine.rotation.x = Math.PI/2;
            scene.add(latLine);

            // Plot Data Calculation
            const hours = Array.from({length: 241}, (_, i) => i * 0.1);
            const calculateEnergy = (lat) => {
                const latRad = THREE.MathUtils.degToRad(lat);
                return hours.map(h => {
                    const HRA = THREE.MathUtils.degToRad(15 * (h - 12));
                    const energy = 1361 * Math.max(0, 
                        Math.sin(latRad)*Math.sin(δ) + 
                        Math.cos(latRad)*Math.cos(δ)*Math.cos(HRA)
                    );
                    return energy;
                });
            };

            // Update Plots
            const equatorData = calculateEnergy(0);
            const selectedData = calculateEnergy(latitude);
            const poleData = calculateEnergy(90);

            const cumulative = (arr) => {
                let sum = 0;
                return arr.map(v => (sum += v * 0.1));
            };

            // Instantaneous Plot
            Plotly.react('plot-instant', [
                { x: hours, y: equatorData, type: 'scatter', mode: 'lines', line: { color: 'red' } },
                { x: hours, y: selectedData, type: 'scatter', mode: 'lines', line: { color: 'black' } },
                { x: hours, y: poleData, type: 'scatter', mode: 'lines', line: { color: 'blue' } },
                { x: [time], y: [selectedData[Math.round(time*10)]], type: 'scatter', mode: 'markers', marker: { color: 'black' } }
            ]);

            // Cumulative Plot
            Plotly.react('plot-cumulative', [
                { x: hours, y: cumulative(equatorData), type: 'scatter', mode: 'lines', line: { color: 'red' } },
                { x: hours, y: cumulative(selectedData), type: 'scatter', mode: 'lines', line: { color: 'black' } },
                { x: hours, y: cumulative(poleData), type: 'scatter', mode: 'lines', line: { color: 'blue' } }
            ]);
        }

        // Event Listeners
        seasonSlider.addEventListener('input', update);
        latitudeSlider.addEventListener('input', update);
        timeSlider.addEventListener('input', update);

        // Initial Update
        update();

        // Animation Loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }
        animate();
    </script>
</body>
</html>
