<!DOCTYPE html>
<html>
<head>
  <title>Congruent Number Visualization</title>
  <style>
    body {
      display: flex;
      justify-content: center;
      align-items: center;
      min-height: 100vh;
      margin: 0;
      font-family: Arial, sans-serif;
    }
    #demo-container {
      display: flex;
      flex-direction: column;
      align-items: center;
      width: 100%;
      max-width: 900px;
      padding: 20px;
    }
    #control-panel {
      display: flex;
      align-items: center;
      margin-bottom: 20px;
    }
    #control-panel span {
      margin-right: 10px;
    }
    #slider-area {
      width: 200px;
      margin-right: 10px;
    }
    #input-area {
      width: 60px;
      text-align: center;
    }
    #canvas-container {
      border: 1px solid #ccc;
    }
  </style>
</head>
<body>
  <div id="demo-container">
    <div id="control-panel">
      <span>area</span>
      <input type="range" id="slider-area" min="1" max="101" step="1" value="13">
      <input type="number" id="input-area" min="1" max="101" step="1" value="13">
    </div>
    <div id="canvas-container"></div>
  </div>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
  <script>
    const congruentData = {
      5: { a: {n: 3, d: 2}, b: {n: 20, d: 3}, c: {n: 41, d: 6} },
      6: { a: {n: 3, d: 1}, b: {n: 4, d: 1}, c: {n: 5, d: 1} },
      7: { a: {n: 35, d: 12}, b: {n: 24, d: 5}, c: {n: 337, d: 60} },
      13: { a: {n: 780, d: 323}, b: {n: 323, d: 30}, c: {n: 106921, d: 9690} },
      79: { a: {n: 233126551, d: 167973000}, b: {n: 335946000, d: 2950969}, c: {n: 56434050774922081, d: 495683115837000} }
    };

    let p5Instance = new p5(sketch);

    function sketch(p) {
      p.currentN = 13;

      function formatNumber(num) {
        return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");
      }

      function drawFraction(fraction, x, y, rotation, fontSize) {
        p.push();
        p.translate(x, y);
        p.rotate(rotation);
        p.textSize(fontSize);
        let numStr = formatNumber(fraction.n);
        let denStr = formatNumber(fraction.d);
        let numWidth = p.textWidth(numStr);
        let denWidth = p.textWidth(denStr);
        let maxWidth = Math.max(numWidth, denWidth);
        p.textAlign(p.CENTER, p.CENTER);
        p.fill(0x8B, 0x5A, 0x2B);
        p.text(numStr, 0, -fontSize/2 - 5);
        p.text(denStr, 0, fontSize/2 + 5);
        p.line(-maxWidth/2, 0, maxWidth/2, 0);
        p.pop();
      }

      p.setup = function() {
        let canvas = p.createCanvas(800, 600);
        canvas.parent('canvas-container');
        p.textFont('Arial');
      };

      p.draw = function() {
        p.background(255);
        let n = p.currentN;
        if (congruentData.hasOwnProperty(n)) {
          let sides = congruentData[n];
          let a = sides.a;
          let b = sides.b;
          let c = sides.c;
          let a_value = a.n / a.d;
          let b_value = b.n / b.d;
          let padding = 50;
          let scale = Math.min((p.width - padding)/a_value, (p.height - padding)/b_value);
          let a_scaled = a_value * scale;
          let b_scaled = b_value * scale;
          let x = (p.width - a_scaled)/2;
          let y = (p.height - b_scaled)/2;
          p.fill(0xFF, 0xF2, 0xE5);
          p.stroke(0);
          p.strokeWeight(2);
          p.beginShape();
          p.vertex(x, y);
          p.vertex(x + a_scaled, y);
          p.vertex(x, y + b_scaled);
          p.endShape(p.CLOSE);
          drawFraction(a, x + a_scaled/2, y + 20, 0, 16);
          drawFraction(b, x + 20, y + b_scaled/2, 0, 16);
          let midX = x + a_scaled/2;
          let midY = y + b_scaled/2;
          let dx = -a_scaled, dy = b_scaled;
          let angle = Math.atan2(dy, dx);
          let perpX = dy, perpY = -dx;
          let len = Math.sqrt(perpX**2 + perpY**2);
          perpX /= len; perpY /= len;
          let labelX = midX + perpX * 20;
          let labelY = midY + perpY * 20;
          drawFraction(c, labelX, labelY, angle, 16);
        } else {
          p.fill(255, 0, 0);
          p.textAlign(p.CENTER, p.CENTER);
          p.textSize(20);
          p.text(`${n} is not a congruent number`, p.width/2, p.height/2);
        }
      };
    }

    document.addEventListener('DOMContentLoaded', function() {
      const slider = document.getElementById('slider-area');
      const input = document.getElementById('input-area');
      slider.addEventListener('input', function() {
        input.value = this.value;
        p5Instance.currentN = parseInt(this.value);
        p5Instance.redraw();
      });
      input.addEventListener('input', function() {
        slider.value = this.value;
        p5Instance.currentN = parseInt(this.value);
        p5Instance.redraw();
      });
    });
  </script>
</body>
