        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/OrbitControls.js"></script>
    <script>
        // Global variables for sliders and plots
        const sliders = {
            alpha: document.getElementById('slider-alpha'),
            beta: document.getElementById('slider-beta'),
            w: document.getElementById('slider-w'),
            r: document.getElementById('slider-r'),
            Q: document.getElementById('slider-Q'),
            C: document.getElementById('slider-C')
        };

        const labels = {
            alpha: document.getElementById('label-alpha'),
            beta: document.getElementById('label-beta'),
            w: document.getElementById('label-w'),
            r: document.getElementById('label-r'),
            Q: document.getElementById('label-Q'),
            C: document.getElementById('label-C')
        };

        const outputs = {
            primal: document.getElementById('output-primal'),
            dual: document.getElementById('output-dual')
        };

        // Initialize default values
        function resetValues() {
            sliders.alpha.value = 0.5;
            sliders.beta.value = 0.5;
            sliders.w.value = 1.0;
            sliders.r.value = 1.0;
            sliders.Q.value = 3.0;
            sliders.C.value = 4.0;
            updateLabels();
        }

        function updateLabels() {
            Object.keys(sliders).forEach(key => {
                labels[key].textContent = parseFloat(sliders[key].value).toFixed(2);
            });
        }

        // Event listeners for sliders
        Object.keys(sliders).forEach(key => {
            sliders[key].addEventListener('input', () => {
                updateLabels();
                update();
            });
        });

        // Reset button
        document.getElementById('btn-reset').addEventListener('click', () => {
            resetValues();
            update();
        });

        // Button interactions
        document.getElementById('btn-C-from-primal').addEventListener('click', () => {
            const primalCost = parseFloat(outputs.primal.dataset.c || 6.0);
            sliders.C.value = primalCost.toFixed(2);
            updateLabels();
            update();
        });

        document.getElementById('btn-Q-from-dual').addEventListener('click', () => {
            const dualQuantity = parseFloat(outputs.dual.dataset.q || 2.0);
            sliders.Q.value = dualQuantity.toFixed(2);
            updateLabels();
            update();
        });

        // Calculation functions
        function solvePrimal() {
            const α = parseFloat(sliders.alpha.value);
            const β = parseFloat(sliders.beta.value);
            const w = parseFloat(sliders.w.value);
            const r = parseFloat(sliders.r.value);
            const Q_in = parseFloat(sliders.Q.value);

            const k = (w * β) / (r * α);
            const x_p = Math.pow(Q_in / Math.pow(k, β), 1 / (α + β));
            const y_p = x_p * k;
            const C_out = w * x_p + r * y_p;

            return { x: x_p, y: y_p, C: C_out };
        }

        function solveDual() {
            const α = parseFloat(sliders.alpha.value);
            const β = parseFloat(sliders.beta.value);
            const w = parseFloat(sliders.w.value);
            const r = parseFloat(sliders.r.value);
            const C_in = parseFloat(sliders.C.value);

            const x_d = (C_in * α) / (w * (α + β));
            const y_d = (C_in * β) / (r * (α + β));
            const Q_out = Math.pow(x_d, α) * Math.pow(y_d, β);

            return { x: x_d, y: y_d, Q: Q_out };
        }

        // Visualization setup
        let primalScene, primalCamera, primalRenderer, primalControls;
        let dualScene, dualCamera, dualRenderer, dualControls;

        function initPrimalPlot() {
            const container = document.getElementById('plot-primal');
            primalScene = new THREE.Scene();
            primalCamera = new THREE.PerspectiveCamera(45, container.clientWidth / container.clientHeight, 0.1, 100);
            primalRenderer = new THREE.WebGLRenderer({ antialias: true });
            primalRenderer.setSize(container.clientWidth, container.clientHeight);
            container.appendChild(primalRenderer.domElement);

            // Lighting
            primalScene.add(new THREE.AmbientLight(0x404040));
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
            directionalLight.position.set(5, 5, 5).normalize();
            primalScene.add(directionalLight);

            // Axes
            const axesHelper = new THREE.AxesHelper(5);
            primalScene.add(axesHelper);

            // Bounding box
            const box = new THREE.Box3(new THREE.Vector3(-5, -5, -5), new THREE.Vector3(5, 5, 5));
            const boxHelper = new THREE.BoxHelper(new THREE.Mesh(new THREE.BoxGeometry(10, 10, 10)));
            primalScene.add(boxHelper);

            // Initial plot
            updatePrimalPlot();

            // Controls
            primalControls = new THREE.OrbitControls(primalCamera, primalRenderer.domElement);
            primalCamera.position.set(5, 5, 5);
            primalControls.update();

            animatePrimal();
        }

        function updatePrimalPlot() {
            if (!primalScene) return;

            // Clear existing plot objects
            primalScene.children = primalScene.children.filter(obj => 
                !(obj.isMesh || obj.isLine || obj.isSprite)
            );

            // Get current values
            const α = parseFloat(sliders.alpha.value);
            const β = parseFloat(sliders.beta.value);
            const w = parseFloat(sliders.w.value);
            const r = parseFloat(sliders.r.value);
            const Q_in = parseFloat(sliders.Q.value);

            // Calculate optimal point
            const { x: x_p, y: y_p, C: C_out } = solvePrimal();

            // Isoquant curtain
            const isoquantGeometry = new THREE.BufferGeometry();
            const isoquantPoints = [];
            for (let x = 0.1; x <= 10; x += 0.1) {
                const y = Math.pow(Q_in / Math.pow(x, α), 1/β);
                isoquantPoints.push(new THREE.Vector3(x, y, 0));
                isoquantPoints.push(new THREE.Vector3(x, y, C_out));
            }
            isoquantGeometry.setFromPoints(isoquantPoints);
            const isoquantMaterial = new THREE.LineBasicMaterial({ color: 0xffa500, opacity: 0.5 });
            const isoquantCurtain = new THREE.LineSegments(isoquantGeometry, isoquantMaterial);
            primalScene.add(isoquantCurtain);

            // Isocost plane
            const isocostGeometry = new THREE.PlaneGeometry(10, 10);
            const isocostMaterial = new THREE.MeshBasicMaterial({ color: 0x808080, opacity: 0.3, transparent: true });
            const isocostPlane = new THREE.Mesh(isocostGeometry, isocostMaterial);

            // Calculate plane rotation based on w and r
            const normal = new THREE.Vector3(w, r, -1).normalize();
            const quaternion = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(0,0,1), normal);
            isocostPlane.quaternion.copy(quaternion);

            // Position plane to pass through (x_p, y_p, C_out)
            isocostPlane.position.set(x_p, y_p, C_out);
            primalScene.add(isocostPlane);

            // Solution vector
            const solutionGeometry = new THREE.BufferGeometry().setFromPoints([
                new THREE.Vector3(0, 0, 0),
                new THREE.Vector3(x_p, y_p, C_out)
            ]);
            const solutionMaterial = new THREE.LineBasicMaterial({ color: 0x0000ff, linewidth: 3 });
            const solutionVector = new THREE.Line(solutionGeometry, solutionMaterial);
            primalScene.add(solutionVector);
        }

        function initDualPlot() {
            const container = document.getElementById('plot-dual');
            dualScene = new THREE.Scene();
            dualCamera = new THREE.PerspectiveCamera(45, container.clientWidth / container.clientHeight, 0.1, 100);
            dualRenderer = new THREE.WebGLRenderer({ antialias: true });
            dualRenderer.setSize(container.clientWidth, container.clientHeight);
            container.appendChild(dualRenderer.domElement);

            // Lighting
            dualScene.add(new THREE.AmbientLight(0x404040));
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
            directionalLight.position.set(5, 5, 5).normalize();
            dualScene.add(directionalLight);

            // Axes
            const axesHelper = new THREE.AxesHelper(5);
            dualScene.add(axesHelper);

            // Bounding box
            const box = new THREE.Box3(new THREE.Vector3(-5, -5, -5), new THREE.Vector3(5, 5, 5));
            const boxHelper = new THREE.BoxHelper(new THREE.Mesh(new THREE.BoxGeometry(10, 10, 10)));
            dualScene.add(boxHelper);

            // Initial plot
            updateDualPlot();

            // Controls
            dualControls = new THREE.OrbitControls(dualCamera, dualRenderer.domElement);
            dualCamera.position.set(5, 5, 5);
            dualControls.update();

            animateDual();
        }

        function updateDualPlot() {
            if (!dualScene) return;

            // Clear existing plot objects
            dualScene.children = dualScene.children.filter(obj => 
                !(obj.isMesh || obj.isLine || obj.isSprite)
            );

            // Get current values
            const α = parseFloat(sliders.alpha.value);
            const β = parseFloat(sliders.beta.value);
            const w = parseFloat(sliders.w.value);
            const r = parseFloat(sliders.r.value);
            const C_in = parseFloat(sliders.C.value);

            // Calculate optimal point
            const { x: x_d, y: y_d, Q: Q_out } = solveDual();

            // Production surface
            const surfaceGeometry = new THREE.ParametricGeometry((u, v, target) => {
                const x = u * 10;
                const y = v * 10;
                const z = Math.pow(x, α) * Math.pow(y, β);
                target.set(x, y, z);
            }, 50, 50);
            const surfaceMaterial = new THREE.MeshBasicMaterial({ color: 0xffa500, wireframe: false });
            const productionSurface = new THREE.Mesh(surfaceGeometry, surfaceMaterial);
            dualScene.add(productionSurface);

            // Budget curtain
            const budgetGeometry = new THREE.BufferGeometry();
            const budgetPoints = [];
            for (let x = 0; x <= 10; x += 0.1) {
                const y = (C_in - w * x) / r;
                if (y >= 0) {
                    budgetPoints.push(new THREE.Vector3(x, y, 0));
                    budgetPoints.push(new THREE.Vector3(x, y, 10));
                }
            }
            budgetGeometry.setFromPoints(budgetPoints);
            const budgetMaterial = new THREE.LineBasicMaterial({ color: 0x808080, opacity: 0.3 });
            const budgetCurtain = new THREE.LineSegments(budgetGeometry, budgetMaterial);
            dualScene.add(budgetCurtain);

            // Intersection curve
            const intersectionGeometry = new THREE.BufferGeometry();
            const intersectionPoints = [];
            for (let x = 0.1; x <= 10; x += 0.1) {
                const y = (C_in - w * x) / r;
                if (y > 0) {
                    const Q = Math.pow(x, α) * Math.pow(y, β);
                    intersectionPoints.push(new THREE.Vector3(x, y, Q));
                }
            }
            intersectionGeometry.setFromPoints(intersectionPoints);
            const intersectionMaterial = new THREE.LineBasicMaterial({ color: 0xff0000 });
            const intersectionCurve = new THREE.Line(intersectionGeometry, intersectionMaterial);
            dualScene.add(intersectionCurve);

            // Optimal point
            const sphereGeometry = new THREE.SphereGeometry(0.1, 16, 16);
            const sphereMaterial = new THREE.MeshBasicMaterial({ color: 0x808080 });
            const optimalPoint = new THREE.Mesh(sphereGeometry, sphereMaterial);
            optimalPoint.position.set(x_d, y_d, Q_out);
            dualScene.add(optimalPoint);
        }

        function animatePrimal() {
            requestAnimationFrame(animatePrimal);
            primalControls.update();
            primalRenderer.render(primalScene, primalCamera);
        }

        function animateDual() {
            requestAnimationFrame(animateDual);
            dualControls.update();
            dualRenderer.render(dualScene, dualCamera);
        }

        function update() {
            // Update primal problem
            const primalSolution = solvePrimal();
            outputs.primal.textContent = `x = ${primalSolution.x.toFixed(2)}, y = ${primalSolution.y.toFixed(2)}, C = ${primalSolution.C.toFixed(2)}`;
            outputs.primal.dataset.c = primalSolution.C;

            // Update dual problem
            const dualSolution = solveDual();
            outputs.dual.textContent = `x = ${dualSolution.x.toFixed(2)}, y = ${dualSolution.y.toFixed(2)}, Q = ${dualSolution.Q.toFixed(2)}`;
            outputs.dual.dataset.q = dualSolution.Q;

            // Update visualizations
            updatePrimalPlot();
            updateDualPlot();
        }

        // Initialize everything
        resetValues();
        initPrimalPlot();
        initDualPlot();
        update();
    </script>
</body>
</html>
