            </div>
        </div>
        <div id="visualization-area"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        let results = {};

        function setup() {
            const canvas = createCanvas(800, 500);
            canvas.parent('visualization-area');
            noLoop();
            updateCalculation();
        }

        function draw() {
            background('#F0F0F0');
            // Draw Flash Vessel
            fill('#E6E6FA');
            stroke(0);
            rect(350, 200, 100, 100);
            fill(0);
            textAlign(CENTER, CENTER);
            text('Flash', 400, 250);

            // Feed Stream
            stroke('#9ACD32');
            line(100, 250, 350, 250);
            drawArrowhead(350, 250, 340, 245);
            drawArrowhead(350, 250, 340, 255);

            // Vapor Stream
            stroke('#9370DB');
            line(400, 200, 400, 100);
            line(400, 100, 550, 100);
            drawArrowhead(550, 100, 540, 95);
            drawArrowhead(550, 100, 540, 105);

            // Liquid Stream
            stroke('#FFA500');
            line(400, 300, 400, 400);
            line(400, 400, 550, 400);
            drawArrowhead(550, 400, 540, 395);
            drawArrowhead(550, 400, 540, 405);

            // Heat Input
            stroke('#FF0000');
            strokeWeight(2);
            for (let x = 200; x <= 350; x += 10) {
                line(x, 150, x + 5, 150);
            }
            drawArrowhead(355, 150, 350, 145);
            drawArrowhead(355, 150, 350, 155);

            // Text Labels
            textAlign(LEFT, TOP);
            fill(0);
            text('Feed: T=323.2 K, P=1000 kPa', 10, 10);

            if (results.y) {
                const yStr = results.y.map(v => v.toFixed(3)).join(', ');
                text('Vapor mole fractions: ' + yStr, 600, 100);
            }

            if (results.x) {
                const xStr = results.x.map(v => v.toFixed(3)).join(', ');
                text('Liquid mole fractions: ' + xStr, 600, 150);
            }

            if (results.T_flash) {
                text(`Vapor fraction: ${results.V_frac.toFixed(3)}\nFlash temperature: ${results.T_flash.toFixed(1)} K\nFlash pressure: ${document.getElementById('slider-pressure').value} kPa`, 600, 200);
            }

            text(`Q = ${document.getElementById('slider-heat').value} kJ/hr`, 400, 150);
        }

        function drawArrowhead(x, y, x2, y2) {
            line(x, y, x2, y2);
            line(x, y, x2, y2 + 5);
        }

        function updateCalculation() {
            const P = parseFloat(document.getElementById('slider-pressure').value);
            const Q = parseFloat(document.getElementById('slider-heat').value);
            document.getElementById('pressure-value').textContent = `${P} kPa`;
            document.getElementById('heat-value').textContent = `${Q} kJ/hr`;
            results = calculateFlash(P, Q);
            redraw();
        }

        document.getElementById('slider-pressure').addEventListener('input', updateCalculation);
        document.getElementById('slider-heat').addEventListener('input', updateCalculation);

        function bisection(func, a, b, tolerance = 1e-6, maxIter = 100) {
            let fa = func(a);
            let fb = func(b);
            if (fa * fb > 0) throw new Error("No root in interval");
            for (let i = 0; i < maxIter; i++) {
                const c = (a + b) / 2;
                const fc = func(c);
                if (fc * fa < 0) {
                    b = c;
                    fb = fc;
                } else {
                    a = c;
                    fa = fc;
                }
                if (Math.abs(b - a) < tolerance) break;
            }
            return (a + b) / 2;
        }

        function calculateFlash(P_flash, Q_hr) {
            const F = 10;
            const T_F = 323.2;
            const z = [0.25, 0.25, 0.25, 0.25];
            const components = [
                { A: 6.80165, B: 1005.157, C: -27.159, Cp_L: 132, ΔH_vap: 22400 },
                { A: 6.86177, B: 1184.249, C: -35.223, Cp_L: 161, ΔH_vap: 25800 },
                { A: 6.90237, B: 1358.59, C: -51.303, Cp_L: 225, ΔH_vap: 31800 },
                { A: 6.92383, B: 1431.149, C: -59.501, Cp_L: 255, ΔH_vap: 34400 }
            ];

            function solveRachfordRice(K) {
                function rrFunc(V) {
                    return z.reduce((sum, zi, i) => sum + zi * (K[i] - 1) / (1 + V * (K[i] - 1)), 0);
                }
                try {
                    return bisection(rrFunc, 0, 1, 1e-6, 100);
                } catch (e) {
                    return 0.5;
                }
            }

            function energyBalance(T) {
                const K = components.map(c => Math.pow(10, c.A - c.B / (T + c.C)) / P_flash);
                const V = solveRachfordRice(K);
                let LHS = Q_hr / F + z.reduce((sum, zi, i) => sum + zi * components[i].Cp_L * (T_F - T), 0);
                const y = z.map((zi, i) => zi * K[i] / (1 + V * (K[i] - 1)));
                const RHS = V * y.reduce((sum, yi, i) => sum + yi * components[i].ΔH_vap, 0);
                return LHS - RHS;
            }

            let T;
            try {
                T = bisection(energyBalance, 200, 600, 1e-3, 100);
            } catch (e) {
                T = 350;
            }

            const K = components.map(c => Math.pow(10, c.A - c.B / (T + c.C)) / P_flash);
            const V = solveRachfordRice(K);
            const x = z.map((zi, i) => zi / (1 + V * (K[i] - 1)));
            const y = z.map((zi, i) => zi * K[i] / (1 + V * (K[i] - 1)));

            return { T_flash: T, V_frac: V, x, y };
        }
    </script>
</body>
</html>
