    <script>
        // Three.js Setup
        const canvas = document.getElementById('scene-canvas');
        const renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        renderer.shadowMap.enabled = true;

        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.set(0, 5, 10);

        // Lighting
        const sunLight = new THREE.DirectionalLight(0xffffff, 1);
        sunLight.position.set(5, 5, 5);
        sunLight.castShadow = true;
        scene.add(sunLight);

        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        // Procedural Moon Texture
        function createMoonTexture() {
            const size = 256;
            const canvas = document.createElement('canvas');
            canvas.width = size;
            canvas.height = size;
            const context = canvas.getContext('2d');
            for (let i = 0; i < size * size; i++) {
                const x = i % size;
                const y = Math.floor(i / size);
                const value = Math.random() * 255;
                context.fillStyle = `rgb(${value},${value},${value})`;
                context.fillRect(x, y, 1, 1);
            }
            return new THREE.CanvasTexture(canvas);
        }

        const moonTexture = createMoonTexture();

        // Moon Surface (Land View)
        const moonSurface = new THREE.Mesh(
            new THREE.PlaneGeometry(20, 20),
            new THREE.MeshStandardMaterial({
                map: moonTexture,
                bumpMap: moonTexture,
                bumpScale: 0.1
            })
        );
        moonSurface.rotation.x = -Math.PI / 2;
        moonSurface.receiveShadow = true;
        scene.add(moonSurface);

        // Moon Sphere (Space View)
        const moonSphere = new THREE.Mesh(
            new THREE.SphereGeometry(1, 32, 32),
            new THREE.MeshStandardMaterial({
                map: moonTexture
            })
        );
        moonSphere.visible = false;
        scene.add(moonSphere);

        // Earth
        const earthGeometry = new THREE.SphereGeometry(0.5, 32, 32);
        const earthMaterial = new THREE.MeshPhongMaterial({ color: 0x0000ff });
        const earth = new THREE.Mesh(earthGeometry, earthMaterial);
        earth.castShadow = true;
        earth.receiveShadow = true;
        scene.add(earth);

        // Atmosphere
        const atmosphereGeometry = new THREE.SphereGeometry(0.51, 32, 32);
        const atmosphereMaterial = new THREE.MeshBasicMaterial({
            color: 0x0000ff,
            blending: THREE.AdditiveBlending,
            transparent: true,
            opacity: 0.2
        });
        const atmosphere = new THREE.Mesh(atmosphereGeometry, atmosphereMaterial);
        scene.add(atmosphere);

        // Constellations
        const constellations = new THREE.Group();
        const stars = [
            { x: 10, y: 10, z: 10 },
            { x: -10, y: 10, z: 10 },
            { x: 0, y: -10, z: 10 },
            { x: 0, y: 0, z: 15 }
        ];
        const starGeometry = new THREE.BufferGeometry().setFromPoints(
            stars.map(s => new THREE.Vector3(s.x, s.y, s.z))
        );
        const starMaterial = new THREE.PointsMaterial({ color: 0xffffcc, size: 0.1 });
        const starPoints = new THREE.Points(starGeometry, starMaterial);
        constellations.add(starPoints);

        const lines = [
            [new THREE.Vector3(10,10,10), new THREE.Vector3(-10,10,10)],
            [new THREE.Vector3(0,-10,10), new THREE.Vector3(0,0,15)]
        ];
        lines.forEach(line => {
            const lineGeometry = new THREE.BufferGeometry().setFromPoints(line);
            const lineMaterial = new THREE.LineBasicMaterial({ color: 0xffffcc });
            const lineMesh = new THREE.Line(lineGeometry, lineMaterial);
            constellations.add(lineMesh);
        });
        scene.add(constellations);

        // State Management
        let currentView = 'land';
        const parameters = {
            time: 0,
            nodalAngle: 0,
            sunEarthAngle: Math.PI / 2,
            zodiac: 0
        };
        const checkboxes = {
            constellations: true,
            realisticSize: false
        };
        const isAnimating = {
            time: false,
            nodalAngle: false,
            sunEarthAngle: false,
            zodiac: false
        };

        // UI Event Listeners
        document.getElementById('btn-land').addEventListener('click', () => {
            currentView = 'land';
            updateCameraView();
            document.getElementById('btn-land').classList.add('active');
            document.getElementById('btn-space').classList.remove('active');
        });

        document.getElementById('btn-space').addEventListener('click', () => {
            currentView = 'space';
            updateCameraView();
            document.getElementById('btn-space').classList.add('active');
            document.getElementById('btn-land').classList.remove('active');
        });

        function updateCameraView() {
            if (currentView === 'land') {
                camera.position.set(0, 1, -5);
                camera.lookAt(0, 0, 0);
                moonSurface.visible = true;
                moonSphere.visible = false;
                earth.visible = false;
            } else {
                camera.position.set(0, 5, 10);
                camera.lookAt(0, 0, 0);
                moonSurface.visible = false;
                moonSphere.visible = true;
                earth.visible = true;
            }
            renderScene();
        }

        function setupSlider(id, param, displayId, decimalPlaces = 2) {
            const input = document.getElementById(id);
            const display = document.getElementById(displayId);
            input.addEventListener('input', (e) => {
                parameters[param] = parseFloat(e.target.value);
                display.textContent = parameters[param].toFixed(decimalPlaces);
                updateScene();
                renderScene();
            });
        }

        setupSlider('slider-time', 'time', 'slider-time-value');
        setupSlider('slider-nodal-angle', 'nodalAngle', 'slider-nodal-angle-value');
        setupSlider('slider-sun-earth-angle', 'sunEarthAngle', 'slider-sun-earth-angle-value', 3);
        setupSlider('slider-zodiac', 'zodiac', 'slider-zodiac-value');

        document.getElementById('checkbox-constellations').addEventListener('change', (e) => {
            checkboxes.constellations = e.target.checked;
            constellations.visible = checkboxes.constellations;
            renderScene();
        });

        document.getElementById('checkbox-realistic-size').addEventListener('change', (e) => {
            checkboxes.realisticSize = e.target.checked;
            earth.scale.set(
                checkboxes.realisticSize ? 0.1 : 1,
                checkboxes.realisticSize ? 0.1 : 1,
                checkboxes.realisticSize ? 0.1 : 1
            );
            renderScene();
        });

        function setupPlayButton(btnId, param) {
            document.getElementById(btnId).addEventListener('click', () => {
                isAnimating[param] = !isAnimating[param];
                document.getElementById(btnId).textContent = isAnimating[param] ? '-' : '+';
            });
        }

        setupPlayButton('btn-time-play', 'time');
        setupPlayButton('btn-nodal-angle-play', 'nodalAngle');
        setupPlayButton('btn-sun-earth-angle-play', 'sunEarthAngle');
        setupPlayButton('btn-zodiac-play', 'zodiac');

        // Animation Loop
        const clock = new THREE.Clock();

        function animate() {
            requestAnimationFrame(animate);
            const delta = clock.getDelta();

            // Update animated sliders
            Object.keys(isAnimating).forEach(param => {
                if (isAnimating[param]) {
                    parameters[param] += delta * 0.5;
                    if (parameters[param] > parseFloat(document.getElementById(`slider-${param}`).max)) {
                        parameters[param] = 0;
                    }
                    document.getElementById(`slider-${param}`).value = parameters[param];
                    document.getElementById(`slider-${param}-value`).textContent = 
                        param === 'sunEarthAngle' ? parameters[param].toFixed(3) : parameters[param].toFixed(2);
                }
            });

            updateScene();
            renderScene();
        }

        function updateScene() {
            // Update Earth position based on time and angles
            const earthOrbitRadius = 3;
            const angle = parameters.time * 0.1 + parameters.sunEarthAngle;
            earth.position.x = earthOrbitRadius * Math.cos(angle);
            earth.position.z = earthOrbitRadius * Math.sin(angle);

            // Rotate constellations based on zodiac
            constellations.rotation.y = parameters.zodiac;

            // Update nodal angle (tilt Earth's orbit)
            // Simplified example: tilt around X-axis
            earth.rotation.x = parameters.nodalAngle;
        }

        function renderScene() {
            renderer.render(scene, camera);
        }

        // Initial render
        renderScene();
        animate();

        // Handle window resize
        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
            renderScene();
        });
    </script>
</body>
</html>
