    <script>
        document.addEventListener('DOMContentLoaded', function() {
            // Initialize slider values and spans
            const sliders = [
                { id: 'slider-residence-time', defaultValue: 200 },
                { id: 'slider-transition-pace', defaultValue: 25 },
                { id: 'slider-wedges', defaultValue: 4 }
            ];

            sliders.forEach(slider => {
                const input = document.getElementById(slider.id);
                input.value = slider.defaultValue;
                const span = document.getElementById('value-' + slider.id.split('-')[1]);
                if (slider.id === 'slider-wedges') {
                    span.textContent = slider.defaultValue.toFixed(2);
                } else {
                    span.textContent = slider.defaultValue;
                }
            });

            // Add event listeners
            document.getElementById('slider-residence-time').addEventListener('input', function() {
                document.getElementById('value-residence-time').textContent = this.value;
                updatePlot();
            });

            document.getElementById('slider-transition-pace').addEventListener('input', function() {
                document.getElementById('value-transition-pace').textContent = this.value;
                updatePlot();
            });

            document.getElementById('slider-wedges').addEventListener('input', function() {
                document.getElementById('value-wedges').textContent = parseFloat(this.value).toFixed(2);
                updatePlot();
            });

            // Constants
            const C0 = 850;
            const E0 = 8.5;
            const C_eq = 586;
            const E_floor = 1.5;
            const rise_rate = 0.02;
            const time_step = 0.5;
            const max_time = 300;

            const layout = {
                title: 'Atmospheric carbon (GtC; blue) and annual emissions (GtC/yr × 100; purple) versus time',
                xaxis: { title: 'years', range: [0, 300], zeroline: true },
                yaxis: { title: 'GtC or GtC/yr × 100', range: [0, 1600], zeroline: true },
                showlegend: false,
                margin: { l: 60, r: 20, b: 40, t: 80 },
                paper_bgcolor: '#f9f9f9',
                plot_bgcolor: 'white',
                grid: { color: '#ddd' }
            };

            const targetTrace = {
                x: [0, 300],
                y: [1200, 1200],
                type: 'scatter',
                mode: 'lines',
                line: { color: 'black', width: 2, dash: 'dash' }
            };

            function updatePlot() {
                const t_res = parseFloat(document.getElementById('slider-residence-time').value);
                const t_trans = parseFloat(document.getElementById('slider-transition-pace').value);
                const N_wedges = parseFloat(document.getElementById('slider-wedges').value);

                const E_peak = E0 + rise_rate * t_trans;

                const times = [];
                for (let t = 0; t <= max_time; t += time_step) {
                    times.push(t);
                }

                const emissions = [];
                const carbon = [];
                let C_prev = C0;

                for (let i = 0; i < times.length; i++) {
                    const t = times[i];
                    let E;
                    if (t < t_trans) {
                        E = E0 + rise_rate * t;
                    } else {
                        const reduction_rate = N_wedges / 50;
                        const E_linear = E_peak - reduction_rate * (t - t_trans);
                        E = Math.max(E_floor, E_linear);
                    }
                    emissions.push(E);

                    if (i === 0) {
                        carbon.push(C_prev);
                    } else {
                        const dC = (E - (C_prev - C_eq)/t_res) * time_step;
                        const newC = C_prev + dC;
                        carbon.push(newC);
                        C_prev = newC;
                    }
                }

                const emissionY = emissions.map(e => e * 100);
                const carbonY = carbon;

                const trace1 = {
                    x: times,
                    y: carbonY,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue', width: 2 }
                };

                const trace2 = {
                    x: times,
                    y: emissionY,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'purple', width: 2 }
                };

                const traces = [trace1, trace2, targetTrace];

                Plotly.react('plot-div', traces, layout);
            }

            // Initial plot
            updatePlot();
        });
    </script>
</body>
</html>
