<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-group">
                <label for="slider-theta0">θ<sub>0</sub></label>
                <input type="range" id="slider-theta0" min="-3.14159" max="3.14159" step="0.01" value="0.5236">
                <span id="value-theta0">π/6</span>
            </div>
            <div class="slider-group">
                <label for="slider-v0">v<sub>0</sub></label>
                <input type="range" id="slider-v0" min="-5" max="5" step="0.1" value="0">
                <span id="value-v0">0</span>
            </div>
            <div class="slider-group">
                <label for="slider-length">connector length</label>
                <input type="range" id="slider-length" min="0.5" max="4" step="0.1" value="2">
                <span id="value-length">2</span>
            </div>
            <div class="animation-controls">
                <label>animate</label>
                <div>
                    <button id="btn-play-pause">▶</button>
                    <button id="btn-reset">|&lt;</button>
                </div>
            </div>
        </div>
        <div id="visualization"></div>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let isPlaying = false;
        let theta, theta_dot, x, x_dot;
        let currentTheta, currentV0, currentLength;

        function setup() {
            let canvas = createCanvas(500, 500);
            canvas.parent('visualization');
            resetSimulation();
        }

        function draw() {
            background('#F0F0F0');

            if (isPlaying) {
                const dt = 0.016;
                const m = 1, M = 1, g = 9.8;
                const l = currentLength;
                const sinTheta = Math.sin(theta);
                const cosTheta = Math.cos(theta);

                const numerator = -m * l * sinTheta * cosTheta * theta_dot**2 - g * (M + m) * sinTheta;
                const denominator = l * (M + m * sinTheta**2);
                const theta_ddot = numerator / denominator;

                theta_dot += theta_ddot * dt;
                theta += theta_dot * dt;

                x_dot = currentV0 - (m * l * cosTheta * theta_dot) / (M + m);
                x += x_dot * dt;
            }

            const scale = 50;
            const px = width / 2 + x * scale;
            const py = height / 2;
            const bx = px + currentLength * scale * Math.sin(theta);
            const by = py + currentLength * scale * Math.cos(theta);

            // Draw pivot
            fill('red');
            noStroke();
            rectMode(CENTER);
            rect(px, py, 20, 10);

            // Draw connector
            stroke('black');
            strokeWeight(2);
            line(px, py, bx, by);

            // Draw bob
            fill('orange');
            stroke('black');
            strokeWeight(2);
            ellipse(bx, by, 20, 20);
        }

        document.addEventListener('DOMContentLoaded', function() {
            const sliderTheta0 = document.getElementById('slider-theta0');
            const sliderV0 = document.getElementById('slider-v0');
            const sliderLength = document.getElementById('slider-length');
            const valueTheta0 = document.getElementById('value-theta0');
            const valueV0 = document.getElementById('value-v0');
            const valueLength = document.getElementById('value-length');
            const btnPlayPause = document.getElementById('btn-play-pause');
            const btnReset = document.getElementById('btn-reset');

            currentTheta = parseFloat(sliderTheta0.value);
            currentV0 = parseFloat(sliderV0.value);
            currentLength = parseFloat(sliderLength.value);

            valueTheta0.textContent = formatTheta(currentTheta);
            valueV0.textContent = currentV0.toFixed(1);
            valueLength.textContent = currentLength.toFixed(1);

            sliderTheta0.addEventListener('input', function() {
                currentTheta = parseFloat(this.value);
                valueTheta0.textContent = formatTheta(currentTheta);
                resetSimulation();
                isPlaying = false;
                btnPlayPause.textContent = '▶';
            });

            sliderV0.addEventListener('input', function() {
                currentV0 = parseFloat(this.value);
                valueV0.textContent = currentV0.toFixed(1);
                resetSimulation();
                isPlaying = false;
                btnPlayPause.textContent = '▶';
            });

            sliderLength.addEventListener('input', function() {
                currentLength = parseFloat(this.value);
                valueLength.textContent = currentLength.toFixed(1);
                resetSimulation();
                isPlaying = false;
                btnPlayPause.textContent = '▶';
            });

            btnPlayPause.addEventListener('click', function() {
                isPlaying = !isPlaying;
                this.textContent = isPlaying ? '||' : '▶';
            });

            btnReset.addEventListener('click', function() {
                resetSimulation();
                isPlaying = false;
                btnPlayPause.textContent = '▶';
            });
        });

        function resetSimulation() {
            theta = currentTheta;
            theta_dot = 0;
            x = 0;
            x_dot = currentV0;
        }

        function formatTheta(radians) {
            const pi = Math.PI;
            const epsilon = 0.01;
            const fractions = [
                { value: pi/6, label: 'π/6' },
                { value: pi/3, label: 'π/3' },
                { value: pi/2, label: 'π/2' },
                { value: 2*pi/3, label: '2π/3' },
                { value: 5*pi/6, label: '5π/6' },
                { value: pi, label: 'π' },
                { value: -pi/6, label: '-π/6' },
                { value: -pi/3, label: '-π/3' },
                { value: -pi/2, label: '-π/2' },
                { value: -2*pi/3, label: '-2π/3' },
                { value: -5*pi/6, label: '-5π/6' },
                { value: -pi, label: '-π' }
            ];

            for (let frac of fractions) {
                if (Math.abs(radians - frac.value) < epsilon) {
                    return frac.label;
                }
            }
            return radians.toFixed(2);
        }
    </script>
</body>
</html>
