    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/examples/js/controls/OrbitControls.js"></script>
    <script>
        // Scene setup
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(45, window.innerWidth / 600, 0.1, 100);
        camera.position.set(3, 3, 3);
        const renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('webgl-canvas'), antialias: true });
        renderer.setSize(window.innerWidth, 600);
        const controls = new THREE.OrbitControls(camera, renderer.domElement);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);
        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight.position.set(5, 5, 5).normalize();
        scene.add(directionalLight);

        // Sphere geometry
        const geometry = new THREE.SphereGeometry(1, 64, 32);
        const originalVertices = geometry.vertices.map(v => v.clone());
        const colors = new Array(geometry.vertices.length).fill(0);
        geometry.colors = colors;

        // Material
        const material = new THREE.MeshStandardMaterial({ vertexColors: THREE.VertexColors });
        const sphere = new THREE.Mesh(geometry, material);
        scene.add(sphere);

        // Box helper
        const boxHelper = new THREE.BoxHelper(sphere, 0x000000);
        boxHelper.visible = false;
        scene.add(boxHelper);

        // Compass rose
        const compassGroup = new THREE.Group();
        const compassCurve = new THREE.EllipseCurve(0, 0, 1.2, 1.2, 0, 2 * Math.PI);
        const compassPoints = compassCurve.getPoints(100);
        const compassGeometry = new THREE.BufferGeometry().setFromPoints(compassPoints);
        const compassMaterial = new THREE.LineDashedMaterial({ color: 0x000000, dashSize: 0.05, gapSize: 0.05 });
        const compass = new THREE.Line(compassGeometry, compassMaterial);
        compass.computeLineDistances();
        compassGroup.add(compass);

        // Compass labels
        const loader = new THREE.FontLoader();
        loader.load('https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/examples/fonts/helvetiker_regular.typeface.json', function (font) {
            const textMaterial = new THREE.MeshBasicMaterial({ color: 0x000000 });
            const addLabel = (text, position) => {
                const textGeometry = new THREE.TextGeometry(text, { font, size: 0.08, height: 0.02 });
                const textMesh = new THREE.Mesh(textGeometry, textMaterial);
                textMesh.position.copy(position);
                textMesh.rotation.x = -Math.PI / 2;
                compassGroup.add(textMesh);
            };
            addLabel('N', new THREE.Vector3(0, 0, 1.3));
            addLabel('E', new THREE.Vector3(1.3, 0, 0));
            addLabel('S', new THREE.Vector3(0, 0, -1.3));
            addLabel('W', new THREE.Vector3(-1.3, 0, 0));
        });
        scene.add(compassGroup);

        // Axis labels
        const axisLabels = { P: null, T: null, U: null };
        const updateAxisLabels = (P, T, U) => {
            const fontLoader = new THREE.FontLoader();
            fontLoader.load('https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/examples/fonts/helvetiker_regular.typeface.json', function (font) {
                const textMaterial = new THREE.MeshBasicMaterial({ color: 0x000000 });
                const createLabel = (text, position) => {
                    const textGeometry = new THREE.TextGeometry(text, { font, size: 0.1, height: 0.02 });
                    const textMesh = new THREE.Mesh(textGeometry, textMaterial);
                    textMesh.position.copy(position);
                    textMesh.rotation.x = -Math.PI / 2;
                    return textMesh;
                };
                if (axisLabels.P) scene.remove(axisLabels.P);
                if (axisLabels.T) scene.remove(axisLabels.T);
                if (axisLabels.U) scene.remove(axisLabels.U);
                axisLabels.P = createLabel('P', P.clone().multiplyScalar(1.1));
                axisLabels.T = createLabel('T', T.clone().multiplyScalar(1.1));
                axisLabels.U = createLabel('U', U.clone().multiplyScalar(1.1));
                scene.add(axisLabels.P, axisLabels.T, axisLabels.U);
            });
        };

        // Default parameters
        let params = {
            strike: 180,
            dip: 60,
            slipAngle: 90,
            slipAmount: 0.5
        };

        // Vector calculations
        const calculateVectors = () => {
            const rad = THREE.MathUtils.degToRad;
            const φ = rad(params.strike);
            const δ = rad(params.dip);
            const λ = rad(params.slipAngle);

            // Fault normal vector (n)
            const nx = Math.sin(δ) * Math.sin(φ);
            const ny = Math.sin(δ) * Math.cos(φ);
            const nz = -Math.cos(δ);
            const n = new THREE.Vector3(nx, ny, nz).normalize();

            // Slip vector (u)
            const ux = Math.cos(λ) * Math.cos(φ) + Math.sin(λ) * Math.cos(δ) * Math.sin(φ);
            const uy = -Math.cos(λ) * Math.sin(φ) + Math.sin(λ) * Math.cos(δ) * Math.cos(φ);
            const uz = Math.sin(λ) * Math.sin(δ);
            const u = new THREE.Vector3(ux, uy, uz).normalize();

            // P and T axes
            const P = n.clone().add(u).normalize();
            const T = u.clone().sub(n).normalize();
            const U = n.clone().cross(u).normalize();

            return { n, u, P, T, U };
        };

        // Update visualization
        const updateVisualization = () => {
            const { P, T, U } = calculateVectors();
            const slipAmount = params.slipAmount;
            const deformationScale = 0.3;

            // Update colors and deformation
            geometry.vertices.forEach((v, i) => {
                const dotP = v.dot(P);
                const dotT = v.dot(T);
                const isCompressive = (dotT * dotT - dotP * dotP) < 0;
                colors[i] = isCompressive ? 0xffffff : 0xffa500;

                // Deformation
                const deformation = deformationScale * slipAmount * (dotT * dotT - dotP * dotP);
                v.copy(originalVertices[i]).addScaledVector(originalVertices[i], deformation);
            });

            geometry.colorsNeedUpdate = true;
            geometry.verticesNeedUpdate = true;
            updateAxisLabels(P, T, U);
            boxHelper.update();
        };

        // Event listeners
        const sliders = {
            'slider-strike': 'strike',
            'slider-dip': 'dip',
            'slider-slip-angle': 'slipAngle',
            'slider-slip-amount': 'slipAmount'
        };

        Object.keys(sliders).forEach(id => {
            const slider = document.getElementById(id);
            const valueDisplay = document.getElementById(`value-${sliders[id]}`);
            slider.addEventListener('input', () => {
                valueDisplay.value = slider.value;
                params[sliders[id]] = parseFloat(slider.value);
                updateVisualization();
            });
        });

        document.getElementById('checkbox-box').addEventListener('change', (e) => {
            boxHelper.visible = e.target.checked;
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            params = { strike: 180, dip: 60, slipAngle: 90, slipAmount: 0.5 };
            Object.keys(sliders).forEach(id => {
                const slider = document.getElementById(id);
                const valueDisplay = document.getElementById(`value-${sliders[id]}`);
                slider.value = params[sliders[id]];
                valueDisplay.value = params[sliders[id]];
            });
            updateVisualization();
        });

        // Initial update
        updateVisualization();

        // Animation loop
        const animate = () => {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        };
        animate();

        // Handle window resize
        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / 600;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, 600);
        });
    </script>
</body>
</html>
