<head>
  <meta charset="UTF-8">
  <title>Grover's Algorithm Visualization</title>
  <style>
    body {
      font-family: Arial, sans-serif;
      margin: 0;
      padding: 0;
      display: flex;
      justify-content: center;
      background-color: #f5f5f5;
    }
    #app-container {
      border: 1px solid #ccc;
      padding: 20px;
      margin: 20px auto;
      max-width: 800px;
      background-color: white;
      box-shadow: 0 0 10px rgba(0,0,0,0.1);
    }
    #control-panel {
      display: flex;
      justify-content: space-between;
      margin-bottom: 20px;
    }
    .control-group {
      display: flex;
      align-items: center;
    }
    .control-group label {
      margin-right: 10px;
    }
    .control-group input {
      margin-right: 10px;
    }
    .value-display {
      width: 50px;
      text-align: center;
    }
    #probability-display {
      margin-top: 20px;
      font-size: 1.2em;
      text-align: center;
    }
  </style>
</head>
<body>
  <div id="app-container">
    <div id="control-panel">
      <div class="control-group">
        <label for="slider-db-size">database size</label>
        <input type="range" id="slider-db-size" min="8" max="100" value="10" step="1">
        <div class="value-display">+<span id="display-db-size">10</span></div>
      </div>
      <div class="control-group">
        <label for="slider-iterations">number of iterations</label>
        <input type="range" id="slider-iterations" min="0" max="20" value="2" step="1">
        <div class="value-display">+<span id="display-iterations">2</span></div>
      </div>
    </div>
    <div id="canvas-container"></div>
    <div id="probability-display"></div>
  </div>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
  <script>
    function setup() {
      const canvas = createCanvas(800, 600);
      canvas.parent('canvas-container');
    }

    function draw() {
      const N = parseInt(document.getElementById('slider-db-size').value);
      const k = parseInt(document.getElementById('slider-iterations').value);

      // Update display elements
      document.getElementById('display-db-size').textContent = N;
      document.getElementById('display-iterations').textContent = k;

      // Calculate probability
      const probability = Math.pow(Math.sin((2 * k + 1) * Math.asin(1 / Math.sqrt(N))), 2) * 100;
      document.getElementById('probability-display').textContent = `${probability.toFixed(1)}% success probability`;

      // Clear canvas
      background(255);

      // Draw four stages
      const stages = [
        computeInitialAmplitudes(N),
        computeAfterInversion(N),
        computeAfterDiffusion(N),
        computeAfterKIterations(N, k)
      ];

      const sectionHeight = height / 4;
      for (let i = 0; i < 4; i++) {
        const y = i * sectionHeight;
        drawStage(stages[i], i, y, sectionHeight, N);
      }
    }

    function computeInitialAmplitudes(N) {
      const amplitude = 1 / Math.sqrt(N);
      return new Array(N).fill(amplitude);
    }

    function computeAfterInversion(N) {
      const amps = computeInitialAmplitudes(N);
      const target = 6;
      amps[target] *= -1;
      return amps;
    }

    function computeAfterDiffusion(N) {
      const amps = computeAfterInversion(N);
      const mean = amps.reduce((a, b) => a + b, 0) / N;
      return amps.map(a => 2 * mean - a);
    }

    function computeAfterKIterations(N, k) {
      let amps = computeInitialAmplitudes(N);
      const target = 6;
      for (let i = 0; i < k; i++) {
        // Inversion
        amps[target] *= -1;
        // Diffusion
        const mean = amps.reduce((a, b) => a + b, 0) / N;
        amps = amps.map(a => 2 * mean - a);
      }
      return amps;
    }

    function drawStage(amps, stageIndex, y, sectionHeight, N) {
      const title = ['Original Amplitudes', 'After First Inversion', 'After First Diffusion', 'After k Iterations'][stageIndex];
      fill(0);
      textAlign(CENTER, BOTTOM);
      text(title, width/2, y - 5);

      const margin = 50;
      const barWidth = (width - 2 * margin) / N;
      const target = 6;

      // Draw baseline
      stroke(0);
      line(margin, y + sectionHeight/2, width - margin, y + sectionHeight/2);

      // Draw bars
      amps.forEach((amp, index) => {
        const x = margin + index * barWidth + barWidth/2;
        const barHeight = amp * 40; // scale factor
        const actualY = y + sectionHeight/2 - barHeight;
        fill(index === target ? '#FFA500' : '#808080');
        noStroke();
        rect(x - barWidth/2, actualY, barWidth, barHeight);
      });

      // Draw parentheses for stage 0
      if (stageIndex === 0) {
        const leftX = margin - 20;
        const rightX = width - margin + 20;
        const centerY = y + sectionHeight/2;
        // Left parenthesis: arc from top to bottom, left side
        arc(leftX, centerY, 20, sectionHeight, PI/2, -PI/2);
        // Right parenthesis: arc from bottom to top, right side
        arc(rightX, centerY, 20, sectionHeight, -PI/2, PI/2);
      }
    }
  </script>
</body>
</html>
