    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-volatility">relative volatility</label>
                <input type="range" id="slider-volatility" min="1.1" max="10" step="0.1" value="2.5">
                <button id="btn-volatility-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-distillate">distillate specification</label>
                <input type="range" id="slider-distillate" min="0.51" max="0.99" step="0.01" value="0.95">
                <button id="btn-distillate-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-bottom">bottom specification</label>
                <input type="range" id="slider-bottom" min="0.01" max="0.49" step="0.01" value="0.05">
                <button id="btn-bottom-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-feed-comp">feed composition</label>
                <input type="range" id="slider-feed-comp" min="0.02" max="0.98" step="0.01" value="0.50">
                <button id="btn-feed-comp-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-feed-quality">feed quality</label>
                <input type="range" id="slider-feed-quality" min="0.0" max="2.0" step="0.05" value="1.0">
                <button id="btn-feed-quality-plus">+</button>
            </div>
        </div>
        <div id="visualization-area">
            <p id="stages-count-display">The number of stages is 8.</p>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script>
        function setup() {
            const canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');
            noLoop();
            initializeSliders();
            drawDiagram();
        }

        function drawDiagram() {
            const alpha = parseFloat(document.getElementById('slider-volatility').value);
            const xD = parseFloat(document.getElementById('slider-distillate').value);
            const xB = parseFloat(document.getElementById('slider-bottom').value);
            const xF = parseFloat(document.getElementById('slider-feed-comp').value);
            const q = parseFloat(document.getElementById('slider-feed-quality').value);

            background(255);
            const margin = 50;
            const plotWidth = width - 2 * margin;
            const plotHeight = height - 2 * margin;

            // Draw axes
            stroke('#808080');
            line(margin, height - margin, width - margin, height - margin);
            line(margin, margin, margin, height - margin);

            // Draw tick marks and labels
            for (let i = 0; i <= 5; i++) {
                const val = i * 0.2;
                const x = map(val, 0, 1, margin, width - margin);
                const y = map(val, 0, 1, height - margin, margin);
                line(x, height - margin, x, height - margin + 5);
                line(margin, y, margin - 5, y);
                textSize(10);
                fill('#808080');
                text(val.toFixed(1), x - 10, height - margin + 20);
                text(val.toFixed(1), margin - 30, y + 5);
            }

            // Draw equilibrium curve
            stroke('#FF0000');
            noFill();
            beginShape();
            for (let x = 0; x <= 1; x += 0.01) {
                const y = (alpha * x) / (1 + (alpha - 1) * x);
                const px = map(x, 0, 1, margin, width - margin);
                const py = map(y, 0, 1, height - margin, margin);
                vertex(px, py);
            }
            endShape();

            // Draw diagonal line
            stroke('#228B22');
            const x0 = map(0, 0, 1, margin, width - margin);
            const y0 = map(0, 0, 1, height - margin, margin);
            const x1 = map(1, 0, 1, margin, width - margin);
            const y1 = map(1, 0, 1, height - margin, margin);
            line(x0, y0, x1, y1);

            // Calculate intersection point
            let x_int, y_int;
            if (q === 1) {
                x_int = xF;
                y_int = (alpha * xF) / (1 + (alpha - 1) * xF);
            } else if (q === 0) {
                y_int = xF;
                x_int = y_int / (alpha - (alpha - 1) * y_int);
            } else {
                const m = q / (q - 1);
                const A = m * (alpha - 1);
                const B = m - m * (alpha - 1) * xF + xF * (alpha - 1) - alpha;
                const C = -m * xF + xF;
                const discriminant = B * B - 4 * A * C;
                if (discriminant < 0) {
                    x_int = xF;
                    y_int = (alpha * xF) / (1 + (alpha - 1) * xF);
                } else {
                    const sqrtD = Math.sqrt(discriminant);
                    const x1 = (-B + sqrtD) / (2 * A);
                    const x2 = (-B - sqrtD) / (2 * A);
                    x_int = (x1 >= 0 && x1 <= 1 && Math.abs(x1 - xF) > 0.001) ? x1 : x2;
                    if (x_int < 0 || x_int > 1 || Math.abs(x_int - xF) < 0.001) x_int = xF;
                    y_int = (alpha * x_int) / (1 + (alpha - 1) * x_int);
                }
            }

            // Draw feed line
            stroke('#0000CD');
            const xF_mapped = map(xF, 0, 1, margin, width - margin);
            const yF_mapped = map(xF, 0, 1, height - margin, margin);
            const x_int_mapped = map(x_int, 0, 1, margin, width - margin);
            const y_int_mapped = map(y_int, 0, 1, height - margin, margin);
            if (q === 1) {
                line(xF_mapped, height - margin, xF_mapped, margin);
            } else if (q === 0) {
                line(margin, yF_mapped, width - margin, yF_mapped);
            } else {
                line(xF_mapped, yF_mapped, x_int_mapped, y_int_mapped);
            }

            // Draw rectifying line
            stroke('#00FFFF');
            const xD_mapped = map(xD, 0, 1, margin, width - margin);
            const yD_mapped = map(xD, 0, 1, height - margin, margin);
            line(xD_mapped, yD_mapped, x_int_mapped, y_int_mapped);

            // Draw stripping line
            stroke('#FF00FF');
            const xB_mapped = map(xB, 0, 1, margin, width - margin);
            const yB_mapped = map(xB, 0, 1, height - margin, margin);
            line(xB_mapped, yB_mapped, x_int_mapped, y_int_mapped);

            // Calculate and draw stages
            stroke('#000000');
            let stage_count = 0;
            let x_current = xD;
            let y_current = xD;

            const equilibriumX = (y) => y / (alpha - (alpha - 1) * y);
            const rectifyingY = (x) => {
                const slope = (y_int - xD) / (x_int - xD);
                return slope * (x - xD) + xD;
            };
            const strippingY = (x) => {
                const slope = (y_int - xB) / (x_int - xB);
                return slope * (x - xB) + xB;
            };

            while (true) {
                const x_new = equilibriumX(y_current);
                const x_new_mapped = map(x_new, 0, 1, margin, width - margin);
                const y_current_mapped = map(y_current, 0, 1, height - margin, margin);
                line(map(x_current, 0, 1, margin, width - margin), y_current_mapped, x_new_mapped, y_current_mapped);
                x_current = x_new;

                if (x_current <= xB) {
                    stage_count += 1;
                    break;
                }

                stage_count += 1;

                const y_new = (x_current > x_int) ? rectifyingY(x_current) : strippingY(x_current);
                const y_new_mapped = map(y_new, 0, 1, height - margin, margin);
                line(x_new_mapped, y_current_mapped, x_new_mapped, y_new_mapped);
                y_current = y_new;
            }

            document.getElementById('stages-count-display').textContent = `The number of stages is ${stage_count}.`;
        }

        function initializeSliders() {
            const sliders = [
                { id: 'slider-volatility', min: 1.1, max: 10, step: 0.1, default: 2.5 },
                { id: 'slider-distillate', min: 0.51, max: 0.99, step: 0.01, default: 0.95 },
                { id: 'slider-bottom', min: 0.01, max: 0.49, step: 0.01, default: 0.05 },
                { id: 'slider-feed-comp', min: 0.02, max: 0.98, step: 0.01, default: 0.50 },
                { id: 'slider-feed-quality', min: 0.0, max: 2.0, step: 0.05, default: 1.0 }
            ];

            sliders.forEach(slider => {
                const el = document.getElementById(slider.id);
                el.min = slider.min;
                el.max = slider.max;
                el.step = slider.step;
                el.value = slider.default;
            });

            updateFeedConstraints();

            sliders.forEach(slider => {
                document.getElementById(slider.id).addEventListener('input', () => {
                    enforceConstraints();
                    updateFeedConstraints();
                    redraw();
                });
            });

            document.getElementById('btn-volatility-plus').addEventListener('click', () => adjustSlider('slider-volatility', 1));
            document.getElementById('btn-distillate-plus').addEventListener('click', () => adjustSlider('slider-distillate', 1));
            document.getElementById('btn-bottom-plus').addEventListener('click', () => adjustSlider('slider-bottom', 1));
            document.getElementById('btn-feed-comp-plus').addEventListener('click', () => adjustSlider('slider-feed-comp', 1));
            document.getElementById('btn-feed-quality-plus').addEventListener('click', () => adjustSlider('slider-feed-quality', 1));
        }

        function enforceConstraints() {
            const xB = parseFloat(document.getElementById('slider-bottom').value);
            const xD = parseFloat(document.getElementById('slider-distillate').value);
            const xFSlider = document.getElementById('slider-feed-comp');
            let xF = parseFloat(xFSlider.value);

            if (xB >= xF) {
                xFSlider.value = (xB + 0.01).toFixed(2);
            }
            if (xD <= xF) {
                xFSlider.value = (xD - 0.01).toFixed(2);
            }
        }

        function updateFeedConstraints() {
            const xB = parseFloat(document.getElementById('slider-bottom').value);
            const xD = parseFloat(document.getElementById('slider-distillate').value);
            const xFSlider = document.getElementById('slider-feed-comp');
            xFSlider.min = (xB + 0.01).toFixed(2);
            xFSlider.max = (xD - 0.01).toFixed(2);

            let xF = parseFloat(xFSlider.value);
            if (xF < parseFloat(xFSlider.min)) xFSlider.value = xFSlider.min;
            if (xF > parseFloat(xFSlider.max)) xFSlider.value = xFSlider.max;
        }

        function adjustSlider(id, direction) {
            const slider = document.getElementById(id);
            const step = parseFloat(slider.step);
            slider.value = (parseFloat(slider.value) + direction * step).toFixed(2);
            slider.dispatchEvent(new Event('input'));
        }
    </script>
</body>
</html>
