<body>
    <div id="demo-container">
        <div id="control-panel">
            <label for="slider-month">month</label>
            <input type="range" id="slider-month" min="0" max="11" step="1">
            <label for="perspective-buttons">perspective</label>
            <div id="perspective-buttons">
                <button id="btn-2d" class="active">2D</button>
                <button id="btn-3d">3D</button>
            </div>
        </div>
        <div id="visualization-area">
            <canvas id="main-canvas"></canvas>
        </div>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        let currentView = '2d';
        let currentMonth = new Date().getMonth();
        let slider = document.getElementById('slider-month');
        slider.value = currentMonth;
        let p5Instance = null;
        let threeScene, threeCamera, threeRenderer, threeEarth, threeMagnetosphere, threeControls;

        document.getElementById('btn-2d').addEventListener('click', () => {
            if (currentView === '3d') {
                switchTo2D();
            }
        });
        document.getElementById('btn-3d').addEventListener('click', () => {
            if (currentView === '2d') {
                switchTo3D();
            }
        });
        slider.addEventListener('input', () => {
            currentMonth = parseInt(slider.value);
            if (currentView === '2d') {
                update2D();
            } else {
                update3D();
            }
        });

        function switchTo2D() {
            document.getElementById('main-canvas').remove();
            const visArea = document.getElementById('visualization-area');
            const newCanvas = document.createElement('canvas');
            newCanvas.id = 'main-canvas';
            visArea.appendChild(newCanvas);
            init2D();
            currentView = '2d';
            document.getElementById('btn-2d').classList.add('active');
            document.getElementById('btn-3d').classList.remove('active');
        }

        function switchTo3D() {
            document.getElementById('main-canvas').remove();
            const visArea = document.getElementById('visualization-area');
            const newCanvas = document.createElement('canvas');
            newCanvas.id = 'main-canvas';
            visArea.appendChild(newCanvas);
            init3D();
            currentView = '3d';
            document.getElementById('btn-3d').classList.add('active');
            document.getElementById('btn-2d').classList.remove('active');
        }

        function init2D() {
            p5Instance = new p5(sketch => {
                sketch.setup = () => {
                    const canvas = sketch.createCanvas(800, 600);
                    canvas.parent('visualization-area');
                    sketch.angleMode(sketch.DEGREES);
                    sketch.noLoop();
                };

                sketch.draw = () => {
                    draw2D(sketch);
                };
            });
        }

        function draw2D(p) {
            const month = currentMonth;
            const tilt = 23.5 * Math.cos(2 * Math.PI * month / 12);
            p.background(0);

            // Solar Wind
            p.stroke(153, 0, 0);
            p.strokeWeight(1);
            for (let i = -50; i <= 50; i += 10) {
                p.line(0, i, 200, i);
                p.line(200, i, 195, i - 5);
                p.line(200, i, 195, i + 5);
            }

            p.push();
            p.translate(p.width / 2, p.height / 2);
            p.rotate(tilt);

            // Bow Shock
            p.noFill();
            p.stroke(255, 165, 0);
            p.ellipse(0, 0, 300, 200);

            // Magnetopause
            p.stroke(0, 255, 255);
            p.ellipse(0, 0, 250, 150);

            // Earth
            p.fill(70, 105, 230);
            p.ellipse(0, 0, 100, 100);
            p.fill(34, 139, 34);
            p.rect(-20, -30, 40, 20);
            p.triangle(-10, 0, 10, 0, 0, 20);

            // Van Allen Belts
            p.noStroke();
            p.fill(0, 100, 0, 150);
            p.arc(0, 0, 120, 120, 0, 180, p.OPEN);
            p.fill(50, 205, 50, 150);
            p.arc(0, 0, 140, 140, 0, 180, p.OPEN);

            // Polar Cusps
            p.fill(240, 230, 130);
            p.triangle(-50, 0, -30, 20, -30, -20);
            p.triangle(50, 0, 30, 20, 30, -20);

            // Magnetotail
            p.fill(0, 0, 139, 100);
            p.beginShape();
            p.vertex(50, -50);
            p.vertex(200, -100);
            p.vertex(200, 100);
            p.vertex(50, 50);
            p.endShape(p.CLOSE);

            // Magnetic Field Lines
            p.stroke(128, 128, 128);
            p.noFill();
            for (let i = 10; i <= 50; i += 10) {
                p.ellipse(0, 0, i * 2, i * 2);
            }
            for (let i = 0; i < 5; i++) {
                p.beginShape();
                p.vertex(50, i * 15);
                p.bezierVertex(100, i * 15, 150, i * 15 + 30, 200, i * 15 + 60);
                p.endShape();
            }

            p.pop();
        }

        function update2D() {
            if (p5Instance) {
                p5Instance.redraw();
            }
        }

        function init3D() {
            const canvas = document.getElementById('main-canvas');
            threeScene = new THREE.Scene();
            threeCamera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            threeRenderer = new THREE.WebGLRenderer({ canvas: canvas });
            threeRenderer.setSize(window.innerWidth, window.innerHeight);
            document.getElementById('visualization-area').appendChild(threeRenderer.domElement);

            // Lights
            const ambientLight = new THREE.AmbientLight(0x404040);
            threeScene.add(ambientLight);
            const sunLight = new THREE.PointLight(0xffeecc, 1, 100);
            sunLight.position.set(0, 0, 0);
            threeScene.add(sunLight);

            // Sun
            const sunGeometry = new THREE.SphereGeometry(5, 32, 32);
            const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
            const sun = new THREE.Mesh(sunGeometry, sunMaterial);
            threeScene.add(sun);

            // Earth's Orbit
            const orbitRadius = 30;
            const curve = new THREE.EllipseCurve(0, 0, orbitRadius, orbitRadius, 0, 2 * Math.PI, false, 0);
            const points = curve.getPoints(100);
            const orbitGeometry = new THREE.BufferGeometry().setFromPoints(points);
            const orbitMaterial = new THREE.LineBasicMaterial({ color: 0xffffff });
            const orbit = new THREE.Line(orbitGeometry, orbitMaterial);
            threeScene.add(orbit);

            // Earth
            const earthGeometry = new THREE.SphereGeometry(2, 32, 32);
            const earthMaterial = new THREE.MeshStandardMaterial({ color: 0x4169e1 });
            threeEarth = new THREE.Mesh(earthGeometry, earthMaterial);
            threeScene.add(threeEarth);

            // Earth's Axis
            const axisDirection = new THREE.Vector3(0, 0, 1).applyAxisAngle(new THREE.Vector3(1, 0, 0), THREE.MathUtils.degToRad(23.5));
            const axis = new THREE.ArrowHelper(axisDirection, new THREE.Vector3(0, 0, 0), 3, 0x00ff00);
            threeEarth.add(axis);

            // Solar Wind
            for (let i = 0; i < 10; i++) {
                const direction = new THREE.Vector3(Math.random() - 0.5, Math.random() - 0.5, Math.random() - 0.5).normalize();
                const arrow = new THREE.ArrowHelper(direction, new THREE.Vector3(0, 0, 0), 5, 0xff0000);
                threeScene.add(arrow);
            }

            // Magnetosphere
            const profile = [];
            for (let i = 0; i < 100; i++) {
                const angle = i / 100 * Math.PI * 2;
                const r = 5 + 3 * Math.cos(angle * 2);
                profile.push(new THREE.Vector2(r * Math.cos(angle), r * Math.sin(angle)));
            }
            const latheGeometry = new THREE.LatheGeometry(profile);
            const magnetosphereMaterial = new THREE.MeshStandardMaterial({
                color: 0x0000ff,
                transparent: true,
                opacity: 0.4
            });
            threeMagnetosphere = new THREE.Mesh(latheGeometry, magnetosphereMaterial);
            threeEarth.add(threeMagnetosphere);

            // Camera
            threeCamera.position.z = 50;
            threeControls = new THREE.OrbitControls(threeCamera, threeRenderer.domElement);
            threeControls.update();

            // Initial Update
            update3D();
            animate();
        }

        function update3D() {
            const month = currentMonth;
            const theta = month * (2 * Math.PI / 12);
            const orbitRadius = 30;
            const earthX = orbitRadius * Math.cos(theta);
            const earthY = orbitRadius * Math.sin(theta);
            threeEarth.position.set(earthX, earthY, 0);

            // Update magnetosphere orientation
            const direction = new THREE.Vector3(earthX, earthY, 0).normalize();
            threeMagnetosphere.lookAt(direction);
            threeMagnetosphere.rotateX(Math.PI / 2);
        }

        function animate() {
            requestAnimationFrame(animate);
            threeControls.update();
            threeRenderer.render(threeScene, threeCamera);
        }

        // Initial load
        window.addEventListener('load', () => {
            if (currentView === '2d') {
                init2D();
            } else {
                init3D();
            }
        });

        // Handle window resize
        window.addEventListener('resize', () => {
            if (currentView === '3d') {
                threeCamera.aspect = window.innerWidth / window.innerHeight;
                threeCamera.updateProjectionMatrix();
                threeRenderer.setSize(window.innerWidth, window.innerHeight);
            }
        });
    </script>
</body>
</html>
