<body>
    <div id="main-container">
        <div class="control-panel">
            <div class="control-group">
                <label for="slider-alpha">parameter α</label>
                <input type="range" id="slider-alpha" min="0.1" max="5.0" step="0.005" value="1.825">
                <span id="display-alpha">1.825</span>
            </div>
            <div class="control-group">
                <label for="slider-beta">parameter β</label>
                <input type="range" id="slider-beta" min="0.1" max="5.0" step="0.005" value="1.945">
                <span id="display-beta">1.945</span>
            </div>
        </div>
        <div id="plot-title">
            <span>density function</span>
            <span>and</span>
            <span>distribution function</span>
        </div>
        <div id="plot-container"></div>
        <div id="stats-display">
            <p>Mean: <span id="mean-value">3.550</span></p>
            <p>Variance: <span id="variance-value">6.904</span></p>
            <p>Moment Generating Function: <span id="mgf-formula"></span></p>
        </div>
    </div>
    <script>
        function gamma(z) {
            const g = 7;
            const p = [
                0.99999999999980993,
                676.5203681218851,
                -1259.1392167224028,
                771.32342877765313,
                -176.61502916214059,
                12.507343278686905,
                -0.13857109526572012,
                9.9843695780195716e-6,
                1.5056327351493116e-7
            ];
            if (z < 0.5) return Math.PI / (Math.sin(Math.PI * z) * gamma(1 - z));
            z -= 1;
            let x = p[0];
            for (let i = 1; i < g + 2; i++) {
                x += p[i] / (z + i);
            }
            let t = z + g + 0.5;
            return Math.sqrt(2 * Math.PI) * Math.pow(t, z + 0.5) * Math.exp(-t) * x;
        }

        function gammaPDF(x, alpha, beta) {
            if (x <= 0) return 0;
            return (Math.pow(x, alpha - 1) * Math.exp(-x / beta)) / (Math.pow(beta, alpha) * gamma(alpha));
        }

        function computePDFCDF(alpha, beta, xRange) {
            const pdf = [];
            const cdf = [];
            const dx = xRange[1] - xRange[0];
            let sum = 0;
            for (let i = 0; i < xRange.length; i++) {
                const x = xRange[i];
                const y = gammaPDF(x, alpha, beta);
                pdf.push(y);
                if (i === 0) {
                    sum += y * dx / 2;
                } else {
                    sum += (pdf[i-1] + y) / 2 * dx;
                }
                cdf.push(sum);
            }
            return { pdf, cdf };
        }

        function updateVisualization() {
            const alpha = parseFloat(document.getElementById('slider-alpha').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);

            document.getElementById('display-alpha').textContent = alpha.toFixed(3);
            document.getElementById('display-beta').textContent = beta.toFixed(3);

            const mean = alpha * beta;
            const variance = alpha * Math.pow(beta, 2);
            const meanY = gammaPDF(mean, alpha, beta);

            document.getElementById('mean-value').textContent = mean.toFixed(3);
            document.getElementById('variance-value').textContent = variance.toFixed(3);

            const mgfSpan = document.getElementById('mgf-formula');
            mgfSpan.innerHTML = `$$ (1 - \\beta t)^{-\\alpha} = \\frac{1}{(1 - ${beta.toFixed(3)} t)^{${alpha.toFixed(3)}}} $$`;
            MathJax.typeset();

            const xRange = [];
            for (let x = 0; x <= 25; x += 0.1) {
                xRange.push(x);
            }

            const { pdf, cdf } = computePDFCDF(alpha, beta, xRange);

            const plotData = [
                {
                    x: xRange,
                    y: pdf,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue', width: 2.5 },
                    name: 'PDF'
                },
                {
                    x: xRange,
                    y: cdf,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'orange', width: 2.5 },
                    name: 'CDF'
                }
            ];

            const layout = {
                xaxis: { range: [0, 25], title: '' },
                yaxis: { range: [0, 1.1], title: '' },
                showlegend: false,
                margin: { t: 40, b: 40, l: 40, r: 40 },
                shapes: [{
                    type: 'line',
                    x0: mean,
                    x1: mean,
                    y0: 0,
                    y1: meanY,
                    line: { color: 'red', width: 3 }
                }]
            };

            Plotly.react('plot-container', plotData, layout);
        }

        document.getElementById('slider-alpha').addEventListener('input', updateVisualization);
        document.getElementById('slider-beta').addEventListener('input', updateVisualization);

        updateVisualization();
    </script>
</body>
</html>
