        let isDragging = false;
        let azeotropes;
        let distillationLine = [];

        function setup() {
            createCanvas(650, 650);
            locator = { x: 0.2, y: 0.22 };
            azeotropes = [
                { name: 'A₁', x: 0.655, y: 0 },
                { name: 'A₂', x: 0, y: 0.795 },
                { name: 'A₃', x: 0.605, y: 0.345 },
                { name: 'A₄', x: 0.26, y: 0.335 }
            ];
            distillationLine = computeDistillationLine();
        }

        function draw() {
            background(255);
            drawAxes();
            drawAzeotropes();
            drawDistillationLine();
            drawLocator();
        }

        function drawAxes() {
            stroke(0);
            strokeWeight(1);
            // X-axis
            line(60, 590, 610, 590);
            // Y-axis
            line(60, 590, 60, 60);
            // Hypotenuse
            line(610, 590, 60, 60);

            // Ticks and labels
            for (let i = 0; i <= 5; i++) {
                const val = i * 0.2;
                const px = 60 + val * 530;
                // X-axis ticks
                line(px, 590, px, 590 - 10);
                text(val.toFixed(1), px, 600);
                // Y-axis
                const py = 60 + (1 - val) * 530;
                line(60, py, 60 - 10, py);
                text(val.toFixed(1), 50, py + 5);
            }

            // Component labels
            fill(255, 0, 0);
            text('methanol', 60, 590 + 20);
            text('chloroform', 610 - 60, 590 + 20);
            text('acetone', 60 - 20, 60 - 10);
        }

        function drawAzeotropes() {
            fill(153, 0, 0);
            noStroke();
            for (let az of azeotropes) {
                const {x, y} = dataToPixel(az.x, az.y);
                ellipse(x, y, 12);
                fill(0);
                text(az.name, x + 10, y - 10);
                fill(153, 0, 0);
            }
        }

        function drawDistillationLine() {
            if (distillationLine.length < 2) return;
            stroke('#0000FF');
            strokeWeight(2);
            noFill();
            beginShape();
            for (let pt of distillationLine) {
                const {x, y} = dataToPixel(pt.x, pt.y);
                curveVertex(x, y);
            }
            endShape();
        }

        function drawLocator() {
            const {x, y} = dataToPixel(locator.x, locator.y);
            fill(255);
            stroke(0);
            strokeWeight(2);
            ellipse(x, y, 16);
        }

        function mousePressed() {
            const locPixel = dataToPixel(locator.x, locator.y);
            const d = dist(mouseX, mouseY, locPixel.x, locPixel.y);
            if (d < 15) {
                isDragging = true;
            }
        }

        function mouseDragged() {
            if (isDragging) {
                const mouseData = pixelToData(mouseX, mouseY);
                const A2 = azeotropes[1];
                const A4 = azeotropes[3];
                const A1 = azeotropes[0];
                const seg1Closest = closestPointOnSegment(mouseData, A2, A4);
                const seg2Closest = closestPointOnSegment(mouseData, A4, A1);
                const d1 = distSq(mouseData, seg1Closest);
                const d2 = distSq(mouseData, seg2Closest);
                if (d1 < d2) {
                    locator.x = seg1Closest.x;
                    locator.y = seg1Closest.y;
                } else {
                    locator.x = seg2Closest.x;
                    locator.y = seg2Closest.y;
                }
                distillationLine = computeDistillationLine();
            }
        }

        function mouseReleased() {
            isDragging = false;
        }

        function dataToPixel(x, y) {
            const px = 60 + x * 530;
            const py = 60 + (1 - y) * 530;
            return { x: px, y: py };
        }

        function pixelToData(px, py) {
            const x = (px - 60) / 530;
            const y = 1 - (py - 60) / 530;
            return { x, y };
        }

        function closestPointOnSegment(p, a, b) {
            const ap = { x: p.x - a.x, y: p.y - a.y };
            const ab = { x: b.x - a.x, y: b.y - a.y };
            const abLenSq = ab.x * ab.x + ab.y * ab.y;
            if (abLenSq === 0) return a;
            let t = (ap.x * ab.x + ap.y * ab.y) / abLenSq;
            t = Math.max(0, Math.min(1, t));
            return {
                x: a.x + t * ab.x,
                y: a.y + t * ab.y
            };
        }

        function distSq(p1, p2) {
            const dx = p1.x - p2.x;
            const dy = p1.y - p2.y;
            return dx*dx + dy*dy;
        }

        function getVector(x, y) {
            const A3 = {x: 0.605, y: 0.345};
            const M = {x: 0, y: 0};

            let v_repel_x = x - A3.x;
            let v_repel_y = y - A3.y;
            let v_attract_x = M.x - x;
            let v_attract_y = M.y - y;

            let dist_repel_sq = v_repel_x * v_repel_x + v_repel_y * v_repel_y + 0.01;
            let dist_attract_sq = v_attract_x * v_attract_x + v_attract_y * v_attract_y + 0.01;

            let dx = (v_repel_x / dist_repel_sq) * 0.05 + (v_attract_x / dist_attract_sq) * 0.03;
            let dy = (v_repel_y / dist_repel_sq) * 0.05 + (v_attract_y / dist_attract_sq) * 0.03;

            dx += -0.1 * y;
            dy += 0.1 * x;

            return { dx, dy };
        }

        function computeDistillationLine() {
            let points = [];
            const stepSize = 0.005;
            const steps = 250;

            let current = { ...locator.position };
            for (let i = 0; i < steps; i++) {
                const v = getVector(current.x, current.y);
                current.x += v.dx * stepSize;
                current.y += v.dy * stepSize;
                if (current.x < 0 || current.y < 0 || current.x + current.y > 1) break;
                points.push({ ...current });
            }

            current = { ...locator.position };
            for (let i = 0; i < steps; i++) {
                const v = getVector(current.x, current.y);
                current.x -= v.dx * stepSize;
                current.y -= v.dy * stepSize;
                if (current.x < 0 || current.y < 0 || current.x + current.y > 1) break;
                points.unshift({ ...current });
            }

            return points;
        }
    </script>
</body>
</html>
