                <input type="number" id="input-a" min="0.1" max="5" step="0.1" value="1.5">
            </div>
            <div id="slider-group-b">
                <label for="slider-b">b</label>
                <input type="range" id="slider-b" min="0.1" max="5" step="0.1" value="1.5">
                <input type="number" id="input-b" min="0.1" max="5" step="0.1" value="1.5">
            </div>
            <div id="slider-group-c">
                <label for="slider-c">c</label>
                <input type="range" id="slider-c" min="0.1" max="5" step="0.1" value="1.0">
                <input type="number" id="input-c" min="0.1" max="5" step="0.1" value="1.0">
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/controls/OrbitControls.min.js"></script>

    <script>
        let scene, camera, renderer, controls, paraboloidMesh, wireframeMesh;
        const defaultValues = { a: 1.5, b: 1.5, c: 1.0 };
        const range = 2.0;

        // Initialize three.js scene
        function initScene() {
            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(window.innerWidth, window.innerHeight);
            document.getElementById('canvas-container').appendChild(renderer.domElement);

            // Camera
            camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 0.1, 100);
            camera.position.set(5, 5, 5);

            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0x000000);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
            directionalLight.position.set(5, 10, 7.5);
            scene.add(directionalLight);

            // Orbit Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;

            // Coordinate Axes
            const axesHelper = new THREE.AxesHelper(5);
            axesHelper.material = new THREE.LineBasicMaterial({ color: 0x888888 });
            scene.add(axesHelper);

            // Initial Paraboloid
            createParaboloid(defaultValues.a, defaultValues.b, defaultValues.c);

            // Animation Loop
            function animate() {
                requestAnimationFrame(animate);
                controls.update();
                renderer.render(scene, camera);
            }
            animate();
        }

        // Create elliptic paraboloid geometry
        function createParaboloid(a, b, c) {
            // Remove existing meshes
            if (paraboloidMesh) scene.remove(paraboloidMesh);
            if (wireframeMesh) scene.remove(wireframeMesh);

            // Parametric Geometry
            const geometry = new THREE.ParametricGeometry((u, v, target) => {
                const angle = u * 2 * Math.PI;
                const radius = v * range;
                const x = a * radius * Math.cos(angle);
                const y = b * radius * Math.sin(angle);
                const z = c * radius * radius;
                target.set(x, y, z);
            }, 50, 50);

            // Vertex Colors
            const maxZ = c * range * range;
            const colors = [];
            for (let i = 0; i < geometry.attributes.position.count; i++) {
                const z = geometry.attributes.position.array[i * 3 + 2];
                const t = Math.max(0, Math.min(1, z / maxZ));
                const color = new THREE.Color().lerpColors(
                    new THREE.Color('#FFD700'),
                    new THREE.Color('#FF4500'),
                    t
                );
                colors.push(color.r, color.g, color.b);
            }
            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));

            // Solid Mesh
            const material = new THREE.MeshPhongMaterial({
                vertexColors: true,
                side: THREE.DoubleSide
            });
            paraboloidMesh = new THREE.Mesh(geometry, material);
            scene.add(paraboloidMesh);

            // Wireframe Mesh
            const wireframeMaterial = new THREE.MeshBasicMaterial({
                wireframe: true,
                color: 0xcccccc
            });
            wireframeMesh = new THREE.Mesh(geometry, wireframeMaterial);
            scene.add(wireframeMesh);
        }

        // Sync Sliders and Inputs
        function setupControls() {
            const elements = [
                { slider: 'slider-a', input: 'input-a', default: 1.5 },
                { slider: 'slider-b', input: 'input-b', default: 1.5 },
                { slider: 'slider-c', input: 'input-c', default: 1.0 }
            ];

            elements.forEach(({ slider, input, default: defaultValue }) => {
                const sliderEl = document.getElementById(slider);
                const inputEl = document.getElementById(input);

                sliderEl.value = defaultValue;
                inputEl.value = defaultValue;

                function updateValue(value) {
                    sliderEl.value = value;
                    inputEl.value = value;
                    updateParaboloid();
                }

                sliderEl.addEventListener('input', () => updateValue(sliderEl.value));
                inputEl.addEventListener('input', () => updateValue(inputEl.value));
            });
        }

        // Update Paraboloid with Current Values
        function updateParaboloid() {
            const a = parseFloat(document.getElementById('slider-a').value);
            const b = parseFloat(document.getElementById('slider-b').value);
            const c = parseFloat(document.getElementById('slider-c').value);
            createParaboloid(a, b, c);
        }

        // Handle Window Resize
        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });

        // Initialize Everything
        initScene();
        setupControls();
    </script>
</body>
</html>
