            <div id="j2-selector">
                <button data-value="0.5">1/2</button>
                <button data-value="1.0" class="selected">1</button>
                <button data-value="1.5">3/2</button>
                <button data-value="2.0">2</button>
            </div>
        </div>
        <div class="selector">
            <span>m₂</span>
            <div id="m2-selector"></div>
        </div>
        <div class="selector">
            <span>j</span>
            <div id="j-selector"></div>
        </div>
        <div class="selector">
            <span>show precession</span>
            <div>
                <button id="btn-play-pause">▶</button>
                <button id="btn-reset">|◀</button>
            </div>
        </div>
    </div>
    <div id="canvas-container">
        <canvas id="main-canvas"></canvas>
    </div>
    <div id="legend">
        <span style="color:black">J →</span>
        <span style="color:red">J₁ →</span>
        <span style="color:blue">J₂ →</span>
    </div>

    <script>
        // State variables
        let state = {
            j1: 1.5,
            m1: 0.5,
            j2: 1.0,
            m2: 0.0,
            j: 2.5,
            isAnimating: false,
            phase: 0
        };

        // Three.js setup
        let scene, camera, renderer, controls;
        let vectors = [];
        let cones = [];

        function initThreeJS() {
            const container = document.getElementById('canvas-container');
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            camera = new THREE.PerspectiveCamera(75, container.clientWidth / container.clientHeight, 0.1, 1000);
            camera.position.set(5, 5, 5);
            camera.up.set(0, 0, 1);
            camera.lookAt(0, 0, 0);

            renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('main-canvas'), antialias: true });
            renderer.setSize(container.clientWidth, container.clientHeight);

            // Lights
            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
            directionalLight.position.set(5, 5, 5).normalize();
            scene.add(directionalLight);

            // Orbit controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.update();

            // Resize handler
            window.addEventListener('resize', () => {
                camera.aspect = container.clientWidth / container.clientHeight;
                camera.updateProjectionMatrix();
                renderer.setSize(container.clientWidth, container.clientHeight);
            });

            animate();
        }

        function createVector(color, length, direction) {
            const arrow = new THREE.ArrowHelper(
                new THREE.Vector3().copy(direction),
                new THREE.Vector3(0, 0, 0),
                length,
                color
            );
            arrow.cone.scale.set(0.5, 0.5, 0.5);
            return arrow;
        }

        function createCone(color, height, angle) {
            const geometry = new THREE.ConeGeometry(height * Math.tan(angle), height, 32);
            const material = new THREE.MeshBasicMaterial({ color, opacity: 0.3, transparent: true });
            const cone = new THREE.Mesh(geometry, material);
            cone.rotation.x = Math.PI / 2;
            return cone;
        }

        function updateScene() {
            // Clear existing objects
            vectors.forEach(obj => scene.remove(obj));
            cones.forEach(obj => scene.remove(obj));
            vectors = [];
            cones = [];

            const Lj1 = Math.sqrt(state.j1 * (state.j1 + 1));
            const Lj2 = Math.sqrt(state.j2 * (state.j2 + 1));
            const Lj = Math.sqrt(state.j * (state.j + 1));
            const m = state.m1 + state.m2;

            const coupled = state.j >= state.j1 && state.j >= state.j2;

            if (coupled) {
                // Coupled model
                const thetaJ = Math.acos(m / Lj);
                const phiJ = state.phase * 0.01;

                // J vector
                const jDir = new THREE.Vector3(
                    Math.sin(thetaJ) * Math.cos(phiJ),
                    Math.sin(thetaJ) * Math.sin(phiJ),
                    Math.cos(thetaJ)
                );
                const jVec = createVector('black', Lj, jDir);
                vectors.push(jVec);

                // J cone
                const jCone = createCone('black', Lj, thetaJ);
                cones.push(jCone);

                // J1 and J2 vectors
                const cosTheta1 = (Lj1**2 + Lj**2 - Lj2**2) / (2 * Lj1 * Lj);
                const theta1 = Math.acos(cosTheta1);
                const phiJ1 = state.phase * 0.02;

                const j1Dir = new THREE.Quaternion()
                    .setFromAxisAngle(new THREE.Vector3(0, 0, 1), phiJ)
                    .multiply(new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(1, 0, 0), theta1))
                    .multiply(new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(0, 0, 1), phiJ1))
                    .multiply(new THREE.Vector3(0, 0, 1));

                const j1Vec = createVector('red', Lj1, j1Dir);
                vectors.push(j1Vec);

                // J2 cone
                const cosTheta2 = (Lj2**2 + Lj**2 - Lj1**2) / (2 * Lj2 * Lj);
                const theta2 = Math.acos(cosTheta2);
                const j2Cone = new THREE.Mesh(
                    new THREE.ConeGeometry(Lj * Math.tan(theta2), Lj, 32),
                    new THREE.MeshBasicMaterial({ color: 'blue', opacity: 0.3, transparent: true })
                );
                j2Cone.rotation.x = Math.PI / 2;
                j2Cone.quaternion.copy(new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(0, 0, 1), phiJ));
                cones.push(j2Cone);
            } else {
                // Uncoupled model
                const thetaJ1 = Math.acos(state.m1 / Lj1);
                const thetaJ2 = Math.acos(state.m2 / Lj2);

                // J1 vector
                const phiJ1 = state.phase * 0.01;
                const j1Dir = new THREE.Vector3(
                    Math.sin(thetaJ1) * Math.cos(phiJ1),
                    Math.sin(thetaJ1) * Math.sin(phiJ1),
                    Math.cos(thetaJ1)
                );
                vectors.push(createVector('red', Lj1, j1Dir));

                // J1 cone
                cones.push(createCone('red', Lj1, thetaJ1));

                // J2 vector
                const phiJ2 = state.phase * 0.015;
                const j2Dir = new THREE.Vector3(
                    Math.sin(thetaJ2) * Math.cos(phiJ2),
                    Math.sin(thetaJ2) * Math.sin(phiJ2),
                    Math.cos(thetaJ2)
                );
                vectors.push(createVector('blue', Lj2, j2Dir));

                // J2 cone
                cones.push(createCone('blue', Lj2, thetaJ2));
            }

            // Add all objects to scene
            vectors.forEach(obj => scene.add(obj));
            cones.forEach(obj => scene.add(obj));
        }

        function animate() {
            requestAnimationFrame(animate);
            if (state.isAnimating) {
                state.phase += 1;
                updateScene();
            }
            controls.update();
            renderer.render(scene, camera);
        }

        function populateSelectors() {
            // Populate m1 selector
            const m1Container = document.getElementById('m1-selector');
            m1Container.innerHTML = '';
            const m1Values = [];
            for (let m = -state.j1; m <= state.j1; m += 1) {
                m1Values.push(m);
            }
            m1Values.forEach(m => {
                const btn = document.createElement('button');
                btn.textContent = m === Math.floor(m) ? m.toFixed(1).replace(/\.0$/, '') : m;
                btn.dataset.value = m;
                if (m === state.m1) btn.classList.add('selected');
                btn.addEventListener('click', () => {
                    document.querySelectorAll('#m1-selector button').forEach(b => b.classList.remove('selected'));
                    btn.classList.add('selected');
                    state.m1 = parseFloat(btn.dataset.value);
                    updateScene();
                    resetAnimation();
                });
                m1Container.appendChild(btn);
            });

            // Populate m2 selector
            const m2Container = document.getElementById('m2-selector');
            m2Container.innerHTML = '';
            const m2Values = [];
            for (let m = -state.j2; m <= state.j2; m += 1) {
                m2Values.push(m);
            }
            m2Values.forEach(m => {
                const btn = document.createElement('button');
                btn.textContent = m === Math.floor(m) ? m.toFixed(1).replace(/\.0$/, '') : m;
                btn.dataset.value = m;
                if (m === state.m2) btn.classList.add('selected');
                btn.addEventListener('click', () => {
                    document.querySelectorAll('#m2-selector button').forEach(b => b.classList.remove('selected'));
                    btn.classList.add('selected');
                    state.m2 = parseFloat(btn.dataset.value);
                    updateScene();
                    resetAnimation();
                });
                m2Container.appendChild(btn);
            });

            // Populate j selector
            const jContainer = document.getElementById('j-selector');
            jContainer.innerHTML = '';
            const minJ = Math.abs(state.j1 - state.j2);
            const maxJ = state.j1 + state.j2;
            const jValues = [];
            for (let j = minJ; j <= maxJ; j += 1) {
                jValues.push(j);
            }
            jValues.forEach(j => {
                const btn = document.createElement('button');
                btn.textContent = j === Math.floor(j) ? j.toFixed(1).replace(/\.0$/, '') : j;
                btn.dataset.value = j;
                if (j === state.j) btn.classList.add('selected');
                btn.addEventListener('click', () => {
                    document.querySelectorAll('#j-selector button').forEach(b => b.classList.remove('selected'));
                    btn.classList.add('selected');
                    state.j = parseFloat(btn.dataset.value);
                    updateScene();
                    resetAnimation();
                });
                jContainer.appendChild(btn);
            });
        }

        function resetAnimation() {
            state.phase = 0;
            state.isAnimating = false;
            document.getElementById('btn-play-pause').textContent = '▶';
            updateScene();
        }

        // Event listeners
        document.querySelectorAll('#j1-selector button, #j2-selector button').forEach(btn => {
            btn.addEventListener('click', () => {
                document.querySelectorAll(`#${btn.parentElement.id} button`).forEach(b => b.classList.remove('selected'));
                btn.classList.add('selected');
                const selector = btn.parentElement.id === 'j1-selector' ? 'j1' : 'j2';
                state[selector] = parseFloat(btn.dataset.value);
                populateSelectors();
                updateScene();
                resetAnimation();
            });
        });

        document.getElementById('btn-play-pause').addEventListener('click', () => {
            state.isAnimating = !state.isAnimating;
            document.getElementById('btn-play-pause').textContent = state.isAnimating ? '❚❚' : '▶';
        });

        document.getElementById('btn-reset').addEventListener('click', resetAnimation);

        // Initialize
        initThreeJS();
        populateSelectors();
        updateScene();
    </script>
</body>
</html>
