<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Flood Fill Visualization</title>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
            display: flex;
            flex-direction: column;
            height: 100vh;
        }
        #control-panel {
            display: flex;
            align-items: center;
            padding: 10px;
            background: #f0f0f0;
            border-bottom: 1px solid #ccc;
        }
        #viz-container {
            flex: 1;
            display: flex;
            justify-content: space-around;
            align-items: center;
            background: #fff;
            padding: 20px;
        }
        .grid-container {
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        button {
            margin: 0 5px;
            padding: 5px 10px;
            cursor: pointer;
            border: 1px solid #ccc;
            background: #fff;
        }
        button.active {
            background-color: #007bff;
            color: white;
            border: none;
            border-radius: 4px;
        }
        #steps-slider {
            flex: 1;
            margin: 0 10px;
        }
        #steps-value {
            margin-left: 10px;
            font-weight: bold;
        }
        canvas {
            border: 1px solid #000;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="steps-slider">steps</label>
            <input type="range" id="steps-slider" min="0" max="100" value="61">
            <span id="steps-value">+ 61</span>
            <label>box</label>
            <button id="btn-none">None</button>
            <button id="btn-box">Box</button>
            <button id="btn-scattered" class="active">Scattered</button>
        </div>
        <div id="viz-container">
            <div id="recursive-container" class="grid-container">
                <h3>Recursive</h3>
                <div id="canvas-recursive"></div>
            </div>
            <div id="iterative-container" class="grid-container">
                <h3>Iterative</h3>
                <div id="canvas-iterative"></div>
            </div>
        </div>
    </div>
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
    <script>
        const GRID_SIZE = 10;
        const CELL_SIZE = 20;
        const EMPTY = 0;
        const OBSTACLE = 1;
        let currentPattern = 'scattered';
        let recursiveHistory = [];
        let iterativeHistory = [];
        let grid = [];
        let recursiveSketch;
        let iterativeSketch;
        const stepsSlider = document.getElementById('steps-slider');
        const stepsValue = document.getElementById('steps-value');

        function setup() {
            // Initialize p5 instances
            recursiveSketch = new p5(sketch => {
                sketch.setup = () => {
                    const canvas = sketch.createCanvas(201, 201);
                    canvas.parent('canvas-recursive');
                    sketch.noLoop();
                };
            }, 'canvas-recursive');

            iterativeSketch = new p5(sketch => {
                sketch.setup = () => {
                    const canvas = sketch.createCanvas(201, 201);
                    canvas.parent('canvas-iterative');
                    sketch.noLoop();
                };
            }, 'canvas-iterative');

            // Event listeners
            document.getElementById('btn-none').addEventListener('click', () => changePattern('none'));
            document.getElementById('btn-box').addEventListener('click', () => changePattern('box'));
            document.getElementById('btn-scattered').addEventListener('click', () => changePattern('scattered'));
            stepsSlider.addEventListener('input', updateDisplay);

            // Initial setup
            generateGrid('scattered');
            runAlgorithms();
            updateSliderMax();
            stepsSlider.value = 61;
            updateDisplay();
        }

        function drawGrid(sketch, history) {
            sketch.background(255);
            sketch.stroke(0);
            sketch.strokeWeight(1);

            // Draw grid lines
            for (let i = 0; i <= GRID_SIZE; i++) {
                sketch.line(i * CELL_SIZE, 0, i * CELL_SIZE, GRID_SIZE * CELL_SIZE);
                sketch.line(0, i * CELL_SIZE, GRID_SIZE * CELL_SIZE, i * CELL_SIZE);
            }

            // Draw obstacles
            for (let y = 0; y < GRID_SIZE; y++) {
                for (let x = 0; x < GRID_SIZE; x++) {
                    if (grid[y][x] === OBSTACLE) {
                        sketch.fill(0);
                        sketch.noStroke();
                        sketch.rect(x * CELL_SIZE, y * CELL_SIZE, CELL_SIZE, CELL_SIZE);
                    }
                }
            }

            // Draw filled cells up to current step
            const currentStep = parseInt(stepsSlider.value);
            for (let i = 0; i < currentStep; i++) {
                const [x, y] = history[i];
                sketch.fill(0, 0, 255);
                sketch.noStroke();
                sketch.rect(x * CELL_SIZE, y * CELL_SIZE, CELL_SIZE, CELL_SIZE);
            }
        }

        function generateGrid(pattern) {
            grid = Array(GRID_SIZE).fill().map(() => Array(GRID_SIZE).fill(EMPTY));
            if (pattern === 'box') {
                for (let i = 0; i < GRID_SIZE; i++) {
                    grid[0][i] = OBSTACLE;
                    grid[GRID_SIZE-1][i] = OBSTACLE;
                    grid[i][0] = OBSTACLE;
                    grid[i][GRID_SIZE-1] = OBSTACLE;
                }
            } else if (pattern === 'scattered') {
                const seed = 42;
                let currentSeed = seed;
                for (let y = 0; y < GRID_SIZE; y++) {
                    for (let x = 0; x < GRID_SIZE; x++) {
                        if (x === 4 && y === 4) continue;
                        currentSeed = (currentSeed * 9301 + 49297) % 233280;
                        const rand = currentSeed / 233280.0;
                        if (rand < 0.25) grid[y][x] = OBSTACLE;
                    }
                }
            }
        }

        function runAlgorithms() {
            recursiveHistory = [];
            iterativeHistory = [];

            // Recursive DFS
            const visitedRecursive = Array(GRID_SIZE).fill().map(() => Array(GRID_SIZE).fill(false));
            floodFillRecursive(4, 4, grid, visitedRecursive, recursiveHistory);

            // Iterative BFS
            iterativeHistory = floodFillIterative(4, 4, grid);
        }

        function floodFillRecursive(x, y, grid, visited, history) {
            if (x < 0 || x >= GRID_SIZE || y < 0 || y >= GRID_SIZE) return;
            if (grid[y][x] === OBSTACLE || visited[y][x]) return;
            visited[y][x] = true;
            history.push([x, y]);
            floodFillRecursive(x, y-1, grid, visited, history);
            floodFillRecursive(x-1, y, grid, visited, history);
            floodFillRecursive(x, y+1, grid, visited, history);
            floodFillRecursive(x+1, y, grid, visited, history);
        }

        function floodFillIterative(startX, startY, grid) {
            const visited = Array(GRID_SIZE).fill().map(() => Array(GRID_SIZE).fill(false));
            const queue = [[startX, startY]];
            const history = [];
            if (grid[startY][startX] === OBSTACLE) return history;
            visited[startY][startX] = true;
            history.push([startX, startY]);
            while (queue.length > 0) {
                const [x, y] = queue.shift();
                const neighbors = [[x, y-1], [x-1, y], [x, y+1], [x+1, y]];
                for (const [nx, ny] of neighbors) {
                    if (nx >=0 && nx < GRID_SIZE && ny >=0 && ny < GRID_SIZE && !visited[ny][nx] && grid[ny][nx] !== OBSTACLE) {
                        visited[ny][nx] = true;
                        history.push([nx, ny]);
                        queue.push([nx, ny]);
                    }
                }
            }
            return history;
        }

        function updateSliderMax() {
            const maxSteps = Math.max(recursiveHistory.length, iterativeHistory.length);
            stepsSlider.max = maxSteps;
            if (stepsSlider.value > maxSteps) stepsSlider.value = maxSteps;
        }

        function updateDisplay() {
            const currentStep = parseInt(stepsSlider.value);
            stepsValue.textContent = `+ ${currentStep}`;
            if (recursiveSketch) {
                recursiveSketch.redraw();
                drawGrid(recursiveSketch, recursiveHistory);
            }
            if (iterativeSketch) {
                iterativeSketch.redraw();
                drawGrid(iterativeSketch, iterativeHistory);
            }
        }

        function changePattern(pattern) {
            currentPattern = pattern;
            document.querySelectorAll('button').forEach(btn => btn.classList.remove('active'));
            document.getElementById(`btn-${pattern}`).classList.add('active');
            generateGrid(pattern);
            runAlgorithms();
            updateSliderMax();
            stepsSlider.value = 0;
            updateDisplay();
        }

        window.addEventListener('load', setup);
    </script>
</body>
</html>
