<!DOCTYPE html>
<html>
<head>
    <title>Bouncing Ball Simulation</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
        }
        #control-panel {
            display: flex;
            justify-content: space-around;
            padding: 20px;
            background-color: #f0f0f0;
        }
        #control-panel div {
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        label {
            margin-bottom: 5px;
        }
        input[type=range] {
            width: 200px;
        }
        #canvas-container {
            margin: 0 auto;
            width: 700px;
            height: 400px;
            display: flex;
            justify-content: center;
            align-items: center;
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <div>
            <label for="slider-k">k</label>
            <input type="range" id="slider-k" min="0.6" max="0.95" step="0.01" value="0.9">
        </div>
        <div>
            <label for="slider-time">time</label>
            <input type="range" id="slider-time" min="5" max="25" step="0.5" value="15">
        </div>
        <div>
            <label for="slider-vx">initial velocity in x</label>
            <input type="range" id="slider-vx" min="1" max="8" step="0.1" value="2.5">
        </div>
        <div>
            <label for="slider-dt">Δt</label>
            <input type="range" id="slider-dt" min="0.05" max="0.4" step="0.01" value="0.15">
        </div>
    </div>
    <div id="canvas-container"></div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        function setup() {
            let canvas = createCanvas(700, 400);
            canvas.parent('canvas-container');
            noLoop();
        }

        function draw() {
            let k = parseFloat(document.getElementById('slider-k').value);
            let totalTime = parseFloat(document.getElementById('slider-time').value);
            let vx = parseFloat(document.getElementById('slider-vx').value);
            let dt = parseFloat(document.getElementById('slider-dt').value);

            const g = 9.8;
            const y0 = 10;
            let currentTime = 0;
            let currentX = 0;
            let currentY = y0;
            let currentVy = 0;
            let pathPoints = [];
            let strobePoints = [];
            let maxX = 0;
            let maxY = y0;

            while (currentTime < totalTime) {
                let discriminant = currentVy * currentVy + 2 * g * currentY;
                if (discriminant < 0) break;
                let t_flight = (currentVy + Math.sqrt(discriminant)) / g;

                if (currentTime + t_flight > totalTime) {
                    t_flight = totalTime - currentTime;
                }

                let steps = Math.ceil(t_flight / 0.01);
                for (let i = 0; i <= steps; i++) {
                    let t = i * 0.01;
                    if (t > t_flight) t = t_flight;
                    let x = currentX + vx * t;
                    let y = currentY + currentVy * t - 0.5 * g * t * t;
                    pathPoints.push({x, y});
                    if (x > maxX) maxX = x;
                    if (y > maxY) maxY = y;
                }

                let startStrobe = Math.ceil(currentTime / dt) * dt;
                for (let s = startStrobe; s < currentTime + t_flight; s += dt) {
                    let tInArc = s - currentTime;
                    let x = currentX + vx * tInArc;
                    let y = currentY + currentVy * tInArc - 0.5 * g * tInArc * tInArc;
                    strobePoints.push({x, y});
                }

                currentTime += t_flight;
                currentX += vx * t_flight;
                let vy_impact = currentVy - g * t_flight;
                currentVy = Math.sqrt(k) * Math.abs(vy_impact);
                currentY = 0;

                if (currentVy < 0.01) break;
            }

            let padding = 40;
            let availableWidth = width - 2 * padding;
            let availableHeight = height - 2 * padding;
            let scaleX = maxX > 0 ? availableWidth / maxX : 1;
            let scaleY = maxY > 0 ? availableHeight / maxY : 1;

            background(0);
            translate(padding, height - padding);
            scale(1, -1);

            stroke(100, 100, 150);
            line(0, 0, maxX * scaleX, 0);
            line(0, 0, 0, maxY * scaleY);

            for (let x = 0; x <= maxX; x += 1) {
                line(x * scaleX, -5, x * scaleX, 5);
            }
            for (let y = 0; y <= maxY; y += 1) {
                line(-5, y * scaleY, 5, y * scaleY);
            }

            stroke(255, 0, 0);
            strokeWeight(2);
            noFill();
            beginShape();
            for (let p of pathPoints) {
                vertex(p.x * scaleX, p.y * scaleY);
            }
            endShape();

            fill(255, 255, 0);
            noStroke();
            for (let p of strobePoints) {
                ellipse(p.x * scaleX, p.y * scaleY, 8, 8);
            }
        }

        window.addEventListener('DOMContentLoaded', () => {
            document.getElementById('slider-k').addEventListener('input', () => redraw());
            document.getElementById('slider-time').addEventListener('input', () => redraw());
            document.getElementById('slider-vx').addEventListener('input', () => redraw());
            document.getElementById('slider-dt').addEventListener('input', () => redraw());
        });
    </script>
</body>
</html>
