            max-width: 800px;
            color: #666;
            margin-bottom: 2rem;
        }
        #canvas-container {
            display: flex;
            justify-content: center;
            align-items: center;
            width: 100%;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <h1>Angle Bisectors on the Circumcircle</h1>
    <p>Extend the angle bisectors of the triangle ABC to meet the circumcircle at A', B' and C'. Then AA' ⊥ B'C', BB' ⊥ A'C', and CC' ⊥ A'B'.</p>
    <div id="canvas-container">
        <canvas id="p5-canvas"></canvas>
    </div>
    <script>
        let angles = {
            A: 7 * Math.PI / 6,
            B: 11 * Math.PI / 6,
            C: Math.PI / 2
        };
        let activePoint = null;
        const radius = 200;
        const center = { x: 250, y: 250 };
        const pointRadius = 6;
        const dragThreshold = 15;

        function setup() {
            createCanvas(500, 500);
            noLoop();
        }

        function draw() {
            background('#F0F0F0');

            // Draw circumcircle
            stroke('#6A5ACD');
            strokeWeight(4);
            noFill();
            circle(center.x, center.y, radius * 2);

            // Calculate all points
            const points = {
                A: getPoint(angles.A),
                B: getPoint(angles.B),
                C: getPoint(angles.C),
                A_prime: getDerivedPoint(angles.B, angles.C),
                B_prime: getDerivedPoint(angles.A, angles.C),
                C_prime: getDerivedPoint(angles.A, angles.B)
            };

            // Draw triangle ABC
            stroke('#6A5ACD');
            strokeWeight(4);
            line(points.A.x, points.A.y, points.B.x, points.B.y);
            line(points.B.x, points.B.y, points.C.x, points.C.y);
            line(points.C.x, points.C.y, points.A.x, points.A.y);

            // Draw extended bisectors and A'B'C' triangle
            stroke('#FFA500');
            strokeWeight(4);
            line(points.A.x, points.A.y, points.A_prime.x, points.A_prime.y);
            line(points.B.x, points.B.y, points.B_prime.x, points.B_prime.y);
            line(points.C.x, points.C.y, points.C_prime.x, points.C_prime.y);
            line(points.A_prime.x, points.A_prime.y, points.B_prime.x, points.B_prime.y);
            line(points.B_prime.x, points.B_prime.y, points.C_prime.x, points.C_prime.y);
            line(points.C_prime.x, points.C_prime.y, points.A_prime.x, points.A_prime.y);

            // Draw points and labels
            drawPoint(points.A, 'A', true);
            drawPoint(points.B, 'B', true);
            drawPoint(points.C, 'C', true);
            drawPoint(points.A_prime, "A'");
            drawPoint(points.B_prime, "B'");
            drawPoint(points.C_prime, "C'");

            redraw();
        }

        function getPoint(angle) {
            return {
                x: center.x + radius * Math.cos(angle),
                y: center.y + radius * Math.sin(angle),
                angle: angle
            };
        }

        function getDerivedPoint(angle1, angle2) {
            const sinSum = Math.sin(angle1) + Math.sin(angle2);
            const cosSum = Math.cos(angle1) + Math.cos(angle2);
            const angle = Math.atan2(sinSum, cosSum);
            return getPoint(angle);
        }

        function drawPoint(point, label, isDraggable = false) {
            // Draw circle
            fill('#FFA500');
            stroke('#000000');
            strokeWeight(1);
            circle(point.x, point.y, pointRadius * 2);

            // Draw crosshair for draggable points
            if (isDraggable) {
                line(point.x - pointRadius, point.y, point.x + pointRadius, point.y);
                line(point.x, point.y - pointRadius, point.x, point.y + pointRadius);
            }

            // Draw label
            noStroke();
            fill('#000000');
            textSize(16);
            const offset = 12;
            const labelPos = {
                x: point.x + (point.x - center.x) / radius * offset,
                y: point.y + (point.y - center.y) / radius * offset
            };
            text(label, labelPos.x, labelPos.y);
        }

        function mousePressed() {
            const points = {
                A: getPoint(angles.A),
                B: getPoint(angles.B),
                C: getPoint(angles.C)
            };

            for (const key in points) {
                const d = dist(mouseX, mouseY, points[key].x, points[key].y);
                if (d < dragThreshold) {
                    activePoint = key;
                    return;
                }
            }
            activePoint = null;
        }

        function mouseDragged() {
            if (!activePoint) return;

            const dx = mouseX - center.x;
            const dy = mouseY - center.y;
            angles[activePoint] = Math.atan2(dy, dx);
            redraw();
        }

        function mouseReleased() {
            activePoint = null;
        }

        function redraw() {
            redraw();
        }
    </script>
</body>
</html>
