<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <title>Carbon Cycle Visualization</title>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
  <style>
    body {
      display: flex;
      justify-content: center;
      align-items: center;
      min-height: 100vh;
      margin: 0;
      background-color: #f8f8f8;
      font-family: Arial, sans-serif;
    }
    #container {
      text-align: center;
    }
    #controls {
      background-color: #f0f0f0;
      padding: 15px;
      border: 1px solid #ccc;
      border-radius: 5px;
      margin-bottom: 20px;
    }
    #canvas-container {
      border: 1px solid #888;
      margin: 0 auto;
    }
    label {
      margin-right: 10px;
      font-weight: bold;
    }
  </style>
</head>
<body>
  <div id="container">
    <div id="controls">
      <label for="slider-steps">carbon cycle steps</label>
      <input type="range" id="slider-steps" min="0" max="7" step="1" value="0">
    </div>
    <div id="canvas-container"></div>
  </div>

  <script>
    let tooltipText = '';
    let tooltipX, tooltipY;

    function setup() {
      let canvas = createCanvas(600, 500);
      canvas.parent('canvas-container');
    }

    function draw() {
      tooltipText = '';

      // Static background
      background(65, 105, 225); // Sky

      // Sun
      fill(255, 215, 0);
      ellipse(500, 80, 50, 50);

      // Cloud
      fill(255);
      ellipse(300, 100, 40, 30);
      ellipse(330, 90, 50, 40);
      ellipse(360, 110, 30, 25);

      // Grass
      fill(34, 139, 34);
      rect(0, 250, 600, 200);

      // Soil
      fill(139, 69, 19);
      rect(0, 450, 600, 50);

      // Water
      fill(0, 0, 205);
      beginShape();
      vertex(500, 400);
      vertex(600, 450);
      vertex(600, 500);
      vertex(500, 450);
      endShape(CLOSE);

      // Trees
      // Tree 1
      fill(139, 69, 19);
      rect(100, 300, 20, 100);
      fill(0, 100, 0);
      ellipse(110, 250, 60, 100);

      // Tree 2
      fill(139, 69, 19);
      rect(250, 320, 15, 80);
      fill(0, 100, 0);
      ellipse(260, 280, 50, 90);

      // Sheep
      fill(255);
      ellipse(200, 350, 50, 40); // Body
      ellipse(210, 320, 30, 30); // Head
      fill(0);
      ellipse(215, 330, 3, 3); // Eye

      // Factory
      fill(105, 105, 105);
      rect(450, 300, 50, 80); // Building
      fill(192, 192, 192);
      rect(460, 280, 10, 30); // Smokestack

      // Fossil deposits
      fill(0);
      ellipse(100, 460, 10, 10);
      ellipse(200, 470, 8, 8);
      ellipse(300, 465, 12, 12);

      let step = parseInt(document.getElementById('slider-steps').value);

      if (step >= 1) drawCellularRespiration();
      if (step >= 2) drawWasteAndDecay();
      if (step >= 3) drawFossils();
      if (step >= 4) drawPhotosynthesis();
      if (step >= 5) drawFossilEmissions();
      if (step >= 6) drawFossilExtraction();
      if (step >= 7) drawCODiffusion();

      if (tooltipText !== '') {
        drawTooltip(tooltipX, tooltipY, tooltipText);
      }
    }

    function drawArrow(x1, y1, x2, y2) {
      stroke(0);
      strokeWeight(2);
      line(x1, y1, x2, y2);
      let angle = atan2(y2 - y1, x2 - x1);
      push();
      translate(x2, y2);
      rotate(angle);
      triangle(0, 0, -10, 5, -10, -5);
      pop();
    }

    function drawProcessLabel(x, y, lines, description) {
      fill(0);
      textSize(12);
      textAlign(LEFT, TOP);
      let lineHeight = 15;
      for (let i = 0; i < lines.length; i++) {
        text(lines[i], x, y + i * lineHeight);
      }
      let maxWidth = 0;
      for (let line of lines) {
        let w = textWidth(line);
        if (w > maxWidth) maxWidth = w;
      }
      let totalHeight = lines.length * lineHeight;
      let bbox = {
        x: x,
        y: y,
        width: maxWidth,
        height: totalHeight
      };
      if (mouseX > bbox.x && mouseX < bbox.x + bbox.width &&
          mouseY > bbox.y && mouseY < bbox.y + bbox.height) {
        tooltipText = description;
        tooltipX = mouseX;
        tooltipY = mouseY;
      }
    }

    function drawCellularRespiration() {
      drawArrow(200, 240, 250, 180);
      drawProcessLabel(150, 180, ["Cellular", "Respiration"], 
        "Animals and plants break down sugars for energy, releasing carbon dioxide.");
    }

    function drawWasteAndDecay() {
      drawArrow(220, 280, 220, 350);
      drawProcessLabel(240, 320, ["Organisms'", "Waste and Decay"], 
        "Decomposers break down dead organic matter, returning carbon to the soil and atmosphere.");
    }

    function drawFossils() {
      drawArrow(220, 370, 220, 440);
      drawProcessLabel(240, 440, ["Fossils and", "Fossil Fuels"], 
        "Over millions of years, buried organic matter can turn into fossil fuels like coal and oil.");
    }

    function drawPhotosynthesis() {
      drawArrow(350, 80, 300, 180);
      drawProcessLabel(360, 120, ["Photosynthesis"], 
        "Plants use sunlight, water, and carbon dioxide to create food, releasing oxygen.");
    }

    function drawFossilEmissions() {
      drawArrow(480, 200, 430, 120);
      drawProcessLabel(440, 150, ["Fossil Fuel", "Emissions"], 
        "Burning fossil fuels releases large amounts of carbon dioxide into the atmosphere.");
    }

    function drawFossilExtraction() {
      drawArrow(450, 420, 480, 280);
      drawProcessLabel(400, 350, ["Fossil Fuel", "Extraction"], 
        "Humans extract fossil fuels from the ground for energy.");
    }

    function drawCODiffusion() {
      drawArrow(520, 250, 550, 400);
      drawArrow(570, 400, 540, 250);
      drawProcessLabel(480, 420, ["CO2 Diffusion"], 
        "Carbon dioxide dissolves into and is released from bodies of water like oceans.");
    }

    function drawTooltip(x, y, text) {
      fill(0, 0, 0, 150);
      noStroke();
      rect(x + 10, y + 10, 200, 80, 5);
      fill(255);
      textSize(12);
      textAlign(LEFT, TOP);
      text(text, x + 15, y + 15, 190, 70);
    }
  </script>
</body>
</html>
