<!DOCTYPE html>
<html>
<head>
    <title>Kinematic Visualization</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
        }
        #main-container {
            display: flex;
            height: 100vh;
        }
        #control-panel {
            width: 250px;
            background: #f0f0f0;
            padding: 15px;
            box-shadow: 2px 0 5px rgba(0,0,0,0.1);
        }
        .slider-container {
            display: flex;
            align-items: center;
            margin-bottom: 10px;
        }
        #slider-time {
            flex: 1;
            margin: 0 5px;
        }
        .toggle-switch {
            display: flex;
            margin-bottom: 10px;
        }
        .toggle-switch input[type="radio"] {
            display: none;
        }
        .toggle-switch label {
            flex: 1;
            padding: 4px 8px;
            text-align: center;
            background: #ccc;
            border: 1px solid #999;
            cursor: pointer;
            user-select: none;
        }
        .toggle-switch input[type="radio"]:checked + label {
            background: #666;
            color: white;
        }
        label {
            display: block;
            margin-top: 10px;
            margin-bottom: 5px;
        }
        #canvas-container {
            flex: 1;
            position: relative;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-time">time</label>
            <div class="slider-container">
                <input type="range" id="slider-time" min="0" max="6.283" step="0.01" value="0.4">
                <button id="btn-reset-time" title="Reset time">+</button>
            </div>

            <label>position</label>
            <div id="toggle-position" class="toggle-switch">
                <input type="radio" id="radio-position-off" name="position" value="off" checked>
                <label for="radio-position-off">off</label>
                <input type="radio" id="radio-position-on" name="position" value="on">
                <label for="radio-position-on">on</label>
            </div>

            <label>velocity</label>
            <div id="toggle-velocity" class="toggle-switch">
                <input type="radio" id="radio-velocity-off" name="velocity" value="off" checked>
                <label for="radio-velocity-off">off</label>
                <input type="radio" id="radio-velocity-on" name="velocity" value="on">
                <label for="radio-velocity-on">on</label>
            </div>

            <label>acceleration</label>
            <div id="toggle-acceleration" class="toggle-switch">
                <input type="radio" id="radio-acceleration-off" name="acceleration" value="off">
                <label for="radio-acceleration-off">off</label>
                <input type="radio" id="radio-acceleration-on" name="acceleration" value="on" checked>
                <label for="radio-acceleration-on">on</label>
            </div>

            <label for="select-coords">coordinate system</label>
            <select id="select-coords">
                <option value="rectangular">rectangular</option>
                <option value="normal-tangential">normal-tangential</option>
                <option value="polar">polar</option>
            </select>

            <label>instantaneous circle</label>
            <div id="toggle-circle" class="toggle-switch">
                <input type="radio" id="radio-circle-off" name="circle" value="off" checked>
                <label for="radio-circle-off">off</label>
                <input type="radio" id="radio-circle-on" name="circle" value="on">
                <label for="radio-circle-on">on</label>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let state = {
            time: 0.4,
            position: 'off',
            velocity: 'off',
            acceleration: 'on',
            coords: 'rectangular',
            circle: 'off'
        };

        function setup() {
            let canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');
            noLoop();
        }

        function draw() {
            background(255);
            drawAxes();
            drawCurve();
            let t = state.time;
            let pos = createVector(cos(t), sin(2 * t));
            let vel = createVector(-sin(t), 2 * cos(2 * t));
            let acc = createVector(-cos(t), -4 * sin(2 * t));

            // Scale for visualization
            let scale = 150;
            let scaledPos = createVector(width/2 + pos.x * scale, height/2 - pos.y * scale);

            // Draw position vector
            if (state.position === 'on') {
                stroke(0);
                line(width/2, height/2, scaledPos.x, scaledPos.y);
                drawArrowhead(scaledPos, createVector(-pos.x, pos.y).normalize());
            }

            // Draw point
            fill(0);
            noStroke();
            ellipse(scaledPos.x, scaledPos.y, 8, 8);

            // Draw vectors from point
            if (state.velocity === 'on') {
                drawVector(scaledPos, vel, color(139, 0, 0), scale, 'v');
            }
            if (state.acceleration === 'on') {
                drawVector(scaledPos, acc, color(128, 0, 128), scale, 'a');
            }

            // Draw basis vectors
            if (state.coords === 'rectangular') {
                drawBasis(scaledPos, createVector(1,0), createVector(0,1), ['î', 'ĵ']);
            } else if (state.coords === 'normal-tangential') {
                let tangent = vel.copy().normalize();
                let normal = createVector(-tangent.y, tangent.x);
                drawBasis(scaledPos, tangent, normal, ['ê_T', 'ê_N']);
            } else if (state.coords === 'polar') {
                let radial = pos.copy().normalize();
                let theta = createVector(-radial.y, radial.x);
                drawBasis(scaledPos, radial, theta, ['ê_r', 'ê_θ']);
            }

            // Draw instantaneous circle
            if (state.circle === 'on') {
                let vMag = vel.mag();
                let cross = vel.x * acc.y - vel.y * acc.x;
                if (abs(cross) > 0.001) {
                    let radius = pow(vMag, 3) / abs(cross);
                    let normalDir = (cross > 0) ? 1 : -1;
                    let normalVec = vel.copy().rotate(PI/2 * normalDir).normalize();
                    let center = p5.Vector.add(scaledPos, p5.Vector.mult(normalVec, radius * scale));
                    stroke(128);
                    noFill();
                    ellipseMode(CENTER);
                    ellipse(center.x, center.y, radius * scale * 2, radius * scale * 2);
                }
            }
        }

        function drawVector(pos, vec, col, scale, label) {
            let scaledVec = vec.copy().mult(scale);
            stroke(col);
            noFill();
            line(pos.x, pos.y, pos.x + scaledVec.x, pos.y - scaledVec.y);
            drawArrowhead(createVector(pos.x + scaledVec.x, pos.y - scaledVec.y), scaledVec.normalize());
            fill(col);
            textSize(12);
            text(label, pos.x + scaledVec.x + 5, pos.y - scaledVec.y - 5);
        }

        function drawBasis(pos, vec1, vec2, labels) {
            let scale = 50;
            stroke(0);
            strokeWeight(1);
            drawDashedVector(pos, vec1, scale, labels[0]);
            drawDashedVector(pos, vec2, scale, labels[1]);
        }

        function drawDashedVector(pos, vec, scale, label) {
            let scaledVec = vec.copy().mult(scale);
            let step = 10;
            let dashes = int(scaledVec.mag() / step);
            push();
            translate(pos.x, pos.y);
            rotate(vec.heading());
            for (let i = 0; i < dashes; i++) {
                line(i * step, 0, (i + 0.5) * step, 0);
            }
            drawArrowhead(createVector(scaledVec.x, scaledVec.y), vec.normalize());
            fill(0);
            textSize(12);
            text(label, scaledVec.x + 5, scaledVec.y - 5);
            pop();
        }

        function drawArrowhead(pos, dir) {
            let angle = dir.heading();
            push();
            translate(pos.x, pos.y);
            rotate(angle);
            triangle(0, 0, -8, 3, -8, -3);
            pop();
        }

        function drawAxes() {
            let scale = 150;
            stroke(150);
            line(0, height/2, width, height/2);
            line(width/2, 0, width/2, height);

            // Tick marks
            for (let x = -2; x <= 2; x++) {
                let px = width/2 + x * scale;
                line(px, height/2 - 5, px, height/2 + 5);
                if (x !== 0) {
                    textSize(12);
                    fill(150);
                    text(x, px + 3, height/2 + 15);
                }
            }
            for (let y = -2; y <= 2; y++) {
                let py = height/2 - y * scale;
                line(width/2 - 5, py, width/2 + 5, py);
                if (y !== 0) {
                    textSize(12);
                    fill(150);
                    text(y, width/2 + 8, py + 5);
                }
            }
        }

        function drawCurve() {
            let scale = 150;
            stroke(0, 0, 255);
            noFill();
            beginShape();
            for (let t = 0; t < TWO_PI; t += 0.05) {
                let x = cos(t);
                let y = sin(2 * t);
                vertex(width/2 + x * scale, height/2 - y * scale);
            }
            endShape();
        }

        // Event Listeners
        document.getElementById('slider-time').addEventListener('input', function() {
            state.time = parseFloat(this.value);
            redraw();
        });

        document.getElementById('btn-reset-time').addEventListener('click', function() {
            document.getElementById('slider-time').value = 0.4;
            state.time = 0.4;
            redraw();
        });

        document.querySelectorAll('input[name="position"]').forEach(radio => {
            radio.addEventListener('change', function() {
                state.position = this.value;
                redraw();
            });
        });

        document.querySelectorAll('input[name="velocity"]').forEach(radio => {
            radio.addEventListener('change', function() {
                state.velocity = this.value;
                redraw();
            });
        });

        document.querySelectorAll('input[name="acceleration"]').forEach(radio => {
            radio.addEventListener('change', function() {
                state.acceleration = this.value;
                redraw();
            });
        });

        document.getElementById('select-coords').addEventListener('change', function() {
            state.coords = this.value;
            redraw();
        });

        document.querySelectorAll('input[name="circle"]').forEach(radio => {
            radio.addEventListener('change', function() {
                state.circle = this.value;
                redraw();
            });
        });

        function redraw() {
            loop();
            noLoop();
        }
    </script>
</body>
</html>
