      <div class="control-group">
        <label for="slider-mass">Mass</label>
        <input type="range" id="slider-mass" min="0.1" max="10.0" step="0.01" value="1.0">
        <span id="mass-value">1.00</span>
      </div>
      <div class="control-group">
        <label>Time Controls</label>
        <div>
          <button id="btn-step-back">◀</button>
          <button id="btn-play-pause">▶</button>
          <button id="btn-step-forward">▶|</button>
        </div>
      </div>
      <button id="btn-reset">x</button>
    </div>
    <div id="visualization-container">
      <div id="plot-canvas-container"></div>
      <div id="animation-canvas-container"></div>
    </div>
  </div>
  <script>
    let amplitude = 0.3;
    let stiffness = 1.0;
    let mass = 1.0;
    let time = 0;
    let isPlaying = false;
    let angularFrequency = Math.sqrt(stiffness / mass);
    let positionHistory = [];
    let xMax = 25;

    // Initialize slider displays
    document.getElementById('amplitude-value').textContent = amplitude.toFixed(3);
    document.getElementById('stiffness-value').textContent = stiffness.toFixed(2);
    document.getElementById('mass-value').textContent = mass.toFixed(2);

    // Slider event listeners
    document.getElementById('slider-amplitude').addEventListener('input', updateSliders);
    document.getElementById('slider-stiffness').addEventListener('input', updateSliders);
    document.getElementById('slider-mass').addEventListener('input', updateSliders);

    function updateSliders(event) {
      const target = event.target;
      if (target.id === 'slider-amplitude') {
        amplitude = parseFloat(target.value);
        document.getElementById('amplitude-value').textContent = amplitude.toFixed(3);
      } else if (target.id === 'slider-stiffness') {
        stiffness = parseFloat(target.value);
        document.getElementById('stiffness-value').textContent = stiffness.toFixed(2);
      } else if (target.id === 'slider-mass') {
        mass = parseFloat(target.value);
        document.getElementById('mass-value').textContent = mass.toFixed(2);
      }
      angularFrequency = Math.sqrt(stiffness / mass);
      time = 0;
      positionHistory = [];
      isPlaying = false;
      document.getElementById('btn-play-pause').textContent = '▶';
      plotSketch.redraw();
      animationSketch.redraw();
    }

    // Play/Pause button
    document.getElementById('btn-play-pause').addEventListener('click', function() {
      isPlaying = !isPlaying;
      this.textContent = isPlaying ? '||' : '▶';
    });

    // Step buttons
    document.getElementById('btn-step-back').addEventListener('click', function() {
      if (!isPlaying) {
        time = Math.max(0, time - 0.1);
        updatePosition();
        plotSketch.redraw();
        animationSketch.redraw();
      }
    });

    document.getElementById('btn-step-forward').addEventListener('click', function() {
      if (!isPlaying) {
        time += 0.1;
        updatePosition();
        plotSketch.redraw();
        animationSketch.redraw();
      }
    });

    // Reset button
    document.getElementById('btn-reset').addEventListener('click', function() {
      document.getElementById('slider-amplitude').value = 0.3;
      document.getElementById('slider-stiffness').value = 1.0;
      document.getElementById('slider-mass').value = 1.0;
      document.getElementById('amplitude-value').textContent = '0.300';
      document.getElementById('stiffness-value').textContent = '1.00';
      document.getElementById('mass-value').textContent = '1.00';
      amplitude = 0.3;
      stiffness = 1.0;
      mass = 1.0;
      time = 0;
      isPlaying = false;
      angularFrequency = Math.sqrt(stiffness / mass);
      positionHistory = [];
      xMax = 25;
      document.getElementById('btn-play-pause').textContent = '▶';
      plotSketch.redraw();
      animationSketch.redraw();
    });

    function updatePosition() {
      const y = amplitude * Math.sin(angularFrequency * time);
      if (positionHistory.length === 0 || time > positionHistory[positionHistory.length - 1].time) {
        positionHistory.push({ time: time, position: y });
      }
      if (time > xMax) {
        xMax = time + 5;
      }
    }

    // Animation loop
    function animate() {
      if (isPlaying) {
        time += 0.05;
        updatePosition();
        plotSketch.redraw();
        animationSketch.redraw();
      }
      requestAnimationFrame(animate);
    }
    requestAnimationFrame(animate);

    // Plot sketch
    let plotSketch = new p5(function(p) {
      p.setup = function() {
        let canvas = p.createCanvas(600, 400);
        canvas.parent('plot-canvas-container');
      };

      p.draw = function() {
        p.background(255);
        p.stroke(0);
        p.line(0, p.height/2, p.width, p.height/2);
        p.line(0, 0, 0, p.height);

        p.stroke(200);
        for (let yLabel = -1; yLabel <= 1; yLabel += 0.5) {
          let y = p.map(yLabel, -1, 1, p.height, 0);
          p.line(0, y, p.width, y);
          p.fill(0);
          p.textAlign(p.RIGHT, p.CENTER);
          p.text(yLabel.toFixed(1), 20, y);
        }

        for (let xLabel = 0; xLabel <= xMax; xLabel += 5) {
          let x = p.map(xLabel, 0, xMax, 0, p.width);
          p.line(x, 0, x, p.height);
          p.fill(0);
          p.textAlign(p.CENTER, p.BOTTOM);
          p.text(xLabel.toFixed(1), x, p.height - 10);
        }

        p.stroke('#6363B2');
        p.noFill();
        p.beginShape();
        for (let i = 0; i < positionHistory.length; i++) {
          let pt = positionHistory[i];
          let x = p.map(pt.time, 0, xMax, 0, p.width);
          let y = p.map(pt.position, -1, 1, p.height, 0);
          p.vertex(x, y);
        }
        p.endShape();
      };
    });

    // Animation sketch
    let animationSketch = new p5(function(p) {
      p.setup = function() {
        let canvas = p.createCanvas(200, 400);
        canvas.parent('animation-canvas-container');
        p.angleMode(p.DEGREES);
      };

      p.draw = function() {
        p.background(255);
        p.translate(p.width/2, p.height/2);
        p.scale(1, -1);

        p.stroke(0);
        p.line(0, -1.2, 0, 1.2);

        p.noStroke();
        p.fill(0);
        p.textAlign(p.CENTER, p.CENTER);
        p.text('-1', 0, -1);
        p.text('0', 0, 0);
        p.text('1', 0, 1);

        let y = amplitude * Math.sin(angularFrequency * time);

        p.stroke(0);
        p.line(-10, 1.2, 10, 1.2);

        drawSpring(p, 0, 1.2, 0, y);

        p.fill('#6363B2');
        p.noStroke();
        p.rect(-15, y, 30, 20);
      };

      function drawSpring(p, x1, y1, x2, y2) {
        let numCoils = 5;
        let coilHeight = (y1 - y2) / numCoils;
        let coilWidth = 0.2;

        p.stroke(0);
        p.noFill();
        for (let i = 0; i < numCoils; i++) {
          let yStart = y1 - i * coilHeight;
          let yEnd = y1 - (i + 1) * coilHeight;
          let xStart = x1 - coilWidth * (i % 2 === 0 ? 1 : -1);
          let xEnd = x1 - coilWidth * ((i + 1) % 2 === 0 ? 1 : -1);
          p.line(xStart, yStart, xEnd, yEnd);
        }
      }
    });
  </script>
</body>
</html>
