<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Distance Transform Demo</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            margin: 0;
        }
        #main-container {
            text-align: center;
        }
        #control-panel {
            margin-bottom: 20px;
        }
        button {
            margin: 0 5px;
            padding: 5px 10px;
            background-color: #DCDCDC;
            border: 1px solid #888;
            cursor: pointer;
        }
        button.active {
            background-color: #A9A9A9;
            border: 2px solid #555;
        }
        #grid-container {
            display: grid;
            grid-template-columns: repeat(7, 40px);
            grid-template-rows: repeat(9, 40px);
            gap: 2px;
            justify-content: center;
        }
        .cell {
            width: 40px;
            height: 40px;
            border: 1px solid #999;
            display: flex;
            justify-content: center;
            align-items: center;
            font-size: 12px;
            cursor: pointer;
        }
        .cell-on {
            background-color: #FFD700;
        }
        .cell-off {
            background-color: #D3D3D3;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <span>distance function</span>
            <button id="btn-manhattan">Manhattan</button>
            <button id="btn-euclidean">squared Euclidean</button>
            <button id="btn-chebyshev">Chebyshev</button>
        </div>
        <div id="grid-container"></div>
    </div>
    <script>
        let currentDistanceFunction = 'manhattan';
        let gridState = Array(9).fill().map(() => Array(7).fill(false));
        gridState[2][1] = true;
        gridState[6][4] = true;

        function initializeGrid() {
            const gridContainer = document.getElementById('grid-container');
            gridContainer.innerHTML = '';
            for (let r = 0; r < 9; r++) {
                for (let c = 0; c < 7; c++) {
                    const cell = document.createElement('div');
                    cell.className = 'cell';
                    cell.dataset.row = r;
                    cell.dataset.col = c;
                    cell.addEventListener('click', () => toggleCell(r, c));
                    const span = document.createElement('span');
                    cell.appendChild(span);
                    gridContainer.appendChild(cell);
                }
            }
            updateGridDisplay();
        }

        function toggleCell(r, c) {
            gridState[r][c] = !gridState[r][c];
            updateGridDisplay();
        }

        function calculateDistances() {
            const sources = [];
            for (let r = 0; r < 9; r++) {
                for (let c = 0; c < 7; c++) {
                    if (gridState[r][c]) {
                        sources.push({ r, c });
                    }
                }
            }
            const distances = Array.from({ length: 9 }, () => Array(7).fill(Infinity));
            if (sources.length === 0) return distances;

            for (let r = 0; r < 9; r++) {
                for (let c = 0; c < 7; c++) {
                    let minDist = Infinity;
                    for (const src of sources) {
                        let dist;
                        if (currentDistanceFunction === 'manhattan') {
                            dist = Math.abs(r - src.r) + Math.abs(c - src.c);
                        } else if (currentDistanceFunction === 'euclidean') {
                            dist = Math.pow(r - src.r, 2) + Math.pow(c - src.c, 2);
                        } else if (currentDistanceFunction === 'chebyshev') {
                            dist = Math.max(Math.abs(r - src.r), Math.abs(c - src.c));
                        }
                        if (dist < minDist) minDist = dist;
                    }
                    distances[r][c] = minDist;
                }
            }
            return distances;
        }

        function updateGridDisplay() {
            const distances = calculateDistances();
            for (let r = 0; r < 9; r++) {
                for (let c = 0; c < 7; c++) {
                    const cell = document.querySelector(`.cell[data-row='${r}'][data-col='${c}']`);
                    if (gridState[r][c]) {
                        cell.textContent = '0';
                        cell.className = 'cell cell-on';
                    } else {
                        const distanceValue = distances[r][c];
                        cell.textContent = distanceValue === Infinity ? '∞' : distanceValue;
                        cell.className = 'cell cell-off';
                    }
                }
            }
        }

        function changeDistance(metric) {
            currentDistanceFunction = metric;
            updateActiveButton();
            updateGridDisplay();
        }

        function updateActiveButton() {
            const buttons = document.querySelectorAll('button[id^="btn-"]');
            buttons.forEach(button => {
                button.classList.remove('active');
                button.style.backgroundColor = '#DCDCDC';
                button.style.border = '1px solid #888';
            });
            const activeButton = document.getElementById(`btn-${currentDistanceFunction}`);
            if (activeButton) {
                activeButton.classList.add('active');
                activeButton.style.backgroundColor = '#A9A9A9';
                activeButton.style.border = '2px solid #555';
            }
        }

        document.addEventListener('DOMContentLoaded', () => {
            initializeGrid();
            updateActiveButton();
            document.getElementById('btn-manhattan').addEventListener('click', () => changeDistance('manhattan'));
            document.getElementById('btn-euclidean').addEventListener('click', () => changeDistance('euclidean'));
            document.getElementById('btn-chebyshev').addEventListener('click', () => changeDistance('chebyshev'));
        });
    </script>
</body>
</html>
