<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Scientific Visualization</title>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
        }
        #main-container {
            display: flex;
            height: 100vh;
        }
        #control-panel {
            width: 300px;
            padding: 20px;
            border-right: 1px solid #ccc;
            overflow-y: auto;
        }
        #visualization-area {
            flex: 1;
            padding: 20px;
            display: flex;
            flex-direction: column;
        }
        #plot-container {
            flex: 1;
        }
        .active {
            background-color: #007bff;
            color: white;
            border: none;
        }
        button {
            margin: 2px;
            padding: 5px 10px;
            border: 1px solid #ccc;
            background-color: #f0f0f0;
            cursor: pointer;
        }
        label {
            display: block;
            margin-top: 10px;
        }
        input[type="range"] {
            width: 100%;
            margin: 5px 0;
        }
        span {
            display: block;
            margin-top: 5px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div>
                <label>data generation model</label>
                <div>
                    <button id="btn-model-ep" class="active">Eyring–Polanyi</button>
                    <button id="btn-model-ar">Arrhenius</button>
                </div>
            </div>
            <div>
                <label>$T_{max}$ upper limit</label>
                <div>
                    <button id="btn-tmax-low" class="active">low (1,000 °C)</button>
                    <button id="btn-tmax-high">high (6,000 °C)</button>
                </div>
            </div>
            <div>
                <label for="slider-n">\(n\)</label>
                <input type="range" id="slider-n">
                <span id="label-n"></span>
            </div>
            <div>
                <label for="slider-tmin">\(T_{min}\) (°C)</label>
                <input type="range" id="slider-tmin">
                <span id="label-tmin"></span>
            </div>
            <div>
                <label for="slider-tmax">\(T_{max}\) (°C)</label>
                <input type="range" id="slider-tmax">
                <span id="label-tmax"></span>
            </div>
            <div>
                <label>data generation model parameters</label>
                <div>
                    <label for="slider-tref">\(T_{ref}\) (°C)</label>
                    <input type="range" id="slider-tref">
                    <span id="label-tref"></span>
                </div>
                <div>
                    <label for="slider-param" id="label-param-name">\(a_{EP}\)</label>
                    <input type="range" id="slider-param">
                    <span id="label-param-value"></span>
                </div>
            </div>
        </div>
        <div id="visualization-area">
            <div id="fit-results"></div>
            <div id="plot-container"></div>
        </div>
    </div>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const btnModelEP = document.getElementById('btn-model-ep');
            const btnModelAR = document.getElementById('btn-model-ar');
            const btnTmaxLow = document.getElementById('btn-tmax-low');
            const btnTmaxHigh = document.getElementById('btn-tmax-high');
            const sliderN = document.getElementById('slider-n');
            const sliderTmin = document.getElementById('slider-tmin');
            const sliderTmax = document.getElementById('slider-tmax');
            const sliderTref = document.getElementById('slider-tref');
            const sliderParam = document.getElementById('slider-param');
            const labelN = document.getElementById('label-n');
            const labelTmin = document.getElementById('label-tmin');
            const labelTmax = document.getElementById('label-tmax');
            const labelTref = document.getElementById('label-tref');
            const labelParamName = document.getElementById('label-param-name');
            const labelParamValue = document.getElementById('label-param-value');
            const fitResults = document.getElementById('fit-results');

            sliderN.min = 2; sliderN.max = 20; sliderN.value = 6;
            sliderTmin.min = 0; sliderTmin.max = 5999; sliderTmin.value = 25;
            sliderTmax.min = 1; sliderTmax.max = 1200; sliderTmax.value = 100;
            sliderTref.min = 0; sliderTref.max = 6000; sliderTref.value = 50;
            sliderParam.min = 1; sliderParam.max = 6000; sliderParam.value = 4000;

            labelN.textContent = sliderN.value;
            labelTmin.textContent = sliderTmin.value + '.';
            labelTmax.textContent = sliderTmax.value + '.';
            labelTref.textContent = sliderTref.value + '.';
            labelParamValue.textContent = sliderParam.value + '.';

            const sliders = [sliderN, sliderTmin, sliderTmax, sliderTref, sliderParam];
            sliders.forEach(slider => {
                slider.addEventListener('input', update);
            });
            [btnModelEP, btnModelAR].forEach(btn => {
                btn.addEventListener('click', () => {
                    btnModelEP.classList.remove('active');
                    btnModelAR.classList.remove('active');
                    btn.classList.add('active');
                    update();
                });
            });
            [btnTmaxLow, btnTmaxHigh].forEach(btn => {
                btn.addEventListener('click', () => {
                    btnTmaxLow.classList.remove('active');
                    btnTmaxHigh.classList.remove('active');
                    btn.classList.add('active');
                    update();
                });
            });

            update();

            function update() {
                const dataModel = btnModelEP.classList.contains('active') ? 'EP' : 'AR';
                const tmaxLimit = btnTmaxLow.classList.contains('active') ? 'low' : 'high';

                if (tmaxLimit === 'low') {
                    sliderTmax.max = 1200;
                } else {
                    sliderTmax.max = 6000;
                }

                let Tmin = parseFloat(sliderTmin.value);
                let Tmax = parseFloat(sliderTmax.value);
                let Tref = parseFloat(sliderTref.value);
                const aParam = parseFloat(sliderParam.value);
                const n = parseInt(sliderN.value);

                if (Tmin >= Tmax) {
                    [Tmin, Tmax] = [Math.min(Tmin, Tmax), Math.max(Tmin, Tmax)];
                    if (Tmin === Tmax) Tmax = Tmin + 1;
                    sliderTmin.value = Tmin;
                    sliderTmax.value = Tmax;
                }

                if (Tref < Tmin) {
                    Tref = Tmin;
                    sliderTref.value = Tref;
                } else if (Tref > Tmax) {
                    Tref = Tmax;
                    sliderTref.value = Tref;
                }

                if (dataModel === 'EP') {
                    labelParamName.textContent = '\\(a_{EP}\\)';
                } else {
                    labelParamName.textContent = '\\(a_{Ar}\\)';
                }
                MathJax.typesetPromise([labelParamName]).then(() => {});

                labelN.textContent = n;
                labelTmin.textContent = Tmin + '.';
                labelTmax.textContent = Tmax + '.';
                labelTref.textContent = Tref + '.';
                labelParamValue.textContent = aParam + '.';

                const T_C = [];
                const step = (Tmax - Tmin) / (n - 1);
                for (let i = 0; i < n; i++) {
                    T_C.push(Tmin + i * step);
                }

                const T_K = T_C.map(t => t + 273.15);
                const Tref_K = Tref + 273.15;

                let y = [];
                if (dataModel === 'EP') {
                    y = T_K.map(t => (t / Tref_K) * Math.exp(aParam * (1/Tref_K - 1/t)));
                } else {
                    y = T_K.map(t => Math.exp(aParam * (1/Tref_K - 1/t)));
                }

                const fitModel = dataModel === 'EP' ? 'AR' : 'EP';
                let bestA = 0;
                let minRSS = Infinity;

                for (let a = 0; a <= 10000; a++) {
                    let yFit = [];
                    if (fitModel === 'AR') {
                        yFit = T_K.map(t => Math.exp(a * (1/Tref_K - 1/t)));
                    } else {
                        yFit = T_K.map(t => (t / Tref_K) * Math.exp(a * (1/Tref_K - 1/t)));
                    }
                    let rss = 0;
                    for (let i = 0; i < n; i++) {
                        const diff = y[i] - yFit[i];
                        rss += diff * diff;
                    }
                    if (rss < minRSS) {
                        minRSS = rss;
                        bestA = a;
                    }
                }

                const yMean = y.reduce((a, b) => a + b, 0) / n;
                let ssTot = 0;
                for (let i = 0; i < n; i++) {
                    const diff = y[i] - yMean;
                    ssTot += diff * diff;
                }
                const r2 = ssTot === 0 ? 1 : 1 - (minRSS / ssTot);

                fitResults.innerHTML = `\\(a_{${fitModel === 'AR' ? 'Ar' : 'EP'}} = ${bestA.toFixed(0)}\\) \\(r^2 = ${r2.toFixed(3)}\\)`;
                MathJax.typesetPromise([fitResults]).then(() => {});

                const fitT_C = [];
                const fitStep = (Tmax - Tmin) / 199;
                for (let i = 0; i < 200; i++) {
                    fitT_C.push(Tmin + i * fitStep);
                }
                const fitT_K = fitT_C.map(t => t + 273.15);
                let fitY = [];
                if (fitModel === 'AR') {
                    fitY = fitT_K.map(t => Math.exp(bestA * (1/Tref_K - 1/t)));
                } else {
                    fitY = fitT_K.map(t => (t / Tref_K) * Math.exp(bestA * (1/Tref_K - 1/t)));
                }

                drawPlot(T_C, y, fitT_C, fitY, dataModel, fitModel);
            }

            function drawPlot(dataT, dataY, fitT, fitY, dataModel, fitModel) {
                const trace1 = {
                    x: dataT,
                    y: dataY,
                    mode: 'markers',
                    type: 'scatter',
                    name: 'Data Points',
                    marker: {
                        color: 'blue',
                        size: 8,
                        line: {color: 'green', width: 1}
                    }
                };
                const trace2 = {
                    x: fitT,
                    y: fitY,
                    mode: 'lines',
                    type: 'scatter',
                    name: 'Fit Curve',
                    line: {color: 'green', width: 2}
                };
                const layout = {
                    title: `${fitModel === 'AR' ? 'Arrhenius' : 'Eyring-Polanyi'} model fit to ${dataModel === 'EP' ? 'Eyring-Polanyi' : 'Arrhenius'} data`,
                    xaxis: {title: 'T °C'},
                    yaxis: {title: 'k(T) / k(T_{ref})'}
                };
                Plotly.newPlot('plot-container', [trace1, trace2], layout);
            }
        });
    </script>
</body>
</html>
