        #canvas-container {
            border: 1px solid #ccc;
            margin: 0 auto;
            width: 90%;
            height: calc(100vh - 60px);
        }
    </style>
</head>
<body>
    <div id="controls-container">
        <span id="n-display">6</span>
        <button id="btn-n-3">3</button>
        <button id="btn-n-4">4</button>
        <button id="btn-n-5">5</button>
        <button id="btn-n-6" class="active">6</button>
        <button id="btn-n-7">7</button>
        <button id="btn-n-8">8</button>
        <button id="btn-n-9">9</button>
        <button id="btn-n-10">10</button>
    </div>
    <div id="canvas-container"></div>

    <script>
        let n = 6;
        let colors = ['#fefd00', '#ee1f24', '#0048a1'];

        function setup() {
            let canvas = createCanvas(windowWidth * 0.9, windowHeight - 60);
            canvas.parent('canvas-container');
            drawScene();
        }

        function drawScene() {
            background('#FFFFFF');
            let canvasWidth = width;
            let canvasHeight = height;
            let R = 150;
            let cx = canvasWidth / 4;
            let cy = canvasHeight / 2;
            let angleStep = PI / n;

            // Draw polygon on left
            for (let j = 1; j <= 2 * n; j++) {
                let k1 = (j - 1) % (2 * n);
                let k2 = j % (2 * n);
                let x1 = cx + R * cos(k1 * angleStep - PI / 2);
                let y1 = cy + R * sin(k1 * angleStep - PI / 2);
                let x2 = cx + R * cos(k2 * angleStep - PI / 2);
                let y2 = cy + R * sin(k2 * angleStep - PI / 2);

                fill(colors[(j - 1) % 3]);
                stroke('#000000');
                beginShape();
                vertex(cx, cy);
                vertex(x1, y1);
                vertex(x2, y2);
                endShape(CLOSE);
            }

            // Draw rectangles on right
            let startX = canvasWidth / 2 + 50;
            let startY = canvasHeight / 2;
            let s = 2 * R * sin(angleStep / 2);
            let a = R * cos(angleStep / 2);
            let rectWidth = s;
            let rectHeight = 2 * a;
            let spacing = 20;

            // Full rectangles
            for (let k = 1; k <= Math.floor(n / 2); k++) {
                let x = startX + (k - 1) * (rectWidth + spacing);
                let y = startY - rectHeight / 2;

                // Top triangle (Yellow)
                fill(colors[0]);
                beginShape();
                vertex(x, y);
                vertex(x + rectWidth, y);
                vertex(x + rectWidth / 2, y - rectHeight / 2);
                endShape();

                // Bottom triangle (Red)
                fill(colors[1]);
                beginShape();
                vertex(x, y + rectHeight);
                vertex(x + rectWidth, y + rectHeight);
                vertex(x + rectWidth / 2, y + rectHeight / 2);
                endShape();

                // Left triangle (Blue)
                fill(colors[2]);
                beginShape();
                vertex(x, y);
                vertex(x, y + rectHeight);
                vertex(x + rectWidth / 2, y + rectHeight / 2);
                endShape();

                // Right triangle (Yellow)
                fill(colors[0]);
                beginShape();
                vertex(x + rectWidth, y);
                vertex(x + rectWidth, y + rectHeight);
                vertex(x + rectWidth / 2, y + rectHeight / 2);
                endShape();

                // Outline
                noFill();
                stroke('#000000');
                rect(x, y, rectWidth, rectHeight);
                line(x, y, x + rectWidth, y + rectHeight);
                line(x + rectWidth, y, x, y + rectHeight);
            }

            // Half-rectangle for odd n
            if (n % 2 !== 0) {
                let k = Math.floor(n / 2) + 1;
                let x = startX + (k - 1) * (rectWidth + spacing);
                let y = startY - rectHeight / 2;

                // Top triangle (Blue)
                fill(colors[2]);
                beginShape();
                vertex(x, y);
                vertex(x + rectWidth, y);
                vertex(x + rectWidth / 2, y - rectHeight / 2);
                endShape();

                // Bottom triangle (Red)
                fill(colors[1]);
                beginShape();
                vertex(x, y + rectHeight);
                vertex(x + rectWidth, y + rectHeight);
                vertex(x + rectWidth / 2, y + rectHeight / 2);
                endShape();

                // Outline
                noFill();
                stroke('#000000');
                rect(x, y, rectWidth, rectHeight);
                line(x, y, x + rectWidth, y + rectHeight);
            }
        }

        // Button event listeners
        document.querySelectorAll('button').forEach(button => {
            button.addEventListener('click', () => {
                n = parseInt(button.textContent);
                document.getElementById('n-display').textContent = n;
                document.querySelectorAll('button').forEach(btn => btn.classList.remove('active'));
                button.classList.add('active');
                drawScene();
            });
        });

        // Handle window resize
        window.addEventListener('resize', () => {
            resizeCanvas(windowWidth * 0.9, windowHeight - 60);
            drawScene();
        });
    </script>
</body>
</html>
