            font-weight: bold;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <h4>problem</h4>
                <div>
                    <label>dimension</label><br>
                    <button id="btn-2d" class="active">2D</button>
                    <button id="btn-3d">3D</button>
                </div>
                <div>
                    <label>random seed</label><br>
                    <input type="range" id="slider-seed" min="0" max="1000" value="525">
                    <span id="seed-value">525</span>
                </div>
                <div>
                    <label>number</label><br>
                    <input type="range" id="slider-number" min="5" max="150" value="40">
                    <span id="number-value">40</span>
                </div>
            </div>
            <div class="control-group">
                <h4>method</h4>
                <div>
                    <button id="btn-prim" class="active">Prim</button>
                    <button id="btn-kruskal">Kruskal</button>
                </div>
            </div>
            <div class="control-group">
                <h4>process</h4>
                <div>
                    <button id="btn-play">►</button>
                    <button id="btn-pause" disabled>||</button>
                    <button id="btn-reset">|◄</button>
                </div>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let dimension = '2D';
        let algorithm = 'prim';
        let animationState = 'stopped';
        let seed = 525;
        let numPoints = 40;
        let points = [];
        let mstSteps = [];
        let currentStep = 0;
        let is3D = false;

        function setup() {
            let canvas = createCanvas(windowWidth - 300, windowHeight, is3D ? WEBGL : P2D);
            canvas.parent('canvas-container');
            generatePoints();
            resetVisualization();
        }

        function draw() {
            if (is3D) {
                background(224);
                orbitControl();
                draw3DScene();
            } else {
                background(224);
                draw2DScene();
            }

            if (animationState === 'playing' && currentStep < mstSteps.length) {
                currentStep++;
                if (currentStep >= mstSteps.length) {
                    animationState = 'stopped';
                    document.getElementById('btn-play').disabled = true;
                    document.getElementById('btn-pause').disabled = true;
                }
            }
        }

        function draw2DScene() {
            // Draw points
            fill('blue');
            noStroke();
            points.forEach(p => {
                ellipse(p.x, p.y, 8, 8);
            });

            // Draw MST edges
            stroke('red');
            strokeWeight(2);
            for (let i = 0; i < currentStep; i++) {
                const [a, b] = mstSteps[i];
                line(points[a].x, points[a].y, points[b].x, points[b].y);
            }
        }

        function draw3DScene() {
            // Draw wireframe cube
            stroke(150);
            noFill();
            box(400);

            // Draw points
            fill('blue');
            noStroke();
            points.forEach(p => {
                push();
                translate(p.x - width/2, p.y - height/2, p.z || 0);
                sphere(5);
                pop();
            });

            // Draw MST edges
            stroke('red');
            strokeWeight(2);
            for (let i = 0; i < currentStep; i++) {
                const [a, b] = mstSteps[i];
                const pa = points[a];
                const pb = points[b];
                line(pa.x - width/2, pa.y - height/2, pa.z || 0,
                     pb.x - width/2, pb.y - height/2, pb.z || 0);
            }
        }

        function generatePoints() {
            randomSeed(seed);
            points = [];
            for (let i = 0; i < numPoints; i++) {
                if (dimension === '2D') {
                    points.push(createVector(random(width), random(height)));
                } else {
                    points.push(createVector(random(-200, 200), random(-200, 200), random(-200, 200)));
                }
            }
            resetAlgorithm();
        }

        function resetAlgorithm() {
            currentStep = 0;
            mstSteps = [];
            // Simplified MST precomputation for demonstration
            if (algorithm === 'prim') {
                // Prim's algorithm steps (simplified)
                let visited = [0];
                while (visited.length < numPoints) {
                    let minDist = Infinity;
                    let nextPoint, fromPoint;
                    for (let i of visited) {
                        for (let j = 0; j < numPoints; j++) {
                            if (!visited.includes(j)) {
                                const d = dist(points[i].x, points[i].y, points[j].x, points[j].y);
                                if (d < minDist) {
                                    minDist = d;
                                    nextPoint = j;
                                    fromPoint = i;
                                }
                            }
                        }
                    }
                    visited.push(nextPoint);
                    mstSteps.push([fromPoint, nextPoint]);
                }
            } else {
                // Kruskal's algorithm (simplified)
                let edges = [];
                for (let i = 0; i < numPoints; i++) {
                    for (let j = i + 1; j < numPoints; j++) {
                        const d = dist(points[i].x, points[i].y, points[j].x, points[j].y);
                        edges.push({a: i, b: j, weight: d});
                    }
                }
                edges.sort((a, b) => a.weight - b.weight);
                let parent = Array(numPoints).fill().map((_, i) => i);
                const find = (x) => parent[x] === x ? x : find(parent[x]);
                edges.forEach(edge => {
                    const a = find(edge.a);
                    const b = find(edge.b);
                    if (a !== b) {
                        mstSteps.push([edge.a, edge.b]);
                        parent[b] = a;
                    }
                });
            }
        }

        function resetVisualization() {
            currentStep = 0;
            document.getElementById('btn-play').disabled = false;
            document.getElementById('btn-pause').disabled = true;
            document.getElementById('btn-reset').disabled = false;
        }

        // Event Listeners
        document.getElementById('btn-2d').addEventListener('click', () => {
            dimension = '2D';
            is3D = false;
            resetAll();
        });

        document.getElementById('btn-3d').addEventListener('click', () => {
            dimension = '3D';
            is3D = true;
            resetAll();
        });

        document.getElementById('slider-seed').addEventListener('input', (e) => {
            seed = parseInt(e.target.value);
            document.getElementById('seed-value').textContent = seed;
            resetAll();
        });

        document.getElementById('slider-number').addEventListener('input', (e) => {
            numPoints = parseInt(e.target.value);
            document.getElementById('number-value').textContent = numPoints;
            resetAll();
        });

        document.getElementById('btn-prim').addEventListener('click', () => {
            algorithm = 'prim';
            resetAlgorithm();
            resetVisualization();
        });

        document.getElementById('btn-kruskal').addEventListener('click', () => {
            algorithm = 'kruskal';
            resetAlgorithm();
            resetVisualization();
        });

        document.getElementById('btn-play').addEventListener('click', () => {
            if (animationState === 'stopped') {
                animationState = 'playing';
                document.getElementById('btn-play').disabled = true;
                document.getElementById('btn-pause').disabled = false;
            }
        });

        document.getElementById('btn-pause').addEventListener('click', () => {
            if (animationState === 'playing') {
                animationState = 'paused';
                document.getElementById('btn-play').disabled = false;
                document.getElementById('btn-pause').disabled = true;
            }
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            animationState = 'stopped';
            resetVisualization();
            resetAlgorithm();
        });

        function resetAll() {
            animationState = 'stopped';
            generatePoints();
            resetVisualization();
            document.getElementById('btn-play').disabled = false;
            document.getElementById('btn-pause').disabled = true;
        }

        // Update button states on page load
        document.getElementById('btn-2d').classList.add('active');
        document.getElementById('btn-prim').classList.add('active');
    </script>
</body>
</html>
