    }
  </style>
</head>
<body>
  <div id="main-container">
    <div id="decorative-plus-btn"></div>
    <div id="control-panel">
      <div class="control-section">
        <label for="select-method">method</label>
        <select id="select-method">
          <option value="estimation">estimation</option>
          <option value="place values">place values</option>
        </select>
      </div>
      <hr>
      <div class="control-section">
        <label>first number</label>
        <div style="flex:1;">
          <label for="slider-num1">numerical value</label>
          <input type="range" id="slider-num1" min="0" max="20" step="0.1" value="1.1">
          <span>+</span>
        </div>
      </div>
      <hr>
      <div class="control-section">
        <label>multiply by</label>
        <div class="toggle-buttons">
          <label>integers only</label>
          <button id="btn-int-yes">yes</button>
          <button id="btn-int-no">no</button>
        </div>
        <div style="flex:1; margin-top:5px;">
          <label for="slider-num2">numerical value</label>
          <input type="range" id="slider-num2" min="0" max="20" step="1" value="2">
          <span>+</span>
        </div>
      </div>
    </div>
    <div id="output-display"></div>
  </div>

  <script>
    function getFactor(num) {
      const str = num.toString();
      const decimalIndex = str.indexOf('.');
      if (decimalIndex === -1) return 1;
      const decimalPlaces = str.length - decimalIndex - 1;
      return Math.pow(10, decimalPlaces);
    }

    function numberToIntegerString(num) {
      const factor = getFactor(num);
      const integerPart = Math.round(num * factor);
      return integerPart.toString();
    }

    function updateDisplay() {
      const num1 = parseFloat(document.getElementById('slider-num1').value);
      const num2 = parseFloat(document.getElementById('slider-num2').value);
      const method = document.getElementById('select-method').value;
      const outputDisplay = document.getElementById('output-display');
      outputDisplay.innerHTML = '';

      if (method === 'estimation') {
        const num1AsInt = numberToIntegerString(num1);
        const num2AsInt = numberToIntegerString(num2);
        const productOfInts = parseInt(num1AsInt) * parseInt(num2AsInt);
        const roundedNum1 = Math.round(num1);
        const roundedNum2 = Math.round(num2);
        const productRounded = roundedNum1 * roundedNum2;
        const actualProduct = num1 * num2;

        const steps = [
          `start with: ${num1} × ${num2}`,
          `convert to: ${num1AsInt}. × ${num2AsInt}. = ${productOfInts}.`,
          `estimate as: ${roundedNum1} × ${roundedNum2} = ${productRounded}`,
          `result is: ${actualProduct}`
        ];

        steps.forEach(step => {
          const p = document.createElement('p');
          p.textContent = step;
          outputDisplay.appendChild(p);
        });
      } else if (method === 'place values') {
        const num1Factor = getFactor(num1);
        const num2Factor = getFactor(num2);
        const num1AsInt = numberToIntegerString(num1);
        const num2AsInt = numberToIntegerString(num2);
        const part1 = num1Factor === 1 ? num1AsInt : `(${num1AsInt} ÷ ${num1Factor})`;
        const part2 = num2Factor === 1 ? num2AsInt : `(${num2AsInt} ÷ ${num2Factor})`;
        const productOfInts = parseInt(num1AsInt) * parseInt(num2AsInt);
        const productOfFactors = num1Factor * num2Factor;
        const actualProduct = num1 * num2;

        const steps = [
          `start with: ${num1} × ${num2}`,
          `rewrite as: ${part1} × ${part2}`,
          `= (${num1AsInt} × ${num2AsInt}) ÷ (${num1Factor} × ${num2Factor})`,
          `= ${productOfInts} ÷ ${productOfFactors}`,
          `= ${actualProduct}`
        ];

        steps.forEach(step => {
          const p = document.createElement('p');
          p.textContent = step;
          outputDisplay.appendChild(p);
        });
      }
    }

    document.getElementById('btn-int-yes').addEventListener('click', () => {
      const slider = document.getElementById('slider-num2');
      slider.step = 1;
      const currentValue = parseFloat(slider.value);
      slider.value = Math.round(currentValue);
      document.getElementById('btn-int-yes').classList.add('active');
      document.getElementById('btn-int-yes').classList.remove('inactive');
      document.getElementById('btn-int-no').classList.add('inactive');
      document.getElementById('btn-int-no').classList.remove('active');
      updateDisplay();
    });

    document.getElementById('btn-int-no').addEventListener('click', () => {
      document.getElementById('slider-num2').step = 0.1;
      document.getElementById('btn-int-no').classList.add('active');
      document.getElementById('btn-int-no').classList.remove('inactive');
      document.getElementById('btn-int-yes').classList.add('inactive');
      document.getElementById('btn-int-yes').classList.remove('active');
      updateDisplay();
    });

    document.getElementById('slider-num1').addEventListener('input', updateDisplay);
    document.getElementById('slider-num2').addEventListener('input', updateDisplay);
    document.getElementById('select-method').addEventListener('change', updateDisplay);

    // Initialize active state for integer toggle
    document.getElementById('btn-int-yes').classList.add('active');
    document.getElementById('btn-int-no').classList.add('inactive');

    // Initial display update
    updateDisplay();
  </script>
</body>
</html>
