            flex: 1;
            margin: 0 10px;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <div id="control-panel">
            <select id="select-vibration-mode">
                <option value="symmetric-stretch">symmetric stretch</option>
                <option value="asymmetric-stretch">stretch (triply degenerate)</option>
                <option value="bending-e">bend (doubly degenerate)</option>
                <option value="bending-f2">bend (triply degenerate)</option>
                <option value="ir-spectrum">IR spectrum</option>
            </select>
            <span>run</span>
            <button id="btn-play">►</button>
            <button id="btn-pause">||</button>
            <button id="btn-reset">|◄</button>
        </div>
        <div id="visualization-area">
            <div id="three-js-canvas-container"></div>
            <div id="plotly-chart-container"></div>
        </div>
        <div id="description-area">
            <p id="label-left">stretching A₁ Raman active 2916 cm⁻¹</p>
            <p id="label-right">no change in the molecular dipole moment</p>
        </div>
    </div>

    <script>
        // Three.js setup
        const container = document.getElementById('three-js-canvas-container');
        const width = container.clientWidth;
        const height = container.clientHeight;
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(width, height);
        container.appendChild(renderer.domElement);

        // Left scene (molecule)
        const sceneLeft = new THREE.Scene();
        const cameraLeft = new THREE.PerspectiveCamera(45, width/2 / height, 0.1, 1000);
        cameraLeft.position.z = 5;
        sceneLeft.add(new THREE.AmbientLight(0x404040));
        const lightLeft = new THREE.DirectionalLight(0xffffff, 1);
        lightLeft.position.set(1, 1, 1).normalize();
        sceneLeft.add(lightLeft);

        // Right scene (vectors)
        const sceneRight = new THREE.Scene();
        const cameraRight = new THREE.PerspectiveCamera(45, width/2 / height, 0.1, 1000);
        cameraRight.position.z = 5;
        sceneRight.add(new THREE.AmbientLight(0x404040));
        const lightRight = new THREE.DirectionalLight(0xffffff, 1);
        lightRight.position.set(1, 1, 1).normalize();
        sceneRight.add(lightRight);

        // Orbit controls
        const controlsLeft = new THREE.OrbitControls(cameraLeft, renderer.domElement);
        const controlsRight = new THREE.OrbitControls(cameraRight, renderer.domElement);
        controlsLeft.screenSpacePanning = false;
        controlsRight.screenSpacePanning = false;

        // Molecule components
        const carbon = new THREE.Mesh(
            new THREE.SphereGeometry(0.5, 32, 32),
            new THREE.MeshLambertMaterial({ color: 'black' })
        );
        sceneLeft.add(carbon);

        const initialPositions = [
            new THREE.Vector3(1, 1, 1).normalize().multiplyScalar(1.5),
            new THREE.Vector3(1, -1, -1).normalize().multiplyScalar(1.5),
            new THREE.Vector3(-1, 1, -1).normalize().multiplyScalar(1.5),
            new THREE.Vector3(-1, -1, 1).normalize().multiplyScalar(1.5)
        ];

        const hydrogens = [];
        const bonds = [];
        for (let i = 0; i < 4; i++) {
            const h = new THREE.Mesh(
                new THREE.SphereGeometry(0.3, 32, 32),
                new THREE.MeshLambertMaterial({ color: 0xadd8e6 })
            );
            h.position.copy(initialPositions[i]);
            hydrogens.push(h);
            sceneLeft.add(h);

            const bond = new THREE.Mesh(
                new THREE.CylinderGeometry(0.1, 0.1, 1, 16),
                new THREE.MeshBasicMaterial({ color: 'gray' })
            );
            bonds.push(bond);
            sceneLeft.add(bond);
            updateBond(bond, carbon.position, h.position);
        }

        // Dipole vectors
        const dipoleVectors = [];
        const arrowMaterial = new THREE.MeshBasicMaterial({ color: 0x0000ff });
        for (let i = 0; i < 4; i++) {
            const arrow = new THREE.ArrowHelper(
                new THREE.Vector3(0, 1, 0),
                new THREE.Vector3(0, 0, 0),
                1,
                0x0000ff,
                0.1,
                0.1
            );
            dipoleVectors.push(arrow);
            sceneRight.add(arrow);
        }

        const netDipole = new THREE.ArrowHelper(
            new THREE.Vector3(0, 1, 0),
            new THREE.Vector3(0, 0, 0),
            1,
            0xff0000,
            0.1,
            0.1
        );
        netDipole.visible = false;
        sceneRight.add(netDipole);

        // Animation variables
        let animationId;
        let isAnimating = false;
        let time = 0;

        // Displacement functions
        function symmetricStretch(t) {
            const A = 0.2, omega = 2 * Math.PI * 0.5;
            return initialPositions.map(pos => 
                pos.clone().normalize().multiplyScalar(A * Math.sin(omega * t))
            );
        }

        function asymmetricStretch(t) {
            const A = 0.2, omega = 2 * Math.PI * 0.5;
            const displacements = initialPositions.map(pos => new THREE.Vector3());
            displacements[0] = initialPositions[0].clone().normalize().multiplyScalar(A * Math.sin(omega * t));
            displacements[1] = initialPositions[1].clone().normalize().multiplyScalar(-A * Math.sin(omega * t));
            return displacements;
        }

        function bendingE(t) {
            const A = 0.2, omega = 2 * Math.PI * 0.5;
            return initialPositions.map(pos => {
                const dir = pos.clone().normalize();
                const perp = new THREE.Vector3(dir.y, -dir.x, 0).normalize();
                return perp.multiplyScalar(A * Math.sin(omega * t));
            });
        }

        function bendingF2(t) {
            const A = 0.2, omega = 2 * Math.PI * 0.5;
            return initialPositions.map(pos => {
                const dir = pos.clone().normalize();
                const perp = new THREE.Vector3(dir.z, 0, -dir.x).normalize();
                return perp.multiplyScalar(A * Math.sin(omega * t));
            });
        }

        // Update bond geometry
        function updateBond(bond, start, end) {
            const direction = new THREE.Vector3().subVectors(end, start);
            const length = direction.length();
            const midpoint = new THREE.Vector3().addVectors(start, end).divideScalar(2);
            const quaternion = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(0, 1, 0), direction.normalize());
            bond.position.copy(midpoint);
            bond.quaternion.copy(quaternion);
            bond.scale.set(1, length, 1);
        }

        // Update dipole vectors
        function updateDipoleVectors() {
            for (let i = 0; i < 4; i++) {
                const bondVector = new THREE.Vector3().subVectors(hydrogens[i].position, carbon.position);
                dipoleVectors[i].setDirection(bondVector.normalize());
                dipoleVectors[i].setLength(bondVector.length() * 2);
            }
        }

        // Calculate net dipole
        function calculateNetDipole() {
            const net = new THREE.Vector3();
            for (let i = 0; i < 4; i++) {
                net.add(new THREE.Vector3().subVectors(hydrogens[i].position, carbon.position));
            }
            if (net.length() > 0.1) {
                netDipole.setDirection(net.normalize());
                netDipole.setLength(net.length() * 2);
                netDipole.visible = true;
            } else {
                netDipole.visible = false;
            }
        }

        // Animation loop
        function animate() {
            if (!isAnimating) return;
            time += 0.016;
            const mode = document.getElementById('select-vibration-mode').value;
            let displacements;
            switch (mode) {
                case 'symmetric-stretch': displacements = symmetricStretch(time); break;
                case 'asymmetric-stretch': displacements = asymmetricStretch(time); break;
                case 'bending-e': displacements = bendingE(time); break;
                case 'bending-f2': displacements = bendingF2(time); break;
                default: displacements = initialPositions.map(() => new THREE.Vector3());
            }
            for (let i = 0; i < 4; i++) {
                hydrogens[i].position.copy(initialPositions[i]).add(displacements[i]);
                updateBond(bonds[i], carbon.position, hydrogens[i].position);
            }
            updateDipoleVectors();
            calculateNetDipole();
            render();
            animationId = requestAnimationFrame(animate);
        }

        function render() {
            renderer.setViewport(0, 0, width/2, height);
            renderer.render(sceneLeft, cameraLeft);
            renderer.setViewport(width/2, 0, width/2, height);
            renderer.render(sceneRight, cameraRight);
        }

        // Event listeners
        document.getElementById('btn-play').addEventListener('click', () => {
            if (!isAnimating) {
                isAnimating = true;
                animate();
            }
        });

        document.getElementById('btn-pause').addEventListener('click', () => {
            isAnimating = false;
            cancelAnimationFrame(animationId);
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            isAnimating = false;
            cancelAnimationFrame(animationId);
            time = 0;
            resetMolecule();
        });

        function resetMolecule() {
            for (let i = 0; i < 4; i++) {
                hydrogens[i].position.copy(initialPositions[i]);
                updateBond(bonds[i], carbon.position, hydrogens[i].position);
            }
            updateDipoleVectors();
            calculateNetDipole();
            render();
        }

        // Update labels
        function updateLabels(mode) {
            let leftText, leftColor, rightText, rightColor;
            switch(mode) {
                case 'symmetric-stretch':
                    leftText = 'stretching A₁ Raman active 2916 cm⁻¹';
                    leftColor = 'blue';
                    rightText = 'no change in the molecular dipole moment';
                    rightColor = 'blue';
                    break;
                case 'asymmetric-stretch':
                    leftText = 'stretching F₂ IR active 3019 cm⁻¹';
                    leftColor = 'red';
                    rightText = 'change in the molecular dipole moment';
                    rightColor = 'red';
                    break;
                case 'bending-e':
                    leftText = 'bending E Raman active 1534 cm⁻¹';
                    leftColor = 'blue';
                    rightText = 'no change in the molecular dipole moment';
                    rightColor = 'blue';
                    break;
                case 'bending-f2':
                    leftText = 'bending F₂ IR active 1306 cm⁻¹';
                    leftColor = 'red';
                    rightText = 'change in the molecular dipole moment';
                    rightColor = 'red';
                    break;
            }
            document.getElementById('label-left').textContent = leftText;
            document.getElementById('label-left').style.color = leftColor;
            document.getElementById('label-right').textContent = rightText;
            document.getElementById('label-right').style.color = rightColor;
        }

        // IR Spectrum
        let spectrumGenerated = false;
        function generateSpectrum() {
            const x = [], y = [];
            const noise = () => (Math.random() - 0.5) * 0.05;
            for (let i = 400; i <= 4000; i++) {
                x.push(i);
                y.push(0.95 + noise());
            }
            function addGaussianPeak(x, y, center, width, depth) {
                for (let i = 0; i < x.length; i++) {
                    const dx = x[i] - center;
                    y[i] += -depth * Math.exp(-dx*dx/(2*width*width));
                }
            }
            addGaussianPeak(x, y, 1306, 50, 0.85);
            addGaussianPeak(x, y, 3019, 50, 0.85);
            addGaussianPeak(x, y, 1300, 10, 0.3);
            addGaussianPeak(x, y, 1312, 10, 0.3);
            addGaussianPeak(x, y, 3010, 10, 0.3);
            addGaussianPeak(x, y, 3028, 10, 0.3);

            const trace = {
                x: x,
                y: y,
                type: 'scatter',
                mode: 'lines',
                line: {color: 'black'}
            };
            const layout = {
                title: 'Methane IR Spectrum',
                xaxis: {title: 'wavenumber (cm⁻¹)', range: [400, 4000]},
                yaxis: {title: 'Transmittance', range: [0, 1.1]},
                annotations: [],
                images: []
            };

            layout.annotations.push({
                x: 1306,
                y: 0.15,
                text: 'ν F₂ 1306 cm⁻¹',
                showarrow: true,
                arrowhead: 2,
                ax: 0,
                ay: -40,
                font: {color: 'red'}
            });
            layout.annotations.push({
                x: 3019,
                y: 0.15,
                text: 'ν F₂ 3019 cm⁻¹',
                showarrow: true,
                arrowhead: 2,
                ax: 0,
                ay: -40,
                font: {color: 'red'}
            });

            const image1 = createMoleculeImage('bending-f2');
            const image2 = createMoleculeImage('asymmetric-stretch');

            layout.images.push({
                source: image1,
                x: 1200,
                y: 0.4,
                xref: 'x',
                yref: 'y',
                sizex: 200,
                sizey: 0.2,
                xanchor: 'center',
                yanchor: 'middle'
            });
            layout.images.push({
                source: image2,
                x: 2900,
                y: 0.4,
                xref: 'x',
                yref: 'y',
                sizex: 200,
                sizey: 0.2,
                xanchor: 'center',
                yanchor: 'middle'
            });

            Plotly.newPlot('plotly-chart-container', [trace], layout);
        }

        function createMoleculeImage(mode) {
            const scene = new THREE.Scene();
            const camera = new THREE.PerspectiveCamera(75, 1, 0.1, 1000);
            camera.position.z = 5;

            const carbon = new THREE.Mesh(
                new THREE.SphereGeometry(0.5, 32, 32),
                new THREE.MeshLambertMaterial({color: 'black'})
            );
            scene.add(carbon);

            const hydrogens = [];
            for (let i = 0; i < 4; i++) {
                const h = new THREE.Mesh(
                    new THREE.SphereGeometry(0.3, 32, 32),
                    new THREE.MeshLambertMaterial({color: 0xadd8e6})
                );
                h.position.copy(initialPositions[i]);
                scene.add(h);
                hydrogens.push(h);
            }

            if (mode === 'bending-f2') {
                hydrogens[0].position.add(new THREE.Vector3(0.5, 0, 0));
                hydrogens[1].position.add(new THREE.Vector3(-0.5, 0, 0));
            } else if (mode === 'asymmetric-stretch') {
                hydrogens[0].position.multiplyScalar(1.2);
                hydrogens[1].position.multiplyScalar(0.8);
            }

            const light = new THREE.DirectionalLight(0xffffff, 1);
            light.position.set(1, 1, 1).normalize();
            scene.add(light);
            scene.add(new THREE.AmbientLight(0x404040));

            const renderer = new THREE.WebGLRenderer({antialias: true});
            renderer.setSize(100, 100);
            renderer.setClearColor(0xffffff, 1);
            renderer.render(scene, camera);
            return renderer.domElement.toDataURL();
        }

        // Initial setup
        updateLabels('symmetric-stretch');
        resetMolecule();
        render();

        // Dropdown change
        document.getElementById('select-vibration-mode').addEventListener('change', function() {
            const mode = this.value;
            if (mode === 'ir-spectrum') {
                document.getElementById('three-js-canvas-container').style.display = 'none';
                document.getElementById('plotly-chart-container').style.display = 'block';
                document.getElementById('description-area').style.display = 'none';
                document.getElementById('btn-play').disabled = true;
                document.getElementById('btn-pause').disabled = true;
                document.getElementById('btn-reset').disabled = true;
                if (!spectrumGenerated) {
                    generateSpectrum();
                    spectrumGenerated = true;
                }
            } else {
                document.getElementById('three-js-canvas-container').style.display = 'block';
                document.getElementById('plotly-chart-container').style.display = 'none';
                document.getElementById('description-area').style.display = 'flex';
                document.getElementById('btn-play').disabled = false;
                document.getElementById('btn-pause').disabled = false;
                document.getElementById('btn-reset').disabled = false;
                resetMolecule();
                updateLabels(mode);
            }
        });
    </script>
</body>
</html>
