</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-time">time (seconds)</label>
            <input type="range" id="slider-time" min="0" max="100" step="0.1" value="9.8">
            <span id="span-time-value">9.8</span>
            <button id="btn-reset">+</button>
        </div>
        <canvas id="canvas-material-bar" width="600" height="80"></canvas>
        <div id="div-plot" style="width: 600px; height: 400px;"></div>
    </div>

    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const slider = document.getElementById('slider-time');
            const spanTime = document.getElementById('span-time-value');
            const canvas = document.getElementById('canvas-material-bar');
            const ctx = canvas.getContext('2d');
            const plotDiv = document.getElementById('div-plot');
            const resetBtn = document.getElementById('btn-reset');

            let currentT = parseFloat(slider.value);

            // Generate curve data
            const xValues = [];
            const yValues = [];
            for (let t = 0; t <= 100; t += 0.1) {
                xValues.push(t);
                yValues.push(0.155 * Math.sqrt(t));
            }

            // Initial Plotly plot
            const curveTrace = {
                x: xValues,
                y: yValues,
                type: 'scatter',
                line: { color: '#FFA500' }
            };

            const initialS = 0.155 * Math.sqrt(currentT);
            const layout = {
                title: 'Stefan distance versus time',
                xaxis: { title: 'time (s)', range: [0, 100] },
                yaxis: { title: 'distance (mm)', range: [0, 1.6] },
                shapes: [{
                    type: 'line',
                    x0: currentT,
                    y0: 0,
                    x1: currentT,
                    y1: initialS,
                    line: { color: '#000000' }
                }]
            };

            Plotly.newPlot(plotDiv, [curveTrace], layout);

            function drawCanvas(t) {
                const s = 0.155 * Math.sqrt(t);
                const mmToPx = canvas.width / 16;
                const glassEnd = 6 * mmToPx;
                const waterWidth = s * mmToPx;
                const iceStart = glassEnd + waterWidth;
                const iceEnd = 16 * mmToPx;

                ctx.clearRect(0, 0, canvas.width, canvas.height);

                // Draw glass gradient
                const gradient = ctx.createLinearGradient(0, 0, glassEnd, 0);
                gradient.addColorStop(0, '#FF0000');
                gradient.addColorStop(1, '#FFFFFF');
                ctx.fillStyle = gradient;
                ctx.fillRect(0, 20, glassEnd, 40);

                // Draw water
                ctx.fillStyle = '#00BFFF';
                ctx.fillRect(glassEnd, 20, waterWidth, 40);

                // Draw ice
                ctx.fillStyle = '#003366';
                ctx.fillRect(iceStart, 20, iceEnd - iceStart, 40);

                // Draw axis line
                ctx.strokeStyle = '#000000';
                ctx.beginPath();
                ctx.moveTo(0, 60);
                ctx.lineTo(canvas.width, 60);
                ctx.stroke();

                // Draw axis labels
                ctx.fillStyle = '#000000';
                ctx.font = '12px Arial';
                const labels = [0, 5, 10, 15];
                labels.forEach(label => {
                    const x = label * mmToPx;
                    ctx.fillText(label, x - 10, 75);
                });
            }

            function update(t) {
                spanTime.textContent = t.toFixed(1);
                drawCanvas(t);
                const s = 0.155 * Math.sqrt(t);
                Plotly.relayout(plotDiv, {
                    'shapes[0].x0': t,
                    'shapes[0].x1': t,
                    'shapes[0].y1': s
                });
            }

            slider.addEventListener('input', function() {
                const t = parseFloat(this.value);
                update(t);
            });

            resetBtn.addEventListener('click', function() {
                slider.value = 0;
                slider.dispatchEvent(new Event('input'));
            });

            update(currentT);
        });
    </script>
</body>
</html>
