        }
        #local-clock-wrapper, #solar-clock-wrapper {
            position: relative;
            width: 200px;
            height: 200px;
        }
        #local-time-digital, #solar-time-digital {
            position: absolute;
            bottom: -30px;
            background: white;
            border: 1px solid black;
            padding: 2px 5px;
            font-family: monospace;
        }
        #offset-display {
            background: white;
            border: 1px solid black;
            padding: 5px;
            margin-top: 10px;
            text-align: center;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <label for="date-slider">date</label>
            <input type="range" id="date-slider" min="0" max="364" step="1" value="340">
            <span id="date-display">Friday, 07 December</span>
            <button id="today-button">today</button>
        </div>
        <div id="visualization-panel">
            <div id="clocks-container">
                <div class="clock-group" id="local-clock-group">
                    <h2>Local Time</h2>
                    <div id="local-clock-wrapper">
                        <canvas id="local-clock-canvas"></canvas>
                        <div id="local-time-digital"></div>
                    </div>
                </div>
                <div class="clock-group" id="solar-clock-group">
                    <h2>Solar Time</h2>
                    <div id="solar-clock-wrapper">
                        <canvas id="solar-clock-canvas"></canvas>
                        <div id="solar-time-digital"></div>
                    </div>
                </div>
            </div>
            <div id="offset-display">offset of solar time from local time: -46 minutes</div>
        </div>
    </div>

    <script>
        let userLatitude = 51.5;
        let userLongitude = 0;
        let localP5, solarP5;

        // Get geolocation
        navigator.geolocation.getCurrentPosition(pos => {
            userLatitude = pos.coords.latitude;
            userLongitude = pos.coords.longitude;
        }, () => {});

        // Initialize date display
        function updateDateDisplay(dayOfYear) {
            const date = new Date();
            date.setMonth(0, 1);
            date.setDate(date.getDate() + dayOfYear);
            const options = { weekday: 'long', day: '2-digit', month: 'long' };
            document.getElementById('date-display').textContent = 
                date.toLocaleDateString('en-US', options).replace(/ \d{4}/, '');
        }

        // Day of year to date conversion
        function getDayOfYear(date) {
            const start = new Date(date.getFullYear(), 0, 1);
            const diff = date - start;
            return Math.floor(diff / (1000 * 60 * 60 * 24));
        }

        // Time formatting
        function formatTime(date) {
            return `${String(date.getHours()).padStart(2, '0')}:${String(date.getMinutes()).padStart(2, '0')}:${String(date.getSeconds()).padStart(2, '0')}`;
        }

        // Solar time calculation
        function calculateSolarTime(localTime, dayOfYear, longitude) {
            const B = (360 / 365.24) * (dayOfYear - 81);
            const B_rad = Math.PI * B / 180;
            const EoT = 9.87 * Math.sin(2 * B_rad) - 7.53 * Math.cos(B_rad) - 1.5 * Math.sin(B_rad);
            const utcTime = localTime.getTime() + localTime.getTimezoneOffset() * 60000;
            const solarTimeMs = utcTime + (EoT + 4 * longitude) * 60000;
            return new Date(solarTimeMs);
        }

        // Initialize p5 sketches
        document.addEventListener('DOMContentLoaded', () => {
            // Local clock sketch
            localP5 = new p5(p => {
                p.setup = () => {
                    const canvas = p.createCanvas(200, 200);
                    canvas.parent('local-clock-wrapper');
                    p.noLoop();
                };
                p.draw = () => drawClock(p, localTime, '#E6E6FA');
            });

            // Solar clock sketch
            solarP5 = new p5(p => {
                p.setup = () => {
                    const canvas = p.createCanvas(200, 200);
                    canvas.parent('solar-clock-wrapper');
                    p.noLoop();
                };
                p.draw = () => drawClock(p, solarTime, '#FAFAD2');
            });
        });

        // Clock drawing function
        function drawClock(p, time, faceColor) {
            if (!time) return;
            const radius = 80;
            const cx = p.width / 2;
            const cy = p.height / 2;

            // Clock face
            p.background(faceColor);
            p.stroke(0);
            p.strokeWeight(2);
            p.ellipse(cx, cy, radius * 2, radius * 2);

            // Hour markers
            p.strokeWeight(1);
            for (let i = 0; i < 12; i++) {
                const angle = p.radians(i * 30);
                const x1 = cx + (radius - 10) * p.cos(angle);
                const y1 = cy + (radius - 10) * p.sin(angle);
                const x2 = cx + radius * p.cos(angle);
                const y2 = cy + radius * p.sin(angle);
                p.line(x1, y1, x2, y2);
            }

            // Numbers
            p.textAlign(p.CENTER, p.CENTER);
            p.textSize(12);
            for (let i = 1; i <= 12; i++) {
                const angle = p.radians(i * 30 - 90);
                const x = cx + (radius - 25) * p.cos(angle);
                const y = cy + (radius - 25) * p.sin(angle);
                p.text(i, x, y);
            }

            // Hands
            const hours = time.getHours();
            const minutes = time.getMinutes();
            const seconds = time.getSeconds();

            // Hour hand
            let angle = p.radians((hours % 12) * 30 + minutes / 2);
            p.push();
            p.translate(cx, cy);
            p.rotate(angle);
            p.stroke(0);
            p.strokeWeight(4);
            p.line(0, 0, 0, -radius + 40);
            p.pop();

            // Minute hand
            angle = p.radians(minutes * 6 + seconds / 10);
            p.push();
            p.translate(cx, cy);
            p.rotate(angle);
            p.stroke(0);
            p.strokeWeight(2);
            p.line(0, 0, 0, -radius + 20);
            p.pop();

            // Second hand
            angle = p.radians(seconds * 6);
            p.push();
            p.translate(cx, cy);
            p.rotate(angle);
            p.stroke(255, 0, 0);
            p.strokeWeight(1);
            p.line(0, 0, 0, -radius + 10);
            p.pop();

            // Center pin
            p.fill(0);
            p.ellipse(cx, cy, 5, 5);
        }

        // Update function
        let localTime, solarTime;
        function update() {
            localTime = new Date();
            const slider = document.getElementById('date-slider');
            const dayOfYear = parseInt(slider.value);

            // Update local display
            document.getElementById('local-time-digital').textContent = formatTime(localTime);

            // Calculate solar time
            solarTime = calculateSolarTime(localTime, dayOfYear, userLongitude);
            document.getElementById('solar-time-digital').textContent = formatTime(solarTime);

            // Update offset display
            const offset = Math.round((solarTime - localTime) / 60000);
            document.getElementById('offset-display').textContent = 
                `offset of solar time from local time: ${offset} minutes`;

            // Redraw clocks
            if (localP5) localP5.redraw();
            if (solarP5) solarP5.redraw();
        }

        // Initialize
        document.getElementById('date-slider').addEventListener('input', (e) => {
            updateDateDisplay(parseInt(e.target.value));
        });

        document.getElementById('today-button').addEventListener('click', () => {
            const today = new Date();
            const dayOfYear = getDayOfYear(today);
            document.getElementById('date-slider').value = dayOfYear;
            updateDateDisplay(dayOfYear);
        });

        // Initial setup
        updateDateDisplay(340);
        setInterval(update, 1000);
    </script>
</body>
</html>
